import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const GameDetail = ({ userId }) => {
  const { gameId } = useParams(); // có thể là ID số hoặc slug
  const initialId = parseInt(gameId, 10);

  const [game, setGame] = useState(null);
  const [numericId, setNumericId] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [reviewText, setReviewText] = useState('');

  // Hàm chuyển tên game sang slug
  const toSlug = (str) =>
    str
      .toLowerCase()
      .replace(/\s+/g, '-')
      .replace(/[^\w-]+/g, '');

  useEffect(() => {
    const fetchGameDetails = async () => {
      try {
        let fetchedGame;
        if (!isNaN(initialId)) {
          // Nếu param là số
          const response = await axios.get(`http://localhost:5000/api/games/${initialId}`);
          fetchedGame = response.data;
        } else {
          // Nếu param là slug
          const listRes = await axios.get('http://localhost:5000/api/games');
          const allGames = listRes.data;
          fetchedGame = allGames.find((g) => toSlug(g.name) === gameId);
          if (!fetchedGame) {
            console.error('Game not found for slug:', gameId);
            return;
          }
        }
        setGame(fetchedGame);
        setNumericId(fetchedGame.id);
      } catch (error) {
        console.error('Error fetching game details:', error);
      }
    };

    fetchGameDetails();
  }, [gameId, initialId]);

  // Gửi review mới
  const handleSubmitReview = async (e) => {
    e.preventDefault();
    if (numericId == null) return;

    try {
      const response = await axios.post(
        `http://localhost:5000/api/games/${numericId}/reviews`,
        {
          userId,
          rating: Number(rating),
          comment: reviewText,
        }
      );
      setReviews([...reviews, response.data]);
      setReviewText('');
      setRating(5);
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  if (!game) return <div>Loading...</div>;

  return (
    <div>
      <h2>{game.name}</h2>
      <p>{game.description}</p>
      <p>Price: ${game.price}</p>
      {game.imageUrl && <img src={game.imageUrl} alt={game.name} />}

      <h3>Reviews</h3>
      {reviews.length > 0 ? (
        reviews.map((review) => (
          <div key={review.id}>
            <p>Rating: {review.rating} stars</p>
            <p>{review.comment}</p>
          </div>
        ))
      ) : (
        <p>No reviews yet.</p>
      )}

      <h3>Submit your review</h3>
      <form onSubmit={handleSubmitReview}>
        <div>
          <label>Rating: </label>
          <select value={rating} onChange={(e) => setRating(e.target.value)}>
            {[1, 2, 3, 4, 5].map((star) => (
              <option key={star} value={star}>
                {star} Star{star > 1 ? 's' : ''}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Review: </label>
          <textarea
            value={reviewText}
            onChange={(e) => setReviewText(e.target.value)}
            rows="4"
            cols="50"
            placeholder="Write your review here"
          />
        </div>
        <button type="submit">Submit Review</button>
      </form>
    </div>
  );
};

export default GameDetail;
