// src/StorePage.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Style/StorePage.css";

// Thiết lập axios baseURL
const API_BASE = process.env.REACT_APP_API_URL || "http://localhost:5000";
axios.defaults.baseURL = API_BASE;

const StorePage = ({ cartItems = [] }) => {
  const navigate = useNavigate();
  const [games, setGames] = useState([]);            // Full list of games
  const [suggestions, setSuggestions] = useState([]); // Search suggestions
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch all games on mount
  useEffect(() => {
    const fetchAll = async () => {
      setLoading(true);
      setError(null);
      try {
        const { data } = await axios.get("/api/games");
        setGames(data);
      } catch (err) {
        console.error("Error fetching games:", err);
        setError(err.response ? `${err.response.status} ${err.response.statusText}` : "Server error");
      } finally {
        setLoading(false);
      }
    };
    fetchAll();
  }, []);

  // Fetch suggestions when searchTerm changes
  useEffect(() => {
    if (!searchTerm) {
      setSuggestions([]);
      return;
    }
    const fetchSuggestions = async () => {
      try {
        const { data } = await axios.get("/api/games", { params: { search: searchTerm } });
        setSuggestions(data);
      } catch (err) {
        console.error("Error fetching suggestions:", err);
      }
    };
    fetchSuggestions();
  }, [searchTerm]);

  // Handle selection from suggestions
  const handleSelect = (game) => {
    setSearchTerm("");
    setSuggestions([]);
    navigate(`/game/${game.id}`);
  };

  // Featured / categories from full list
  const featured      = games[0] || null;
  const specialOffers = games.filter(g => Number(g.discount) > 0);
  const freeGames     = games.filter(g => Number(g.price) === 0);
  const bestSellers   = games.slice(0, 5);

  return (
    <div>
      <header className="store-header">
        {/* ... banner, etc. ... */}
      </header>

      {/* Search bar */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Tìm kiếm trò chơi..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
        {suggestions.length > 0 && (
          <ul className="suggestions-list">
            {suggestions.map(game => (
              <li key={game.id} onClick={() => handleSelect(game)}>
                {game.title}
              </li>
            ))}
          </ul>
        )}
      </div>

      {loading && <p>Đang tải...</p>}
      {error   && <p className="error-text">Lỗi: {error}</p>}

      {/* Featured */}
      {featured && (
        <section className="featured-container">
          <h2>FEATURED</h2>
          <div className="featured">
            <div className="featured-item" onClick={() => navigate(`/game/${featured.id}`)}>
              <img src={featured.imageUrl} alt={featured.title} />
              <div className="game-info">
                <h3>{featured.title}</h3>
                <p>{featured.description}</p>
              </div>
            </div>
          </div>
        </section>
      )}

      {/* Special Offers */}
      {specialOffers.length > 0 && (
        <section>
          <h2>Special Offers</h2>
          <div className="game-list">
            {specialOffers.map(game => (
              <div key={game.id} className="game-card" onClick={() => navigate(`/game/${game.id}`)}>
                <img src={game.imageUrl} alt={game.title} />
                <span className="discount">-{game.discount}%</span>
                <h3>{game.title}</h3>
                <span className="price">{typeof game.price === 'number' ? `$${game.price}` : game.price}</span>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Free-to-Play Games */}
      {freeGames.length > 0 && (
        <section>
          <h2>Free-to-Play Games</h2>
          <div className="game-list">
            {freeGames.map(game => (
              <div key={game.id} className="game-card" onClick={() => navigate(`/game/${game.id}`)}>
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <span className="price">Free</span>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Best Sellers */}
      {bestSellers.length > 0 && (
        <section>
          <h2>Best Sellers</h2>
          <div className="game-list">
            {bestSellers.map(game => (
              <div key={game.id} className="game-card" onClick={() => navigate(`/game/${game.id}`)}>
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <span className="price">{typeof game.price === 'number' ? `$${game.price}` : game.price}</span>
              </div>
            ))}
          </div>
        </section>
      )}

      <footer className="store-footer">
        {/* ... footer content ... */}
      </footer>
    </div>
  );
};

export default StorePage;
