import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Style/StorePage.css';

export default function StorePage({ cartItems, setCartItems }) {
  const [featured, setFeatured] = useState([]);
  const [special, setSpecial] = useState([]);
  const [freeGames, setFreeGames] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);

  const PAGE_SIZE = 4;
  const [pageInfo, setPageInfo] = useState({ special: 1, free: 1, best: 1 });
  const [totalPages, setTotalPages] = useState({ special: 1, free: 1, best: 1 });

  useEffect(() => {
    async function fetchStoreGames() {
      try {
        const res = await axios.get('/api/storeGames');
        const { featured, special, freeGames, bestSellers } = res.data;
        setFeatured(featured);
        setSpecial(special);
        setFreeGames(freeGames);
        setBestSellers(bestSellers);
        setTotalPages({
          special: Math.ceil(special.length / PAGE_SIZE),
          free: Math.ceil(freeGames.length / PAGE_SIZE),
          best: Math.ceil(bestSellers.length / PAGE_SIZE),
        });
      } catch (err) {
        console.error('Error fetching store games:', err);
      }
    }
    fetchStoreGames();
  }, []);

  const handlePageChange = (category, delta) => {
    setPageInfo(prev => {
      const nextPage = prev[category] + delta;
      return { ...prev, [category]: Math.max(1, Math.min(nextPage, totalPages[category])) };
    });
  };

  const paginate = (items, page) => items.slice((page - 1) * PAGE_SIZE, page * PAGE_SIZE);

  return (
    <div className="store-page">
      {/* Featured Games */}
      <section className="featured-games">
        <h2>Featured Games</h2>
        <div className="game-list">
          {featured.map(game => (
            <Link key={game.id} to={`/game/${game.id}`} className="game-card">
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
            </Link>
          ))}
        </div>
      </section>

      {/* Special Deals */}
      <section className="special-games">
        <h2>Special Deals</h2>
        <div className="game-list">
          {paginate(special, pageInfo.special).map(game => (
            <Link key={game.id} to={`/game/${game.id}`} className="game-card">
              <div className="discount">-{game.discount}%</div>
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
              {game.price > 0 && <span className="original">${game.price.toFixed(2)}</span>}
              <span className="sale">${(game.price * (1 - game.discount/100)).toFixed(2)}</span>
              <p>{(game.salesCount || 0).toLocaleString()} lượt mua</p>
            </Link>
          ))}
        </div>
        <div className="pagination">
          <button onClick={() => handlePageChange('special', -1)} disabled={pageInfo.special === 1}>&lt; Prev</button>
          <span>{pageInfo.special}/{totalPages.special}</span>
          <button onClick={() => handlePageChange('special', 1)} disabled={pageInfo.special === totalPages.special}>Next &gt;</button>
        </div>
      </section>

      {/* Free Games */}
      <section className="free-games">
        <h2>Free Games</h2>
        <div className="game-list">
          {paginate(freeGames, pageInfo.free).map(game => (
            <Link key={game.id} to={`/game/${game.id}`} className="game-card">
              {game.discount > 0 && <div className="discount">-{game.discount}%</div>}
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
              <p>Free</p>
              <p>{(game.salesCount || 0).toLocaleString()} lượt mua</p>
            </Link>
          ))}
        </div>
        <div className="pagination">
          <button onClick={() => handlePageChange('free', -1)} disabled={pageInfo.free === 1}>&lt; Prev</button>
          <span>{pageInfo.free}/{totalPages.free}</span>
          <button onClick={() => handlePageChange('free', 1)} disabled={pageInfo.free === totalPages.free}>Next &gt;</button>
        </div>
      </section>

      {/* Best Sellers */}
      <section className="best-sellers">
        <h2>Best Sellers</h2>
        <div className="game-list">
          {paginate(bestSellers, pageInfo.best).map(game => (
            <Link key={game.id} to={`/game/${game.id}`} className="game-card">
              <div className="discount">-{game.discount}%</div>
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
              {game.price > 0 && <span className="original">${game.price.toFixed(2)}</span>}
              <span className="sale">${(game.price * (1 - game.discount/100)).toFixed(2)}</span>
              <p>{(game.salesCount || 0).toLocaleString()} lượt mua</p>
            </Link>
          ))}
        </div>
        <div className="pagination">
          <button onClick={() => handlePageChange('best', -1)} disabled={pageInfo.best === 1}>&lt; Prev</button>
          <span>{pageInfo.best}/{totalPages.best}</span>
          <button onClick={() => handlePageChange('best', 1)} disabled={pageInfo.best === totalPages.best}>Next &gt;</button>
        </div>
      </section>
    </div>
  );
}
