import axios from 'axios';

// API call to fetch games
export const getGames = async () => {
  try {
    const response = await axios.get('/api/games');
    return response.data;
  } catch (error) {
    console.error("There was an error fetching games!", error);
  }
};

// API call to add a review for a game
export const addReview = async (userId, gameId, rating, comment) => {
  try {
    const response = await axios.post('/api/reviews', {
      user_id: userId,
      game_id: gameId,
      rating: rating,
      comment: comment
    });
    return response.data;
  } catch (error) {
    console.error("There was an error submitting review!", error);
  }
};
