import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Style/Profile.css";

const Profile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [uploadedGames, setUploadedGames] = useState([]);
  const [reviews, setReviews] = useState([]);

  // Assuming you store token and userId in localStorage after login
  const token = localStorage.getItem("token");
  const userId = localStorage.getItem("userId");

  useEffect(() => {
    if (!token || !userId) return;

    // Set default authorization header
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

    // Fetch user profile
    axios
      .get(`/api/users/${userId}`)
      .then((res) => setUser(res.data))
      .catch((err) => console.error("Error fetching user:", err));

    // Fetch uploaded games
    axios
      .get(`/api/games?uploader=${userId}`)
      .then((res) => setUploadedGames(res.data))
      .catch((err) => console.error("Error fetching games:", err));

    // Fetch user reviews
    axios
      .get(`/api/users/${userId}/reviews`)
      .then((res) => setReviews(res.data))
      .catch((err) => console.error("Error fetching reviews:", err));
  }, [token, userId]);

  const handleDiscoverClick = () => {
    navigate("/library");
  };

  const handleAddGame = () => {
    navigate("/addgame");
  };

  if (!user) {
    return <div>Loading profile...</div>;
  }

  return (
    <div className="profile-page">
      <header>
        <div className="nav-bar">
          <span onClick={handleDiscoverClick} style={{ cursor: "pointer" }}>
            Discover
          </span>
          <span style={{ cursor: "pointer" }} onClick={() => navigate("/profile")}>Profile</span>
        </div>
      </header>

      <div className="profile-container">
        <section className="profile-header">
          <img src={user.avatar} alt="Avatar" className="avatar" />
          <div className="user-info">
            <h2>{user.username}</h2>
            <p>
              Level {user.level} • {user.yearsOfService} Years of Service
            </p>
            <p>Bank account number: {user.bankAccountNumber}</p>
            <p>Games Owned: {user.gamesOwned}</p>
            <p>DLCs Owned: {user.dlcsOwned}</p>
            <p>Wishlisted: {user.wishlisted}</p>
            <p>Reviews: {reviews.length}</p>
          </div>
        </section>

        <section className="recent-activity">
          <h3>Recent Activity</h3>
          <div className="activity-list">
            {user.recentActivity.map((item, idx) => (
              <div key={idx} className="activity-item">
                <img src={item.image} alt={item.game} />
                <p>{item.game}</p>
                <p>{item.hours} hrs</p>
                <p>Last played: {item.lastPlayed}</p>
              </div>
            ))}
          </div>
        </section>

        <section className="uploaded-games">
          <h3>Uploaded Games</h3>
          <div className="uploaded-list">
            {uploadedGames.map((game) => (
              <div key={game.id} className="game-card">
                <img src={game.imageUrl} alt={game.title} />
                <h4>{game.title}</h4>
                <p>{game.price}</p>
                <button onClick={() => navigate(`/games/${game.id}`)}>View</button>
              </div>
            ))}
          </div>
        </section>

        <section className="add-game-section">
          <button className="add-game-btn" onClick={handleAddGame}>
            Add Game
          </button>
        </section>
      </div>
    </div>
  );
};

export default Profile;
