import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/Checkout.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function Checkout({ user }) {
  const navigate = useNavigate();
  const userId = user?.id;

  const [cartItems, setCartItems] = useState([]);
  const [loadingCart, setLoadingCart] = useState(true);
  const [checkoutLoading, setCheckoutLoading] = useState(false);

  // Redirect if not logged in
  useEffect(() => {
    if (!userId) {
      alert('Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ thanh to√°n');
      navigate('/login');
    }
  }, [userId, navigate]);

  // Fetch gi·ªè h√†ng t·ª´ API
  useEffect(() => {
    if (!userId) return;
    (async () => {
      try {
        const res = await axios.get(`/api/cart/${userId}`);
        setCartItems(res.data.items || []);
      } catch (err) {
        console.error('L·∫•y gi·ªè h√†ng l·ªói:', err);
        alert('Kh√¥ng th·ªÉ t·∫£i gi·ªè h√†ng. Vui l√≤ng th·ª≠ l·∫°i sau.');
      } finally {
        setLoadingCart(false);
      }
    })();
  }, [userId]);

  // T√≠nh t·ªïng ti·ªÅn
  const totalPrice = cartItems.reduce((sum, item) => {
    const raw = typeof item.Price === 'number'
      ? item.Price
      : parseFloat(item.Price) || 0;
    const final = item.Discount > 0
      ? raw * (1 - item.Discount / 100)
      : raw;
    return sum + final;
  }, 0);

  // X·ª≠ l√Ω thanh to√°n
  const handleCheckout = async () => {
    if (!cartItems.length) {
      alert('Gi·ªè h√†ng ƒëang tr·ªëng.');
      return;
    }
    setCheckoutLoading(true);

    // L·∫•y danh s√°ch cartItemIds
    const cartItemIds = cartItems.map(item => item.cartItemId);

    // 1) Th·ª±c hi·ªán thanh to√°n
    let checkoutSuccess = false;
    try {
      const res = await axios.post('/api/checkout', { userId, cartItemIds });
      // Ch·ªâ t√≠nh l√† success khi backend tr·∫£ status 2xx v√† k√®m purchaseId
      if (res.status >= 200 && res.status < 300 && res.data.purchaseId) {
        checkoutSuccess = true;
      } else {
        throw new Error(res.data.message || 'Thanh to√°n l·ªói');
      }
    } catch (err) {
      console.error('Checkout l·ªói:', err);
      alert(err.response?.data.message || 'Thanh to√°n th·∫•t b·∫°i.');
      setCheckoutLoading(false);
      return;  // D·ª´ng ·ªü ƒë√¢y, kh√¥ng th√™m v√†o th∆∞ vi·ªán
    }

    // 2) N·∫øu thanh to√°n th√†nh c√¥ng, m·ªõi th√™m game v√†o th∆∞ vi·ªán
    try {
      // Batch add
      await axios.post(`/api/users/${userId}/library`, { gameIds: cartItemIds });
    } catch (batchErr) {
      console.warn('Batch add to library failed, fallback to individual adds:', batchErr);
      for (const id of cartItemIds) {
        try {
          await axios.post(`/api/users/${userId}/library`, { gameId: id });
        } catch (singleErr) {
          console.error(`Add game ${id} to library failed:`, singleErr);
        }
      }
    }

    alert('Thanh to√°n th√†nh c√¥ng!');
    setCheckoutLoading(false);
    navigate('/library');
  };


  if (loadingCart) {
    return <div className="checkout-page"><p>ƒêang t·∫£i gi·ªè h√†ng...</p></div>;
  }

  return (
    <div className="checkout-page">
      <header className="store-header">
        <div className="nav">
          <div className="steam-logo" onClick={() => navigate('/')}>My Game Store</div>
          <div className="user-actions">
            <button className="download-btn" onClick={() => navigate(-1)}>‚Üê Quay l·∫°i</button>
            <button className="cart-btn" onClick={() => navigate('/cart')}>Gi·ªè h√†ng</button>
          </div>
        </div>
      </header>

      <div className="checkout-main">
    <div className="checkout-container">
      <h2 className="checkout-title">üõí Thanh to√°n</h2>
      <div className="order-summary">
        <h3>T√≥m t·∫Øt ƒë∆°n h√†ng</h3>
        <ul className="checkout-cart-list">
          {cartItems.map(item => (
            <li className="checkout-cart-item" key={item.id}>
              <span className="checkout-cart-item-name">{item.name}</span>
              <span className="checkout-cart-item-price">${item.price.toFixed(2)}</span>
            </li>
          ))}
        </ul>
      </div>
      <div className="checkout-footer">
        <div className="checkout-cart-total">
          T·ªïng ti·ªÅn: <span>${totalPrice.toFixed(2)}</span>
        </div>
        <button className="checkout-btn" onClick={handleCheckout}>
          Thanh to√°n
        </button>
      </div>
    </div>
  </div>

      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Logo" className="footer-logo" />
            <p>¬© 2025 My Game Store</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy</li>
              <li>Terms</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
}
