// src/GameDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/GameDetail.css';

// Configure axios base URL
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function GameDetail() {
  const { gameId } = useParams();
  const navigate = useNavigate();

  const [game, setGame] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [inCart, setInCart] = useState(false);

  const userId = 1; // Replace with actual logged-in user ID or context

  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        const res = await axios.get(`/api/games/${gameId}`);
        setGame(res.data);

        // Fetch reviews for this game
        const revRes = await axios.get(`/api/games/${gameId}/reviews`);
        const list = Array.isArray(revRes.data) ? revRes.data : revRes.data.reviews;
        setReviews(list || []);
      } catch (err) {
        setError(err.response?.data.error || 'Failed to load data');
      } finally {
        setLoading(false);
      }
    }
    fetchData();
  }, [gameId]);

  // Add to Cart API integration
  const handleAddToCart = async () => {
    try {
      const res = await axios.post('/api/cart/add', {
        userId,
        gameId: parseInt(gameId, 10)
      });
      if (res.status === 201) {
        setInCart(true);
        navigate('/cart');
      } else {
        alert('Already in cart');
      }
    } catch (err) {
      console.error('Add to cart error:', err);
      alert('Error adding to cart.');
    }
  };

  // Review submission handler (unchanged)
  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`/api/games/${gameId}/reviews`, {
        user_id: userId,
        comment,
        star: parseInt(rating, 10)
      });
      setShowModal(true);
      const revRes = await axios.get(`/api/games/${gameId}/reviews`);
      const list = Array.isArray(revRes.data) ? revRes.data : revRes.data.reviews;
      setReviews(list || []);
      setRating('');
      setComment('');
    } catch (err) {
      setError(err.response?.data.error || 'Failed to submit review');
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="game-detail">
      {game && (
        <>
          <div className="detail-banner">
            <img src={game.screenshotUrl} alt={game.title} />
          </div>

          <div className="detail-main">
            <div className="detail-left">
              <h2>{game.title}</h2>
              <p>{game.description}</p>
              {/* Add to Cart Button */}
              <button
                className="buy-button"
                onClick={handleAddToCart}
                disabled={inCart}
              >
                {inCart ? 'In Cart' : 'Add to Cart'}
              </button>

              <hr />

              <div className="reviews-list">
                {reviews.length > 0 ? (
                  reviews.map(r => (
                    <div key={r.id} className="review-item">
                      <strong>User: {r.user_id}</strong>
                      <p>{r.comment}</p>
                      <span>Rating: {r.star} â˜…</span>
                    </div>
                  ))
                ) : (
                  <p>No reviews yet.</p>
                )}
              </div>
            </div>

            <div className="detail-right">
              <button onClick={() => setShowModal(true)} className="open-review-btn">
                Write a Review
              </button>

              {showModal && (
                <div className="modal">
                  <form onSubmit={handleReviewSubmit} className="review-form">
                    <label>
                      Rating:
                      <select
                        value={rating}
                        onChange={e => setRating(e.target.value)}
                      >
                        <option value="">--Select--</option>
                        {[1,2,3,4,5].map(n => (
                          <option key={n} value={n}>{n}</option>
                        ))}
                      </select>
                    </label>
                    <label>
                      Comment:
                      <textarea
                        value={comment}
                        onChange={e => setComment(e.target.value)}
                      />
                    </label>
                    <button type="submit" className="submit-btn">
                      Submit Review
                    </button>
                    <button type="button" onClick={() => setShowModal(false)}>
                      Close
                    </button>
                  </form>
                </div>
              )}
            </div>
          </div>

          <footer className="detail-footer">
            <div className="footer-content">
              <ul>
                <li>Privacy Policy</li>
                <li>Terms of Service</li>
              </ul>
            </div>
          </footer>
        </>
      )}
    </div>
  );
}
