import React, { useState, useEffect } from 'react';
import axios from 'axios';  // Đảm bảo rằng axios đã được import đúng
import { getGames, addReview } from './services/apiService';  // Đảm bảo đường dẫn chính xác từ file này

const GameDetail = ({ gameId, userId }) => {
  const [game, setGame] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [reviewText, setReviewText] = useState('');

  useEffect(() => {
    const fetchGameDetails = async () => {
      if (!gameId) {
        console.error('No gameId provided');
        return;
      }

      try {
        const response = await getGames(); // Gọi API từ apiService.js
        console.log('API response:', response);  // Kiểm tra dữ liệu trả về

        // Kiểm tra xem dữ liệu có phải là mảng không
        if (response && Array.isArray(response)) {
          const currentGame = response.find(game => game.id === gameId);
          if (currentGame) {
            setGame(currentGame);
          } else {
            console.error('Game not found for id:', gameId);
          }
        } else {
          console.error('API response is not an array:', response);
        }
      } catch (error) {
        console.error('Error fetching game details:', error);
      }
    };

    fetchGameDetails();
  }, [gameId]);

  const handleSubmitReview = async () => {
    try {
      const newReview = await addReview(userId, gameId, rating, reviewText);
      setReviews([...reviews, newReview]);
      setReviewText('');
      setRating(5); // Reset rating
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  if (!game) return <div>Loading...</div>;  // Kiểm tra khi game chưa được tải

  return (
    <div>
      <h2>{game.name}</h2>
      <p>{game.description}</p>
      <p>Price: ${game.price}</p>
      <img src={game.imageUrl} alt={game.name} />

      <h3>Reviews</h3>
      {reviews.map((review) => (
        <div key={review.id}>
          <p>Rating: {review.rating} stars</p>
          <p>{review.comment}</p>
        </div>
      ))}

      <h3>Submit your review</h3>
      <form onSubmit={handleSubmitReview}>
        <div>
          <label>Rating: </label>
          <select value={rating} onChange={(e) => setRating(e.target.value)}>
            {[1, 2, 3, 4, 5].map((star) => (
              <option key={star} value={star}>
                {star} Star{star > 1 ? 's' : ''}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Review: </label>
          <textarea
            value={reviewText}
            onChange={(e) => setReviewText(e.target.value)}
            rows="4"
            cols="50"
            placeholder="Write your review here"
          />
        </div>
        <button type="submit">Submit Review</button>
      </form>
    </div>
  );
};

export default GameDetail;
