// src/Checkout.js
import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import './Style/Checkout.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function Checkout() {
  const navigate = useNavigate();
  const location = useLocation();
  // Nhận cartItems và userId từ state (do navigate sang từ Cart)
  const { cartItems = [], userId = 1 } = location.state || {};

  const [loading, setLoading] = useState(false);

  // Tính tổng giá trị đơn hàng
  const totalPrice = cartItems.reduce((sum, item) => {
    const raw = typeof item.Price === 'number'
      ? item.Price
      : parseFloat(item.Price) || 0;
    const final = item.Discount > 0
      ? raw * (1 - item.Discount / 100)
      : raw;
    return sum + final;
  }, 0);

  // Xử lý click nút Thanh toán
  const handleCheckout = async () => {
    if (cartItems.length === 0) {
      alert('Giỏ hàng đang trống.');
      return;
    }
    setLoading(true);
    try {
      const res = await axios.post('/api/checkout', { userId, cartItems });
      setLoading(false);
      alert('Thanh toán thành công! Mã đơn: ' + res.data.purchaseId);
      // Chuyển về trang thư viện hoặc trang khác
      navigate('/library');
    } catch (err) {
      setLoading(false);
      console.error('Checkout error:', err);
      alert('Thanh toán thất bại. Vui lòng thử lại.');
    }
  };

  return (
    <div className="checkout-page">
      <header className="checkout-header">
        <h1>Checkout</h1>
        <button onClick={() => navigate(-1)} className="back-btn">
          ← Quay lại
        </button>
      </header>

      <main className="checkout-main">
        {cartItems.length === 0 ? (
          <p>Giỏ hàng của bạn đang trống.</p>
        ) : (
          <>
            <table className="checkout-table">
              <thead>
                <tr>
                  <th>Game</th>
                  <th>Giá</th>
                  <th>Discount</th>
                </tr>
              </thead>
              <tbody>
                {cartItems.map(item => {
                  const raw = typeof item.Price === 'number'
                    ? item.Price
                    : parseFloat(item.Price) || 0;
                  const final = item.Discount > 0
                    ? raw * (1 - item.Discount / 100)
                    : raw;
                  return (
                    <tr key={item.cartItemId}>
                      <td>{item.Name}</td>
                      <td>${raw.toFixed(2)}</td>
                      <td>
                        {item.Discount > 0
                          ? `${item.Discount}% → $${final.toFixed(2)}`
                          : '—'}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>

            <div className="checkout-summary">
              <span>Tổng thanh toán:</span>
              <strong>${totalPrice.toFixed(2)}</strong>
            </div>

            <button
              className="checkout-btn"
              onClick={handleCheckout}
              disabled={loading}
            >
              {loading ? 'Đang xử lý...' : 'Thanh toán'}
            </button>
          </>
        )}
      </main>

      <footer className="checkout-footer">
        © 2025 My Game Store
      </footer>
    </div>
  );
}
