// src/GameDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/GameDetail.css';

// Thiết lập Base URL cho Axios
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const placeholderBanner = '/images/placeholder-banner.jpg';

export default function GameDetail() {
  const navigate = useNavigate();
  // Đổi tên param slug cho khớp với route
  const { slug } = useParams();

  const [game, setGame] = useState(null);
  const [notFound, set404] = useState(false);

  // State cho review
  const [rating, setRating] = useState(5);
  const [reviewText, setReviewText] = useState('');
  const [reviews, setReviews] = useState([]);

  // Lấy dữ liệu khi slug thay đổi
  useEffect(() => {
    if (!slug) return;
    setGame(null);
    set404(false);

    axios
      .get(`/api/games/${encodeURIComponent(slug)}`)
      .then(res => setGame(res.data))
      .catch(() => set404(true));
  }, [slug]);

  // Xử lý gửi review
  const submitReview = e => {
    e.preventDefault();
    const newRev = { rating, text: reviewText, date: new Date().toLocaleDateString() };
    setReviews([newRev, ...reviews]);
    setReviewText('');
    setRating(5);
  };

  // Chuyển về trang store
  const handleDiscoverClick = () => navigate('/store');

  if (notFound) {
    return <div className="detail-not-found">Game not found</div>;
  }

  if (!game) {
    return <div className="loading">Loading...</div>;
  }

  // URL banner
  const bannerUrl = game.imageUrl || placeholderBanner;

  return (
    <div className="game-detail">
      {/* Header */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>Steam</span>
          <div className="user-actions">
            <button className="download-btn" onClick={handleDiscoverClick}>
              Library
            </button>
            <span className="cart-btn" onClick={() => navigate('/profile')} style={{ cursor: 'pointer' }}>
              Profile
            </span>
          </div>
        </div>
      </header>

      {/* Banner */}
      <div className="detail-banner">
        <img src={bannerUrl} alt={game.title} />
      </div>

      {/* Info */}
      <div className="detail-info">
        <h2>{game.title}</h2>
        <p>{game.description}</p>
        <ul className="detail-meta">
          <li><strong>Developer:</strong> {game.developer}</li>
          <li><strong>Publisher:</strong> {game.publisher}</li>
          <li><strong>Genre:</strong> {game.genre}</li>
        </ul>
        <div className="detail-price">
          {Number(game.discount) > 0 && (
            <span className="discount">-{game.discount}%</span>
          )}
          <span className="price">
            {Number(game.price) === 0 ? 'Free' : `$${game.price}`}
          </span>
        </div>
        <button className="buy-btn">
          {Number(game.price) === 0 ? 'Play Now' : 'Buy Now'}
        </button>
      </div>

      {/* Reviews */}
      <section className="reviews-section">
        <h3>Reviews</h3>
        {reviews.length === 0 ? (
          <p>No reviews yet. Be the first!</p>
        ) : (
          reviews.map((r, i) => (
            <div key={i} className="review-item">
              <strong>{r.rating} Star{r.rating > 1 ? 's' : ''}</strong>
              <p>{r.text}</p>
              <small>{r.date}</small>
            </div>
          ))
        )}
        <form className="submit-review" onSubmit={submitReview}>
          <label>
            Rating:
            <select
              className="rating-select"
              value={rating}
              onChange={e => setRating(Number(e.target.value))}
            >
              {[1,2,3,4,5].map(n => (
                <option key={n} value={n}>{n}</option>
              ))}
            </select>
          </label>
          <label>
            Your Review:
            <textarea
              value={reviewText}
              onChange={e => setReviewText(e.target.value)}
            />
          </label>
          <button type="submit">Submit</button>
        </form>
      </section>

      {/* Footer */}
      <footer className="store-footer">
        <div className="footer-content">
          <div>
            <ul>
              <li>About</li>
              <li>Careers</li>
              <li>Steamworks</li>
              <li>Steam Distribution</li>
              <li>Support</li>
            </ul>
          </div>
          <div>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Cookies</li>
            </ul>
          </div>
        </div>
      </footer>
    </div>
  );
}
