import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Style/StorePage.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function StorePage({ cartItems, setCartItems }) {
  const [featured, setFeatured] = useState([]);
  const [special, setSpecial] = useState([]);
  const [freeGames, setFreeGames] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [games, setGames] = useState([]);
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState(0);
  const [query, setQuery] = useState('');
  const navigate = useNavigate();
  const PAGE_SIZE = 4;
  const [pageInfo, setPageInfo] = useState({ special: 1, free: 1, best: 1 });
  const [totalPages, setTotalPages] = useState({ special: 1, free: 1, best: 1 });

  // Fetch genres
  useEffect(() => {
    axios.get('/api/genres')
      .then(res => {
        const raw = res.data;
        let arr = Array.isArray(raw.data) ? raw.data : [];
        const list = arr.map(g => ({ id: g.GenreID, name: g.Name }));
        setGenres([{ id: 0, name: 'All' }, ...list]);
      })
      .catch(err => console.error('Error fetching genres:', err));
  }, []);

  // Fetch games
  useEffect(() => {
    async function fetchData() {
      try {
        const paramsAll = { page: 1, pageSize: 1000 };
        const paramsPaged = { page: 1, pageSize: PAGE_SIZE };
        const [resFeat, resSpec, resFree, resBest, resAll] = await Promise.all([
          axios.get('/api/games', { params: paramsPaged }),
          axios.get('/api/games', { params: { ...paramsPaged, type: 'special' } }),
          axios.get('/api/games', { params: { ...paramsPaged, type: 'free' } }),
          axios.get('/api/games', { params: { ...paramsPaged, type: 'best' } }),
          axios.get('/api/games', { params: paramsAll }),
        ]);
        const feats = resFeat.data.items || [];
        const specs = resSpec.data.items || [];
        const frees = resFree.data.items || [];
        const bests = resBest.data.items || [];
        const all = resAll.data.items || [];
        setFeatured(feats);
        setSpecial(specs);
        setFreeGames(frees);
        setBestSellers(bests);
        setGames(all);
        setTotalPages({
          special: Math.ceil((resSpec.data.total || 0) / PAGE_SIZE),
          free: Math.ceil((resFree.data.total || 0) / PAGE_SIZE),
          best: Math.ceil((resBest.data.total || 0) / PAGE_SIZE),
        });
      } catch (err) {
        console.error('Error fetching games:', err);
      }
    }
    fetchData();
  }, []);

  const handlePageChange = (category, delta) => {
    setPageInfo(prev => {
      const next = prev[category] + delta;
      return { ...prev, [category]: Math.max(1, Math.min(next, totalPages[category])) };
    });
  };

  function safePrice(val) {
    if (val == null) return null;
    const num = Number(String(val).replace(/,/g, ''));
    return Number.isFinite(num) ? num : null;
  }

  // Filter helper
  const appliesFilter = game => {
    const title = game.title || game.Title;
    const matchesQuery = title.toLowerCase().includes(query.toLowerCase());
    const genreId = game.genreId || game.GenreID;
    const matchesGenre = selectedGenre === 0 || genreId === selectedGenre;
    return matchesQuery && matchesGenre;
  };

  // Prepare filtered lists
  const filteredFeatured = featured.filter(appliesFilter);
  const filteredSpecial = special.filter(appliesFilter);
  const filteredFree = freeGames.filter(appliesFilter);
  const filteredBest = bestSellers.filter(appliesFilter);
  const filteredAll = games.filter(appliesFilter);

  return (
    <div className="store-page">
      {/* Header, Banner, Search & Genres */}
      <header className="store-header">…</header>
      <div className="banner">…</div>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search games..."
          value={query}
          onChange={e => setQuery(e.target.value)}
        />
      </div>
      <div className="genres-container">
        {genres.map(g => (
          <button
            key={g.id}
            className={selectedGenre === g.id ? 'genre-button active' : 'genre-button'}
            onClick={() => setSelectedGenre(g.id)}
          >{g.name}</button>
        ))}
      </div>

      {/* Content: Search Results or Main Sections */}
      <div className="content-container">
        {query ? (
          <section className="search-results">
            <h2>Search Results for "{query}"</h2>
            <div className="game-list">
              {filteredAll.length ? filteredAll.map(game => (
                <Link key={game.id || game.GameID} to={`/game/${game.id || game.GameID}`} className="game-card">
                  <img src={game.imageUrl || game.ImageUrl} alt={game.title || game.Title} />
                  <h3>{game.title || game.Title}</h3>
                  {safePrice(game.price || game.Price) > 0
                    ? <span>${safePrice(game.price || game.Price).toFixed(2)}</span>
                    : <span>Free</span>}
                </Link>
              )) : <p>No games found.</p>}
            </div>
          </section>
        ) : (
          <>
            {/* Featured */}
            <section className="special-offers">
              <h2>Featured Game</h2>
              {filteredFeatured[0] && (
                <div className="featured">…</div>
              )}
            </section>

            {/* Special Deals */}
            <section className="special-offers">
              <h2>Special Deals</h2>
              <div className="game-list">…</div>
              <div className="pagination">…</div>
            </section>

            {/* Free Games */}
            <section className="free-games">…</section>

            {/* Best Sellers */}
            <section className="best-seller">…</section>
          </>
        )}
      </div>

      {/* Footer */}
      <footer className="footer">…</footer>
    </div>
  );
}
