import React, { useState, useEffect } from "react";
import axios from "axios";
import { NavLink, useNavigate } from "react-router-dom";
import "./Style/Profile.css";

// Hàm parse JWT payload mà không cần thư viện ngoài
function parseJwt(token) {
  if (!token) return {};
  const parts = token.split('.');
  if (parts.length < 2) return {};
  const base64 = parts[1].replace(/-/g, '+').replace(/_/g, '/');
  try {
    const json = decodeURIComponent(
      atob(base64)
        .split('')
        .map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2))
        .join('')
    );
    return JSON.parse(json);
  } catch (e) {
    console.error('Error parsing token:', e);
    return {};
  }
}

const Profile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [uploadedGames, setUploadedGames] = useState([]);
  const [reviews, setReviews] = useState([]);

  // Lấy token và giải mã userId
  const token = localStorage.getItem("token");
  const decoded = parseJwt(token);
  const userId = decoded.sub || decoded.userId || localStorage.getItem("userId");

  useEffect(() => {
    if (!token || !userId) {
      navigate("/login");
      return;
    }

    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

    // Fetch profile
    axios
      .get(`/api/users/${userId}`)
      .then(res => setUser(res.data))
      .catch(err => console.error("Error fetching user:", err));

    // Fetch uploaded games
    axios
      .get(`/api/games?uploader=${userId}`)
      .then(res => {
        let list = [];
        if (Array.isArray(res.data.data)) list = res.data.data;
        else if (Array.isArray(res.data.games)) list = res.data.games;
        else if (Array.isArray(res.data)) list = res.data;
        setUploadedGames(list);
      })
      .catch(err => console.error("Error fetching games:", err));

    // Fetch reviews
    axios
      .get(`/api/users/${userId}/reviews`)
      .then(res => {
        let list = [];
        if (Array.isArray(res.data.data)) list = res.data.data;
        else if (Array.isArray(res.data.reviews)) list = res.data.reviews;
        else if (Array.isArray(res.data)) list = res.data;
        setReviews(list);
      })
      .catch(err => console.error("Error fetching reviews:", err));

  }, [token, userId, navigate]);

  if (!user) {
    return <div>Loading profile...</div>;
  }

  return (
    <div className="profile-page">
      <header>
        <nav className="nav-bar">
          <NavLink
            to="/library"
            className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
          >
            Discover
          </NavLink>
          <NavLink
            to="/profile"
            className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
          >
            Profile
          </NavLink>
        </nav>
      </header>

      <div className="profile-container">
        {/* Profile Header */}
        <section className="profile-header">
          {user.avatar && <img src={user.avatar} alt="Avatar" className="avatar" />}
          <div className="user-info">
            <h2>{user.username}</h2>
            {user.level && user.yearsOfService && (
              <p>Level {user.level} • {user.yearsOfService} Years of Service</p>
            )}
            <p>Bank account number: {user.bankAccountNumber}</p>
            <p>Games Owned: {user.gamesOwned ?? 0}</p>
            <p>DLCs Owned: {user.dlcsOwned ?? 0}</p>
            <p>Wishlisted: {user.wishlisted ?? 0}</p>
            <p>Reviews: {reviews.length}</p>
          </div>
        </section>

        {/* Recent Activity */}
        <section className="recent-activity">
          <h3>Recent Activity</h3>
          {Array.isArray(user.recentActivity) && user.recentActivity.length > 0 ? (
            <div className="activity-list">
              {user.recentActivity.map((item, idx) => (
                <div key={idx} className="activity-item">
                  <img src={item.image} alt={item.game} />
                  <p>{item.game}</p>
                  <p>{item.hours} hrs</p>
                  <p>Last played: {item.lastPlayed}</p>
                </div>
              ))}
            </div>
          ) : (
            <p>Chưa có hoạt động nào gần đây.</p>
          )}
        </section>

        {/* Uploaded Games */}
        <section className="uploaded-games">
          <h3>Uploaded Games</h3>
          {uploadedGames.length > 0 ? (
            <div className="uploaded-list">
              {uploadedGames.map(game => (
                <div key={game.id} className="game-card">
                  <img src={game.imageUrl} alt={game.title} />
                  <h4>{game.title}</h4>
                  <p>{game.price}</p>
                  <button onClick={() => navigate(`/games/${game.id}`)}>View</button>
                </div>
              ))}
            </div>
          ) : (
            <p>Chưa có game nào được upload.</p>
          )}
        </section>

        {/* Add Game Section */}
        <section className="add-game-section">
          <button className="add-game-btn" onClick={() => navigate("/addgame")}>Add Game</button>
        </section>
      </div>
    </div>
  );
};

export default Profile;
