import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Style/Profile.css";

// set base URL for all requests
axios.defaults.baseURL =
  process.env.REACT_APP_API_URL || "http://localhost:5000";

export default function Profile({ user, onLogout, setUser }) {
  const navigate = useNavigate();

  // dữ liệu gốc
  const [purchaseHistory, setPurchaseHistory] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [loadingHistory, setLoadingHistory] = useState(true);

  // trạng thái phân trang
  const [historyPage, setHistoryPage] = useState(1);
  const [reviewsPage, setReviewsPage] = useState(1);
  const itemsPerPage = 5; // số mục trên mỗi trang

  // fetch purchase history
  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const res = await axios.get(`/api/users/${user.id}/purchase-history`);
        setPurchaseHistory(res.data || []);
      } catch (err) {
        console.error(err);
      } finally {
        setLoadingHistory(false);
      }
    };
    fetchHistory();
  }, [user.id]);

  // fetch reviews
  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const res = await axios.get(`/api/users/${user.id}/reviews`);
        setReviews(res.data || []);
      } catch (err) {
        console.error(err);
      }
    };
    fetchReviews();
  }, [user.id]);

  // tính tổng trang
  const totalHistoryPages = Math.ceil(purchaseHistory.length / itemsPerPage) || 1;
  const totalReviewsPages = Math.ceil(reviews.length / itemsPerPage) || 1;

  // dữ liệu trang hiện tại
  const currentHistory = purchaseHistory.slice(
    (historyPage - 1) * itemsPerPage,
    historyPage * itemsPerPage
  );
  const currentReviews = reviews.slice(
    (reviewsPage - 1) * itemsPerPage,
    reviewsPage * itemsPerPage
  );

  // handler chuyển trang
  const goHistoryPage = (newPage) => {
    if (newPage >= 1 && newPage <= totalHistoryPages) {
      setHistoryPage(newPage);
    }
  };
  const goReviewsPage = (newPage) => {
    if (newPage >= 1 && newPage <= totalReviewsPages) {
      setReviewsPage(newPage);
    }
  };

  return (
    <div className="profile-container">
      <header className="profile-header">
        <h1>Welcome, {user.username}</h1>
        <button onClick={onLogout}>Logout</button>
      </header>

      <main className="profile-main">
        {/* Purchase History Section */}
        <section className="profile-section">
          <h2>Purchase History</h2>
          {loadingHistory ? (
            <p>Loading purchase history...</p>
          ) : currentHistory.length === 0 ? (
            <p>You have no purchase history yet.</p>
          ) : (
            <>
              <ul className="history-list">
                {currentHistory.map((item) => (
                  <li key={item.id} className="history-item">
                    <span className="history-product">{item.productName}</span>
                    <span className="history-date">{new Date(item.date).toLocaleDateString()}</span>
                    <span className="history-price">${item.price.toFixed(2)}</span>
                  </li>
                ))}
              </ul>
              <div className="pagination">
                <button
                  onClick={() => goHistoryPage(historyPage - 1)}
                  disabled={historyPage === 1}
                >
                  « Prev
                </button>
                <span>
                  Page {historyPage} of {totalHistoryPages}
                </span>
                <button
                  onClick={() => goHistoryPage(historyPage + 1)}
                  disabled={historyPage === totalHistoryPages}
                >
                  Next »
                </button>
              </div>
            </>
          )}
        </section>

        {/* Reviews Section */}
        <section className="profile-section">
          <h2>Your Reviews</h2>
          {reviews.length === 0 ? (
            <p>You haven't written any reviews yet.</p>
          ) : (
            <>
              <ul className="reviews-list">
                {currentReviews.map((rev) => (
                  <li key={rev.id} className="review-item">
                    <h3>{rev.productName}</h3>
                    <p className="review-text">{rev.text}</p>
                    <small className="review-date">
                      {new Date(rev.date).toLocaleDateString()}
                    </small>
                  </li>
                ))}
              </ul>
              <div className="pagination">
                <button
                  onClick={() => goReviewsPage(reviewsPage - 1)}
                  disabled={reviewsPage === 1}
                >
                  « Prev
                </button>
                <span>
                  Page {reviewsPage} of {totalReviewsPages}
                </span>
                <button
                  onClick={() => goReviewsPage(reviewsPage + 1)}
                  disabled={reviewsPage === totalReviewsPages}
                >
                  Next »
                </button>
              </div>
            </>
          )}
        </section>
      </main>

      <footer className="profile-footer">
        <div className="footer-left">
          <img src="/logo.png" alt="Logo" className="footer-logo" />
          <p>© 2023 Valve Corporation. All rights reserved.</p>
          <p>All trademarks are property of their respective owners.</p>
          <p>VAT included in all prices where applicable.</p>
        </div>
        <div className="footer-links">
          <ul>
            <li>About Valve</li>
            <li>Jobs</li>
            <li>Steamworks</li>
            <li>Distribution</li>
            <li>Support</li>
          </ul>
          <ul>
            <li>Privacy Policy</li>
            <li>Legal</li>
            <li>Subscriber Agreement</li>
            <li>Refunds</li>
            <li>Cookies</li>
          </ul>
        </div>
      </footer>
    </div>
  );
}
