import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Style/StorePage.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function StorePage({ cartItems, setCartItems }) {
  /* ================= STATE ================= */
  // Genre filter
  const [genres, setGenres] = useState([]);
  const [selectedGenreId, setSelectedGenreId] = useState(null);

  // Main game grid (filtered)
  const [games, setGames] = useState([]);
  const [page, setPage] = useState(1);
  const pageSize = 12;
  const [totalPagesGames, setTotalPagesGames] = useState(1);

  // Featured / special / free / best blocks (static size 4)
  const PAGE_SIZE_BLOCK = 4;
  const [featured, setFeatured]       = useState([]);
  const [special, setSpecial]         = useState([]);
  const [freeGames, setFreeGames]     = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [pageInfo, setPageInfo] = useState({ special: 1, free: 1, best: 1 });
  const [totalPagesBlock, setTotalPagesBlock] = useState({ special: 1, free: 1, best: 1 });

  // Search
  const [query, setQuery] = useState('');
  const navigate = useNavigate();

  /* ============== FETCH GENRES (once) ============== */
  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const res = await axios.get('/api/genres');
        const list = Array.isArray(res.data) ? res.data : (res.data.items || []);
      setGenres(list);
        if (res.data.length) setSelectedGenreId(res.data[0].id);
      } catch (err) {
        console.error('Load genres fail:', err);
      }
    };
    fetchGenres();
  }, []);

  /* ============== FETCH GAMES (when filter changes) ============== */
  useEffect(() => {
    if (selectedGenreId === null) return;
    const fetchGames = async () => {
      try {
        const params = { page, pageSize, genreId: selectedGenreId, q: query.trim() };
        const res = await axios.get('/api/games', { params });
        setGames(res.data.items);
        setTotalPagesGames(res.data.totalPages || 1);
      } catch (err) {
        console.error('Load games fail:', err);
      }
    };
    fetchGames();
  }, [page, pageSize, query, selectedGenreId]);

  /* ============== FETCH SPECIAL / FREE / BEST blocks (once) ============== */
  useEffect(() => {
    const fetchBlocks = async () => {
      try {
        const params = { page: 1, pageSize: PAGE_SIZE_BLOCK };
        const [resFeat, resSpec, resFree, resBest] = await Promise.all([
          axios.get('/api/games', { params }),
          axios.get('/api/games', { params: { ...params, type: 'special' } }),
          axios.get('/api/games', { params: { ...params, type: 'free' } }),
          axios.get('/api/games', { params: { ...params, type: 'best' } }),
        ]);
        setFeatured(resFeat.data.items);
        setSpecial(resSpec.data.items);
        setFreeGames(resFree.data.items);
        setBestSellers(resBest.data.items);
        setTotalPagesBlock({
          special: Math.ceil(resSpec.data.total / PAGE_SIZE_BLOCK) || 1,
          free:    Math.ceil(resFree.data.total / PAGE_SIZE_BLOCK)  || 1,
          best:    Math.ceil(resBest.data.total / PAGE_SIZE_BLOCK)  || 1,
        });
      } catch (err) {
        console.error('Load blocks fail:', err);
      }
    };
    fetchBlocks();
  }, []);

  /* ============== HANDLERS ============== */
  const handleGenreClick = (id) => { setSelectedGenreId(id); setPage(1); };
  const handlePageChangeGames = (delta) => setPage(p => Math.max(1, Math.min(totalPagesGames, p + delta)));
  const handleBlockPageChange = (category, delta) => {
    setPageInfo(prev => {
      const next = prev[category] + delta;
      return { ...prev, [category]: Math.max(1, Math.min(next, totalPagesBlock[category])) };
    });
  };
  const handleDiscoverClick = () => navigate('/library');

  /* ============== UTIL ============== */
  const safePrice = (val) => {
    if (val == null || val === '') return null;
    let str = String(val).trim();
    if (/^\d{1,3}(\.\d{3})*,\d+$/.test(str)) str = str.replace(/\./g, '').replace(',', '.');
    else {
      const parts = str.split('.');
      if (parts.length > 2) { const dec = parts.pop(); str = parts.join('') + '.' + dec; }
      str = str.replace(/,/g, '');
    }
    const num = Number(str);
    return Number.isFinite(num) ? num : null;
  };

  /* ============== RENDER ============== */
  return (
    <div className="store-page">
      {/* Header */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}><img src="/logo.png" alt="Logo" className="footer-logo" /></span>
          <div className="user-actions">
            <button className="download-btn" onClick={handleDiscoverClick}>Library</button>
            <span className="cart-btn" onClick={() => navigate('/cart')}>Cart</span>
            <span className="cart-btn" onClick={() => navigate('/profile')}>Profile</span>
          </div>
        </div>
      </header>

      {/* Banner */}
      <div className="banner"><img src="/images/featured-banner.jpg" alt="Featured" className="banner-img"/></div>

      {/* Genre Bar */}
      <div className="genre-bar">
        {genres.map(g => (
          <button key={g.id} className={`genre-btn ${selectedGenreId===g.id?'active':''}`} onClick={() => handleGenreClick(g.id)}>{g.name}</button>
        ))}
      </div>

      {/* Search */}
      <div className="search-bar"><input type="text" placeholder="Search games..." value={query} onChange={e=>{setQuery(e.target.value); setPage(1);}} /></div>

      {/* Game Grid */}
      <div className="game-grid">
        {games.map(game => (
          <Link key={game.id} to={`/game/${game.id}`} className="game-card">
            <img src={game.cover || game.imageUrl} alt={game.title} className="game-cover" />
            <div className="game-info">
              <h3>{game.title}</h3>
              <p className="price">
                {game.discountPrice ? (<><span className="discount-price">{game.discountPrice.toLocaleString()}₫</span><span className="original-price">{game.price.toLocaleString()}₫</span></>) : (<span>{game.price.toLocaleString()}₫</span>)}
              </p>
            </div>
          </Link>
        ))}
        {games.length===0 && <p className="empty">Không có game nào phù hợp.</p>}
      </div>

      {/* Pagination main grid */}
      <div className="pagination">
        <button disabled={page===1} onClick={()=>handlePageChangeGames(-1)}>‹</button>
        {Array.from({length: totalPagesGames}, (_,i)=>i+1).map(p=>(
          <button key={p} className={page===p?'active':''} onClick={()=>setPage(p)}>{p}</button>
        ))}
        <button disabled={page===totalPagesGames} onClick={()=>handlePageChangeGames(1)}>›</button>
      </div>

      {/* --------- Special Deals Block --------- */}
      <section className="special-offers">
        <h2>Special Deals</h2>
        <div className="game-list">
          {special.map(game => (
            <Link key={game.id} to={`/game/${game.id}`} className="game-card">
              {game.discount>0 && <div className="discount">-{game.discount}%</div>}
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
              <span className="discounted-price">{safePrice(game.price) && (safePrice(game.price)*(1-game.discount/100)).toLocaleString()}₫</span>
            </Link>
          ))}
        </div>
        <div className="pagination">
          <button onClick={()=>handleBlockPageChange('special',-1)} disabled={pageInfo.special===1}>‹ Prev</button>
          <span>{pageInfo.special}/{totalPagesBlock.special}</span>
          <button onClick={()=>handleBlockPageChange('special',1)} disabled={pageInfo.special===totalPagesBlock.special}>Next ›</button>
        </div>
      </section>

      {/* --------- Free Games Block --------- */}
      <section className="free-games">
        <h2>Free Games</h2>
        <div className="game-list">
          {freeGames.map(game => (
            <Link key={game.id} to={`/game/${game.id}`} className="game-card">
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
              <p>Free</p>
            </Link>
          ))}
        </div>
        <div className="pagination">
          <button onClick={()=>handleBlockPageChange('free',-1)} disabled={pageInfo.free===1}>‹ Prev</button>
          <span>{pageInfo.free}/{totalPagesBlock.free}</span>
          <button onClick={()=>handleBlockPageChange('free',1)} disabled={pageInfo.free===totalPagesBlock.free}>Next ›</button>
        </div>
      </section>

      {/* --------- Best Sellers Block --------- */}
      <section className="best-seller">
        <h2>Best Sellers</h2>
        <div className="game-list">
          {bestSellers.map(game => (
            <Link key={game.id} to={`/game/${game.id}`} className="game-card">
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
              <span className="discounted-price">{safePrice(game.price) && (safePrice(game.price)*(1-game.discount/100)).toLocaleString()}₫</span>
            </Link>
          ))}
        </div>
        <div className="pagination">
          <button onClick={()=>handleBlockPageChange('best',-1)} disabled={pageInfo.best===1}>‹ Prev</button>
          <span>{pageInfo.best}/{totalPagesBlock.best}</span>
          <button onClick={()=>handleBlockPageChange('best',1)} disabled={pageInfo.best===totalPagesBlock.best}>Next ›</button>
        </div>
      </section>

      {/* ---------- SPECIAL BLOCKS (featured, special deals, free, best) ---------- */}
      {/* Featured */}
      <section className="special-offers">
        <h2>Featured Game</h2>
        {featured[0] && (
          <div className="featured">
            <Link to={`/game/${featured[0].id}`} className="game-card">
              <img src={featured[0].imageUrl} alt={featured[0].title} />
            </Link>
            <div className="game-info">
              <h3>{featured[0].title}</h3>
              <p>{featured[0].description}</p>
              <p>{(featured[0].salesCount || 0).toLocaleString()} lượt mua</p>
            </div>
          </div>
        )}
      </section>

      {/* Special Deals */}
      <section className="special-offers">
        <h2>Special Deals</h2>
        <div className="game-list">
          {special.map(game => (
            <Link key={game.id} to={`/game/${game.id}`} className="game-card">
              {game.discount > 0 && <div className="discount">-{game.discount}%</div>}
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
              {safePrice(game.price) > 0 && <span className="original-price">${(safePrice(game.price) ?? 0).toFixed(2)}</span>}
              <span className="discounted-price">${((safePrice(game.price) ?? 0) * (1 - game.discount / 100)).toFixed(2)}</span>
              <p>{(game.salesCount || 0).toLocaleString()} lượt mua</p>
            </Link>
          ))}
        </div>
        <div className="pagination">
          <button onClick={() => handleBlockPageChange('special', -1)} disabled={pageInfo.special === 1}>Prev</button>
          <span>{pageInfo.special}/{totalPagesBlock.special}</span>
          <button onClick={() => handleBlockPageChange('special', 1)} disabled={pageInfo.special === totalPagesBlock.special}>Next</button>
        </div>
      </section>

      {/* Free Games */}
      <section className="free-games">
        <h2>Free Games</h2>
        <div className="game-list">
          {freeGames.map(game => (
            <Link key={game.id} to={`/game/${game.id}`} className="game-card">
              {game.discount > 0 && <div className="discount">-{game.discount}%</div>}
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
              <p>Free</p>
              <p>{(game.salesCount || 0).toLocaleString()} lượt mua</p>
            </Link>
          ))}
        </div>
        <div className="pagination">
          <button onClick={() => handleBlockPageChange('free', -1)} disabled={pageInfo.free === 1}>Prev</button>
          <span>{pageInfo.free}/{totalPagesBlock.free}</span>
          <button onClick={() => handleBlockPageChange('free', 1)} disabled={pageInfo.free === totalPagesBlock.free}>Next</button>
        </div>
      </section>

      {/* Best Sellers */}
      <section className="best-seller">
        <h2>Best Sellers</h2>
        <div className="game-list">
          {bestSellers.map(game => (
            <Link key={game.id} to={`/game/${game.id}`} className="game-card">
              <div className="discount">-{game.discount}%</div>
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
              {safePrice(game.price) > 0 && <span className="original-price">${(safePrice(game.price) ?? 0).toFixed(2)}</span>}
              <span className="discounted-price">${((safePrice(game.price) ?? 0) * (1 - game.discount / 100)).toFixed(2)}</span>
              <p>{(game.salesCount || 0).toLocaleString()} lượt mua</p>
            </Link>
          ))}
        </div>
        <div className="pagination">
          <button onClick={() => handleBlockPageChange('best', -1)} disabled={pageInfo.best === 1}>Prev</button>
          <span>{pageInfo.best}/{totalPagesBlock.best}</span>
          <button onClick={() => handleBlockPageChange('best', 1)} disabled={pageInfo.best === totalPagesBlock.best}>Next</button>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img
              src="/logo.png"
              alt="Valve Steam Logo"
              className="footer-logo"
            />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>
              All trademarks are property of their respective owners in the US
              and other countries.
            </p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Steam Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Steam Subscriber Agreement</li>
              <li>Refunds</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
}
