// src/GameDetail.js
import React, { useState, useEffect } from 'react';
import { useParams }           from 'react-router-dom';
import axios                   from 'axios';
import './Style/GameDetail.css';

const placeholderBanner = '/images/placeholder-banner.jpg';

export default function GameDetail() {
  // Đón cả slug key và numeric gameId
  const { key, gameId } = useParams();
  const slugOrId        = key || gameId;        

  const [game, setGame]       = useState(null);
  const [notFound, set404]    = useState(false);
  const [rating, setRating]   = useState(5);
  const [review, setReview]   = useState('');

  useEffect(() => {
    if (!slugOrId) return;      // nếu không có param thì dừng
    setGame(null);
    set404(false);

    // Gọi API tới backend (thay proxy bằng URL tuyệt đối)
    axios.get(`http://localhost:5000/api/games/${slugOrId}`)
      .then(r => setGame(r.data))
      .catch(() => set404(true));
  }, [slugOrId]);

  // 1) Loading
  if (game === null && !notFound) {
    return <div className="loading">Loading...</div>;
  }
  // 2) Not found
  if (notFound) {
    return <div className="detail-not-found">Game not found</div>;
  }
  // 3) Success
  const banner = game.imageUrl || placeholderBanner;

  const submitReview = e => {
    e.preventDefault();
    console.log('Send review →', { rating, review, gameId: game.id });
    setReview('');
    setRating(5);
    alert('Cảm ơn bạn đã đánh giá!');
  };

  return (
    <div className="game-detail">
      {/* Header */}
      <header className="detail-header">
        <div className="nav">
          <div className="steam-logo">MySteam</div>
          <nav>
            <a href="/">Home</a>
            <a href="/special">Special offers</a>
          </nav>
        </div>
      </header>

      {/* Banner */}
      <div className="detail-banner">
        <img src={banner} alt={game.name} />
      </div>

      {/* Main Detail */}
      <main className="detail-main">
        {/* Left column */}
        <section className="detail-left">
          <h2>{game.name}</h2>
          <p className="description">{game.description}</p>
          <p><strong>Developer:</strong> {game.developer}</p>
          <p><strong>Publisher:</strong> {game.publisher}</p>
          <p><strong>Genre:</strong> {game.genre}</p>
          <div className="detail-buy">
            <div className="price">${game.price}</div>
            <button className="buy-button">Add to Cart</button>
          </div>
          <hr />
        </section>
        {/* Right column */}
        <aside className="detail-right">
          {game.screenshotUrl && (
            <img
              className="detail-screenshot"
              src={game.screenshotUrl}
              alt={`${game.name} screenshot`}
            />
          )}
        </aside>
      </main>

      {/* Reviews */}
      <div className="detail-extras">
        <h3>Reviews</h3>
        <form className="submit-review" onSubmit={submitReview}>
          <h4>Đánh giá trò chơi</h4>
          <div className="form-group">
            <label>Rating</label>
            <select
              className="rating-select"
              value={rating}
              onChange={e => setRating(+e.target.value)}
            >
              {[1,2,3,4,5].map(n => (
                <option key={n} value={n}>
                  {n} Star{n>1 && 's'}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group" style={{ marginTop: 16 }}>
            <label>Your review</label>
            <textarea
              className="review-textarea"
              rows="5"
              placeholder="Write something awesome…"
              value={review}
              onChange={e => setReview(e.target.value)}
            />
          </div>
          <button className="submit-btn" disabled={!review.trim()}>
            Submit Review
          </button>
        </form>
      </div>

      {/* Footer */}
      <footer className="detail-footer">
        &copy; 2025 MySteam. All rights reserved.
      </footer>
    </div>
  );
}
