import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Style/AdminPanel.css';

/**
 * AdminPanel.js (with Modal Forms & Handlers)
 * Updated: 21-May-2025
 */
const pageSize = 5;
const API_USERS = '/api/admin/users';
const API_ORDERS = '/api/admin/orders';
const API_GAMES = '/api/games';

const AdminPanel = ({ onLogout }) => {
  const token = sessionStorage.getItem('token');
  const authHeader = { Authorization: `Bearer ${token}` };
  const [activeTab, setActiveTab] = useState('users');

  // --- Users ---
  const [users, setUsers] = useState([]);
  const [searchUser, setSearchUser] = useState('');
  const [userPage, setUserPage] = useState(1);
  const [totalUsers, setTotalUsers] = useState(0);
  const [selectedUser, setSelectedUser] = useState(null);

  // --- Orders ---
  const [orders, setOrders] = useState([]);
  const [orderPage, setOrderPage] = useState(1);
  const [totalOrders, setTotalOrders] = useState(0);

  // --- Games ---
  const [games, setGames] = useState([]);
  const [searchGame, setSearchGame] = useState('');
  const [gamePage, setGamePage] = useState(1);
  const [totalGames, setTotalGames] = useState(0);

  // --- Modal state ---
  const [modalVisible, setModalVisible] = useState(false);
  const [modalType, setModalType] = useState(''); // 'editUser','addGame','editGame'
  const [modalData, setModalData] = useState({});

  // Effects
  useEffect(() => { if (activeTab === 'users') fetchUsers(); }, [searchUser, userPage, activeTab]);
  useEffect(() => { if (activeTab === 'orders') fetchOrders(); }, [orderPage, activeTab]);
  useEffect(() => { if (activeTab === 'games') fetchGames(); }, [searchGame, gamePage, activeTab]);

  // Fetchers
  const fetchUsers = async () => {
    try {
      const { data } = await axios.get(API_USERS, { params: { search: searchUser, page: userPage, pageSize }, headers: authHeader });
      setUsers(data.users);
      setTotalUsers(data.total);
      setSelectedUser(null);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchOrders = async () => {
    try {
      const { data } = await axios.get(API_ORDERS, { params: { page: orderPage, pageSize }, headers: authHeader });
      setOrders(data.orders);
      setTotalOrders(data.total);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchGames = async () => {
    try {
      const { data } = await axios.get(API_GAMES, { params: { search: searchGame, page: gamePage, pageSize }, headers: authHeader });
      setGames(data.items);
      setTotalGames(data.total);
    } catch (err) {
      console.error(err);
    }
  };

  // Handlers
  const openEditUser = (user) => { setModalType('editUser'); setModalData({ ...user }); setModalVisible(true); };
  const openAddGame = () => { setModalType('addGame'); setModalData({ title: '', price: '', genreId: '', discount: 0 }); setModalVisible(true); };
  const openEditGame = (game) => { setModalType('editGame'); setModalData({ ...game }); setModalVisible(true); };

  // Delete user
  const handleDeleteUser = async (id) => {
    if (!window.confirm('Xoá người dùng?')) return;
    try {
      await axios.delete(`${API_USERS}/${id}`, { headers: authHeader });
      fetchUsers();
    } catch (err) {
      alert(err.response?.data?.message || 'Lỗi xoá người dùng');
    }
  };

  // View order
  const handleViewOrder = async (id) => {
    try {
      const { data } = await axios.get(`${API_ORDERS}/${id}`, { headers: authHeader });
      alert(JSON.stringify(data.orderDetails, null, 2));
    } catch (err) {
      alert(err.response?.data?.message || 'Lỗi xem đơn hàng');
    }
  };

  // Delete game
  const handleDeleteGame = async (id) => {
    if (!window.confirm('Xoá game?')) return;
    try {
      await axios.delete(`${API_GAMES}/${id}`, { headers: authHeader });
      fetchGames();
    } catch (err) {
      alert(err.response?.data?.message || 'Lỗi xoá game');
    }
  };

  // Modal form change
  const handleModalChange = (e) => {
    const { name, value } = e.target;
    setModalData((d) => ({ ...d, [name]: value }));
  };

  // Modal submit
  const handleModalSubmit = async () => {
    try {
      if (modalType === 'editUser') {
        const { id, Name, role, accountNumberBank } = modalData;
        await axios.put(`${API_USERS}/${id}`, { name: Name, role, accountNumberBank }, { headers: authHeader });
        fetchUsers();
      } else if (modalType === 'addGame') {
        const { title, price, genreId, discount } = modalData;
        await axios.post(API_GAMES, { Name: title, Price: Number(price), genreId: Number(genreId), Discount: Number(discount) }, { headers: authHeader });
        fetchGames();
      } else if (modalType === 'editGame') {
        const { id, title, price, discount, genreId } = modalData;
        await axios.put(`${API_GAMES}/${id}`, { Name: title, Price: Number(price), genreId: Number(genreId), Discount: Number(discount) }, { headers: authHeader });
        fetchGames();
      }
      setModalVisible(false);
    } catch (err) {
      alert(err.response?.data?.message || 'Lỗi lưu dữ liệu');
    }
  };

  // Render Modal
  const renderModal = () => {
    if (!modalVisible) return null;
    const isUser = modalType === 'editUser';
    const isAddG = modalType === 'addGame';
    const isEditG = modalType === 'editGame';
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h3>{isUser ? 'Chỉnh sửa Người dùng' : isAddG ? 'Thêm Game' : 'Chỉnh sửa Game'}</h3>
          <form onSubmit={(e) => { e.preventDefault(); handleModalSubmit(); }}>
            {isUser && (
              <>
                <label>Họ tên:<input name="Name" value={modalData.Name || ''} onChange={handleModalChange} /></label>
                <label>Role:<input name="role" value={modalData.role || ''} onChange={handleModalChange} /></label>
                <label>Số TK:<input name="accountNumberBank" value={modalData.accountNumberBank || ''} onChange={handleModalChange} /></label>
              </>
            )}
            {(isAddG || isEditG) && (
              <>
                <label>Title:<input name="title" value={modalData.title || ''} onChange={handleModalChange} /></label>
                <label>Price:<input name="price" type="number" value={modalData.price || ''} onChange={handleModalChange} /></label>
                <label>Discount:<input name="discount" type="number" value={modalData.discount || 0} onChange={handleModalChange} /></label>
                <label>GenreID:<input name="genreId" type="number" value={modalData.genreId || ''} onChange={handleModalChange} /></label>
              </>
            )}
            <div className="modal-actions">
              <button type="button" onClick={() => setModalVisible(false)}>Hủy</button>
              <button type="submit">Lưu</button>
            </div>
          </form>
        </div>
      </div>
    );
  };

  // Pagination counts
  const totalUserPages = Math.max(1, Math.ceil(totalUsers / pageSize));
  const totalOrderPages = Math.max(1, Math.ceil(totalOrders / pageSize));
  const totalGamePages = Math.max(1, Math.ceil(totalGames / pageSize));

  return (
    <div className="admin-panel-container">
      <aside className="admin-sidebar">
        <h2 className="admin-sidebar-title">Quản lý</h2>
        <div className={`admin-menu-item ${activeTab === 'users' ? 'active' : ''}`} onClick={() => setActiveTab('users')}>Người dùng</div>
        <div className={`admin-menu-item ${activeTab === 'orders' ? 'active' : ''}`} onClick={() => setActiveTab('orders')}>Đơn hàng</div>
        <div className={`admin-menu-item ${activeTab === 'games' ? 'active' : ''}`} onClick={() => setActiveTab('games')}>Game</div>
        <button className="admin-logout-btn" onClick={onLogout}>Đăng xuất</button>
      </aside>

      <main className="admin-main">
        {activeTab === 'users' && (
          <section className="admin-section users-grid">
            <div className="users-table-wrap">
              <h2>Quản lý Người dùng</h2>
              <input placeholder="Tìm kiếm" value={searchUser} onChange={e => { setSearchUser(e.target.value); setUserPage(1); }} />
              <table className="admin-table">
                <thead><tr><th>ID</th><th>Name</th><th>Email</th><th>Role</th><th>Hành động</th></tr></thead>
                <tbody>
                  {!users.length && <tr><td colSpan={5}>Không có người dùng</td></tr>}
                  {users.map((u) => (
                    <tr key={u.id} onClick={() => setSelectedUser(u)} className={selectedUser?.id === u.id ? 'selected' : ''}>
                      <td>{u.id}</td>
                      <td>{u.Name || u.name}</td>
                      <td>{u.Email || u.email}</td>
                      <td>{u.role}</td>
                      <td>
                        <button onClick={() => openEditUser(u)}>Sửa</button>
                        <button onClick={() => handleDeleteUser(u.id)}>Xoá</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <div className="admin-pagination">
                <button disabled={userPage === 1} onClick={() => setUserPage((p) => p - 1)}>«</button>
                <span>{userPage}/{totalUserPages}</span>
                <button disabled={userPage === totalUserPages} onClick={() => setUserPage((p) => p + 1)}>»</button>
              </div>
            </div>
            <div className="user-detail-card">
              {selectedUser ? (
                <>
                  <h3>Thông tin chi tiết</h3>
                  <p><strong>ID:</strong> {selectedUser.id}</p>
                  <p><strong>Họ tên:</strong> {selectedUser.Name || selectedUser.name}</p>
                  <p><strong>Email:</strong> {selectedUser.Email || selectedUser.email}</p>
                  <p><strong>Role:</strong> {selectedUser.role}</p>
                  {selectedUser.accountNumberBank && <p><strong>Số TK:</strong> {selectedUser.accountNumberBank}</p>}
                </>
              ) : (
                <p>Chọn một người dùng để xem chi tiết…</p>
              )}
            </div>
          </section>
        )}

        {activeTab === 'orders' && (
          <section className="admin-section">
            <h2>Quản lý Đơn hàng</h2>
            <table className="admin-table">
              <thead><tr><th>ID</th><th>User</th><th>Total</th><th>Date</th><th>Hành động</th></tr></thead>
              <tbody>
                {!orders.length && <tr><td colSpan={5}>Không có đơn hàng</td></tr>}
                {orders.map((o) => (
                  <tr key={o.id}>
                    <td>{o.id}</td>
                    <td>{o.customerName}</td>
                    <td>{o.total_amount?.toLocaleString()}</td>
                    <td>{new Date(o.purchase_date).toLocaleDateString()}</td>
                    <td><button onClick={() => handleViewOrder(o.id)}>Xem</button></td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div className="admin-pagination">
              <button disabled={orderPage === 1} onClick={() => setOrderPage((p) => p - 1)}>«</button>
              <span>{orderPage}/{totalOrderPages}</span>
              <button disabled={orderPage === totalOrderPages} onClick={() => setOrderPage((p) => p + 1)}>»</button>
            </div>
          </section>
        )}

        {activeTab === 'games' && (
          <section className="admin-section">
            <h2>Quản lý Game</h2>
            <div className="admin-toolbar">
              <input placeholder="Tìm kiếm" value={searchGame} onChange={e => { setSearchGame(e.target.value); setGamePage(1); }} />
              <button onClick={openAddGame}>+ Thêm game</button>
            </div>
            <table className="admin-table">
              <thead><tr><th>ID</th><th>Title</th><th>Price</th><th>Discount</th><th>Hành động</th></tr></thead>
              <tbody>
                {!games.length && <tr><td colSpan={5}>Không có game</td></tr>}
                {games.map((g) => (
                  <tr key={g.id}>
                    <td>{g.id}</td>
                    <td>{g.title}</td>
                    <td>{g.price?.toLocaleString()}</td>
                    <td>{g.discount}</td>
                    <td>
                    <button className="admin-btn edit"   onClick={() => openEditGame(g)}>Sửa</button>
<button className="admin-btn delete" onClick={() => handleDeleteGame(g.id)}>Xoá</button>

                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div className="admin-pagination">
              <button disabled={gamePage === 1} onClick={() => setGamePage((p) => p - 1)}>«</button>
              <span>{gamePage}/{totalGamePages}</span>
              <button disabled={gamePage === totalGamePages} onClick={() => setGamePage((p) => p + 1)}>»</button>
            </div>
          </section>
        )}
      </main>

      {renderModal()}
    </div>
  );
};

export default AdminPanel;
