const AdminPanel = ({ onLogout }) => {
  /* ========== AUTH ========== */
  const token      = sessionStorage.getItem('token');
  const authHeader = { Authorization: `Bearer ${token}` };

  /* ========== STATE ========== */
  const [activeTab, setActiveTab] = useState('users');

  // Users
  const [users, setUsers] = useState([]);
  const [searchUser, setSearchUser] = useState('');
  const [userPage, setUserPage] = useState(1);
  const [totalUsers, setTotalUsers] = useState(0);
  const [selectedUser, setSelectedUser] = useState(null);

  // Orders
  const [orders, setOrders] = useState([]);
  const [orderPage, setOrderPage] = useState(1);
  const [totalOrders, setTotalOrders] = useState(0);

  // Games
  const [games, setGames] = useState([]);
  const [searchGame, setSearchGame] = useState('');
  const [gamePage, setGamePage] = useState(1);
  const [totalGames, setTotalGames] = useState(0);

  // Genres  ⭢ NEW
  const [genres, setGenres] = useState([]);
  const [searchGenre, setSearchGenre] = useState('');
  const [genrePage, setGenrePage] = useState(1);
  const [totalGenres, setTotalGenres] = useState(0);

  // Modal
  const [modalVisible, setModalVisible] = useState(false);
  const [modalType, setModalType]       = useState('');
  const [modalData, setModalData]       = useState({});

  /* ========== EFFECTS ========== */
  useEffect(() => { if (activeTab==='users')   fetchUsers();   }, [searchUser,  userPage,  activeTab]);
  useEffect(() => { if (activeTab==='orders')  fetchOrders();  }, [orderPage,   activeTab]);
  useEffect(() => { if (activeTab==='games')   fetchGames();   }, [searchGame,  gamePage,  activeTab]);
  useEffect(() => { if (activeTab==='genres')  fetchGenres();  }, [searchGenre, genrePage, activeTab]);

  /* ========== API FETCHERS ========== */
  const fetchUsers  = async () => {
    const { data } = await axios.get(API_USERS , { params:{ search:searchUser , page:userPage , pageSize }, headers:authHeader });
    setUsers(data.users); setTotalUsers(data.total); setSelectedUser(null);
  };
  const fetchOrders = async () => {
    const { data } = await axios.get(API_ORDERS, { params:{ page:orderPage , pageSize },            headers:authHeader });
    setOrders(data.orders); setTotalOrders(data.total);
  };
  const fetchGames  = async () => {
    const { data } = await axios.get(API_GAMES , { params:{ search:searchGame , page:gamePage , pageSize }, headers:authHeader });
    setGames(data.items); setTotalGames(data.total);
  };
  const fetchGenres = async () => {                         // <── NEW
    const { data } = await axios.get(API_GENRES, { params:{ keyword:searchGenre , page:genrePage , pageSize }, headers:authHeader });
    // BE trả về {data,total,...} theo code cũ
    setGenres(data.data || []); setTotalGenres(data.total);   // .data vì BE trả về {data:[...]}
  };

  /* ========== HANDLERS (OPEN MODAL) ========== */
  const openEditUser  = (u) => { setModalType('editUser');  setModalData({...u});   setModalVisible(true); };
  const openAddGame   = ()  => { setModalType('addGame');   setModalData({ title:'',description:'',developer:'',publisher:'',price:'',discount:0,genreId:'' }); setModalVisible(true); };
  const openEditGame  = (g) => { setModalType('editGame');  setModalData({...g});   setModalVisible(true); };

  /* Genre modals */                                       // <── NEW
  const openAddGenre  = ()  => { setModalType('addGenre');  setModalData({ name:'' });       setModalVisible(true); };
  const openEditGenre = (gr) =>{ setModalType('editGenre'); setModalData({...gr});           setModalVisible(true); };

  /* ========== DELETE ACTIONS ========== */
  const handleDeleteUser  = async(id)=>{ if(confirmDel())   { await axios.delete(`${API_USERS}/${id}`,  {headers:authHeader}); fetchUsers(); } };
  const handleDeleteGame  = async(id)=>{ if(confirmDel())   { await axios.delete(`${API_GAMES}/${id}`,  {headers:authHeader}); fetchGames(); } };
  const handleDeleteGenre = async(id)=>{ if(confirmDel())   { await axios.delete(`${API_GENRES}/${id}`, {headers:authHeader}); fetchGenres(); } };

  const confirmDel = () => window.confirm('Xác nhận xoá?');

  const handleViewOrder = async(id)=>{
    const {data}=await axios.get(`${API_ORDERS}/${id}`,{headers:authHeader});
    alert(JSON.stringify(data.orderDetails,null,2));
  };

  /* ========== MODAL CHANGE & SUBMIT ========== */
  const handleModalChange = e => setModalData(d=>({...d,[e.target.name]:e.target.value}));

  const handleModalSubmit = async () => {
    try{
      if(modalType==='editUser'){ /* ... giữ nguyên */ 
        const {id,Name,role,accountNumberBank}=modalData;
        await axios.put(`${API_USERS}/${id}`,{name:Name,role,accountNumberBank},{headers:authHeader});
        fetchUsers();

      }else if(modalType==='addGame'){ const {title,description,developer,publisher,price,discount,genreId}=modalData;
        await axios.post(API_GAMES,{Name:title,Description:description,Developer:developer,Publisher:publisher,Price:+price,Discount:+discount,genreId:+genreId},{headers:authHeader});
        fetchGames();

      }else if(modalType==='editGame'){ const {id,title,description,developer,publisher,price,discount,genreId}=modalData;
        await axios.put(`${API_GAMES}/${id}`,{Name:title,Description:description,Developer:developer,Publisher:publisher,Price:+price,Discount:+discount,genreId:+genreId},{headers:authHeader});
        fetchGames();

      }else if(modalType==='addGenre'){ const {name}=modalData;               /* <── NEW */ 
        await axios.post(API_GENRES,{Name:name},{headers:authHeader});
        fetchGenres();

      }else if(modalType==='editGenre'){ const {GenreID,name}=modalData;      /* <── NEW */
        await axios.put(`${API_GENRES}/${GenreID}`,{Name:name},{headers:authHeader});
        fetchGenres();
      }

      setModalVisible(false);
    }catch(err){ alert(err.response?.data?.message||'Lỗi'); }
  };

  /* ========== MODAL RENDER ========== */
  const Modal = ()=> modalVisible && (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3>{
          modalType==='editUser'  ? 'Chỉnh sửa Người dùng' :
          modalType==='addGame'   ? 'Thêm Game'            :
          modalType==='editGame'  ? 'Chỉnh sửa Game'       :
          modalType==='addGenre'  ? 'Thêm Thể loại'        :
                                    'Chỉnh sửa Thể loại'
        }</h3>
        <form onSubmit={e=>{e.preventDefault();handleModalSubmit();}}>
          {modalType==='editUser' && (
            <>
              <label>Họ tên:<input name="Name" value={modalData.Name||''} onChange={handleModalChange}/></label>
              <label>Role:<input name="role" value={modalData.role||''} onChange={handleModalChange}/></label>
              <label>Số TK:<input name="accountNumberBank" value={modalData.accountNumberBank||''} onChange={handleModalChange}/></label>
            </>
          )}

          {(modalType==='addGame'||modalType==='editGame') && (
            <>
              <label>Title:<input name="title" value={modalData.title||''} onChange={handleModalChange}/></label>
              <label>Description:<input name="description" value={modalData.description||''} onChange={handleModalChange}/></label>
              <label>Developer:<input name="developer" value={modalData.developer||''} onChange={handleModalChange}/></label>
              <label>Publisher:<input name="publisher" value={modalData.publisher||''} onChange={handleModalChange}/></label>
              <label>Price:<input type="number" name="price" value={modalData.price||''} onChange={handleModalChange}/></label>
              <label>Discount %:<input type="number" name="discount" value={modalData.discount||0} onChange={handleModalChange}/></label>
              <label>GenreID:<input type="number" name="genreId" value={modalData.genreId||''} onChange={handleModalChange}/></label>
            </>
          )}

          {(modalType==='addGenre'||modalType==='editGenre') && (
            <label>Tên thể loại:<input name="name" value={modalData.name||modalData.Name||''} onChange={handleModalChange}/></label>
          )}

          <div className="modal-actions">
            <button type="button" onClick={()=>setModalVisible(false)}>Huỷ</button>
            <button type="submit">Lưu</button>
          </div>
        </form>
      </div>
    </div>
  );

  /* ========== PAGINATION TOTALS ========== */
  const totalUserPages  = Math.max(1,Math.ceil(totalUsers /pageSize));
  const totalOrderPages = Math.max(1,Math.ceil(totalOrders/pageSize));
  const totalGamePages  = Math.max(1,Math.ceil(totalGames /pageSize));
  const totalGenrePages = Math.max(1,Math.ceil(totalGenres/pageSize));

  /* -------------------------------- RENDER -------------------------------- */
  return (
    <div className="admin-panel-container">
      {/* ---------- SIDEBAR ---------- */}
      <aside className="admin-sidebar">
        <h3 className="admin-sidebar-title">Quản lý</h3>
        {['users','orders','games','genres'].map(tab=>(
          <div key={tab}
               className={`admin-menu-item ${activeTab===tab?'active':''}`}
               onClick={()=>setActiveTab(tab)}>
            {{
              users :'Người dùng',
              orders:'Đơn hàng',
              games :'Game',
              genres:'Thể loại'
            }[tab]}
          </div>
        ))}
        <button className="admin-logout-btn" onClick={onLogout}>Đăng xuất</button>
      </aside>

      {/* ---------- MAIN ---------- */}
      <main className="admin-main">
        {/* --- USERS TAB --- */}
        {/* ... (giữ nguyên phần Users & Orders & Games như trước) ... */}

        {/* --- GENRES TAB --- */}
        {activeTab==='genres' && (
          <section className="admin-section">
            <h2>Quản lý Thể loại</h2>
            <div className="admin-toolbar">
              <input placeholder="Tìm kiếm"
                     value={searchGenre}
                     onChange={e=>{ setSearchGenre(e.target.value); setGenrePage(1); }}/>
              <button onClick={openAddGenre}>+ Thêm thể loại</button>
            </div>
            <table className="admin-table">
              <thead><tr><th>ID</th><th>Name</th><th className="action-cell">Hành động</th></tr></thead>
              <tbody>
                {genres.length===0 ? (
                  <tr><td colSpan={3}>Không có thể loại</td></tr>
                ) : genres.map(g=>(
                  <tr key={g.GenreID}>
                    <td>{g.GenreID}</td>
                    <td>{g.Name}</td>
                    <td className="action-cell">
                      <button className="admin-btn edit"   onClick={()=>openEditGenre(g)}>Sửa</button>
                      <button className="admin-btn delete" onClick={()=>handleDeleteGenre(g.GenreID)}>Xoá</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div className="admin-pagination">
              <button disabled={genrePage===1} onClick={()=>setGenrePage(p=>p-1)}>«</button>
              <span>{genrePage}/{totalGenrePages}</span>
              <button disabled={genrePage===totalGenrePages} onClick={()=>setGenrePage(p=>p+1)}>»</button>
            </div>
          </section>
        )}
      </main>

      {Modal()}
    </div>
  );
};

export default AdminPanel;
