import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/Login.css';

const Login = () => {
  const navigate = useNavigate();
  const [isRegister, setIsRegister] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    accountNumberBank: ''
  });
  const [error, setError] = useState('');
  const [user, setUser] = useState(null);

  const handleChange = e => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    try {
      const url = isRegister
        ? 'http://localhost:5000/api/auth/register'
        : 'http://localhost:5000/api/auth/login';

      const payload = isRegister
        ? {
            name: formData.name,
            email: formData.email,
            password: formData.password,
            accountNumberBank: formData.accountNumberBank
          }
        : {
            email: formData.email,
            password: formData.password
          };

      const res = await axios.post(url, payload, {
        headers: { 'Content-Type': 'application/json' }
      });

      setUser(res.data);
      // Sau khi đăng nhập/đăng ký thành công, chuyển sang trang Store
      navigate('/store');
    } catch (err) {
      setError(err.response?.data?.error || 'Something went wrong');
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
      <h2>{isRegister ? 'Đăng ký' : 'Đăng nhập'}</h2>

      {user && (
        <div className="welcome">
          <p>Xin chào, <strong>{user.name || user.email}</strong>!</p>
        </div>
      )}

      <form onSubmit={handleSubmit} className="login-form">
        {isRegister && (
          <>
            <label>Họ & Tên</label>
            <input
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
            <label>Số tài khoản</label>
            <input
              name="accountNumberBank"
              value={formData.accountNumberBank}
              onChange={handleChange}
              required
            />
          </>
        )}

        <label>Email</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        />

        <label>Mật khẩu</label>
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          required
        />

        {error && <p className="error-message">{error}</p>}

        <button type="submit" className="btn">
          {isRegister ? 'Đăng ký' : 'Đăng nhập'}
        </button>
      </form>

      <p className="toggle-link">
        {isRegister ? 'Bạn đã có tài khoản? ' : 'Chưa có tài khoản? '}
        <span onClick={() => setIsRegister(!isRegister)}>
          {isRegister ? 'Đăng nhập ngay' : 'Đăng ký ngay'}
        </span>
      </p>
    </div>
    {/* Footer */}
    <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img
              src="/logo.png"
              alt="Valve Steam Logo"
              className="footer-logo"
            />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>
              All trademarks are property of their respective owners in the US
              and other countries.
            </p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Steam Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Steam Subscriber Agreement</li>
              <li>Refunds</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Login;
