import React, { useParams, useState, useEffect } from 'react';
import axios from 'axios';
import './Style/GameDetail.css';

const GameDetail = () => {
  const { gameId } = useParams();
  const [game, setGame] = useState(null);

  useEffect(() => {
    axios.get(`/api/games/${gameId}`)
      .then(res => setGame(res.data))
      .catch(() => setGame(undefined));
  }, [gameId]);

  if (game === null) return <div>Loading...</div>;
  if (!game) return <div className="detail-not-found">Game not found</div>;

  return (
    <div className="game-detail">
      <header className="detail-header">
        <div className="nav">
          <div className="steam-logo">MySteam</div>
          <nav>
            <a href="/">Home</a>
            <a href="/special">Special offers</a>
          </nav>
        </div>
      </header>

      <div className="detail-banner">
        <img src={game.imageUrl} alt={game.name} />
      </div>

      <main className="detail-main">
        <section className="detail-left">
          <h2>{game.name}</h2>
          <p className="description">{game.description}</p>
          <p><strong>Developer:</strong> {game.developer}</p>
          <p><strong>Publisher:</strong> {game.publisher}</p>
          <p><strong>Genre:</strong> {game.genre}</p>

          <div className="detail-buy">
            <div className="price">${game.price}</div>
            <button className="buy-button">Add to Cart</button>
          </div>

          <hr />
        </section>

        <aside className="detail-right">
          {game.screenshotUrl && (
            <img
              className="detail-screenshot"
              src={game.screenshotUrl}
              alt={`${game.name} screenshot`}
            />
          )}
          {/* Ví dụ hiển thị cấu hình nếu có */}
          {game.systemReq && (
            <div className="system-req">
              <h3>System Requirements</h3>
              <pre>{game.systemReq}</pre>
            </div>
          )}
        </aside>
      </main>

      {/* … phẩn extras, reviews, form review giữ nguyên hoặc tùy chỉnh … */}

      <footer className="detail-footer">
        &copy; 2025 MySteam. All rights reserved.
      </footer>
    </div>
  );
};

export default GameDetail;
