import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Style/StorePage.css';

/**
 * Chuyển val (string hoặc number) thành Number hợp lệ
 * - Hỗ trợ các định dạng: "1.234.567", "1,234,567.89", "1.234.567,89"
 * - Trả về null nếu không parse được
 */
function safePrice(val) {
  if (val == null || val === '') return null;

  let str = String(val).trim();

  // Trường hợp "1.234.567,89" (VN/Europe: dot thousands, comma decimal)
  if (/^\d{1,3}(\.\d{3})*,\d+$/.test(str)) {
    str = str.replace(/\./g, '').replace(',', '.');
  } else {
    // Nếu có nhiều dấu chấm, coi các chấm ở giữa là tách nghìn
    const parts = str.split('.');
    if (parts.length > 2) {
      const decimal = parts.pop();
      const integer = parts.join('');
      str = integer + '.' + decimal;
    }
    // Loại bỏ dấu phẩy tách nghìn
    str = str.replace(/,/g, '');
  }

  const num = Number(str);
  return Number.isFinite(num) ? num : null;
}

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function StorePage({ cartItems, setCartItems }) {
  const [featured, setFeatured] = useState([]);
  const [special, setSpecial] = useState([]);
  const [freeGames, setFreeGames] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const navigate = useNavigate();

  const fetchData = async () => {
    try {
      const [fRes, sRes, freeRes, bsRes] = await Promise.all([
        axios.get('/api/games/featured'),
        axios.get('/api/games/special'),
        axios.get('/api/games/free'),
        axios.get('/api/games/bestsellers'),
      ]);

      setFeatured(fRes.data);
      setSpecial(sRes.data);
      setFreeGames(freeRes.data);
      setBestSellers(bsRes.data);
    } catch (error) {
      console.error('Error fetching games:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Handlers giỏ hàng, phân trang, v.v.
  // ... (giữ nguyên code xử lý paging, thêm giỏ hàng)

  return (
    <div className="store-container">
      {/* Các phần FEATURED, BESTSELLERS, v.v. */}

      {/* SPECIAL DEALS */}
      <section className="special-deals">
        <h2>SPECIAL DEALS</h2>
        <div className="deal-list">
          {special.map((game) => (
            <div key={game.id} className="deal-card">
              <img src={game.image} alt={game.title} />
              <h3>{game.title}</h3>
              <span>
                {safePrice(game.price) != null
                  ? safePrice(game.price).toLocaleString('en-US', {
                      style: 'currency',
                      currency: 'USD',
                      minimumFractionDigits: 2,
                    })
                  : 'Free'}
              </span>
              <span className="original-price">
                {safePrice(game.price) != null
                  ? safePrice(game.price).toLocaleString('en-US', {
                      style: 'currency',
                      currency: 'USD',
                      minimumFractionDigits: 2,
                    })
                  : ''}
              </span>
              {/* Nếu có discount, hiển thị giá sau giảm */}
              {game.discount > 0 && (
                <span className="discounted-price">
                  {(
                    safePrice(game.price) * (1 - game.discount / 100)
                  ).toLocaleString('en-US', {
                    style: 'currency',
                    currency: 'USD',
                    minimumFractionDigits: 2,
                  })}
                </span>
              )}
              <p>{game.purchases.toLocaleString('vi-VN')} lượt mua</p>
            </div>
          ))}
        </div>
        {/* Pagination controls */}
      </section>

      {/* FREE GAMES */}
      <section className="free-games">
        <h2>FREE GAMES</h2>
        <div className="game-list">
          {freeGames.map((game) => (
            <div key={game.id} className="game-card">
              <img src={game.image} alt={game.title} />
              <h3>{game.title}</h3>
              <span>Free</span>
              <p>{game.purchases.toLocaleString('vi-VN')} lượt mua</p>
            </div>
          ))}
        </div>
      </section>

      {/* BESTSELLERS */}
      <section className="bestsellers">
        <h2>BESTSELLERS</h2>
        <div className="game-list">
          {bestSellers.map((game) => (
            <div key={game.id} className="game-card">
              <img src={game.image} alt={game.title} />
              <h3>{game.title}</h3>
              <span>
                {safePrice(game.price) != null
                  ? safePrice(game.price).toLocaleString('en-US', {
                      style: 'currency',
                      currency: 'USD',
                      minimumFractionDigits: 2,
                    })
                  : 'Free'}
              </span>
              <p>{game.purchases.toLocaleString('vi-VN')} lượt mua</p>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img
              src="/logo.png"
              alt="Valve Steam Logo"
              className="footer-logo"
            />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>
              All trademarks are property of their respective owners in the US
              and other countries.
            </p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Steam Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Steam Subscriber Agreement</li>
              <li>Refunds</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
}
