// File: src/services/apiService.js
import axios from 'axios';

const API_BASE = 'http://localhost:5000/api';

export const loginUser = async (email, password) => {
  const response = await axios.post(`${API_BASE}/customers/login`, { email, password });
  return response.data;
};

export const getStoreGames = async () => {
  const response = await axios.get(`${API_BASE}/games`);
  return response.data;
};

export const getGameReviews = async (gameId) => {
  const response = await axios.get(`${API_BASE}/reviews/${gameId}`);
  return response.data;
};

export const addGameReview = async (user_id, game_id, comment) => {
  const response = await axios.post(`${API_BASE}/reviews`, { user_id, game_id, comment });
  return response.data;
};

export const getCart = async (userId) => {
  const response = await axios.get(`${API_BASE}/carts/${userId}`);
  return response.data;
};

export const addToCart = async (user_id) => {
  const response = await axios.post(`${API_BASE}/carts`, { user_id });
  return response.data;
};

export const getLibrary = async (userId) => {
  const response = await axios.get(`${API_BASE}/libraries/${userId}`);
  return response.data;
};
