import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/Cart.css';

// Cấu hình base URL cho API
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const Cart = ({ cartItems = [], setCartItems }) => {
  const navigate = useNavigate();
  const userId = 1; // Thay bằng userId thật từ auth

  // Lấy danh sách items trong giỏ
  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await axios.get(`/api/cart/${userId}`);
        setCartItems(res.data.items || []);
      } catch (err) {
        console.error('Lấy giỏ hàng lỗi:', err);
      }
    };
    fetchCart();
  }, [setCartItems, userId]);

  // Xóa một item khỏi giỏ hàng
  const handleRemoveItem = async (cartItemId) => {
    try {
      await axios.delete(`/api/cart/${cartItemId}`);
      setCartItems(prev => prev.filter(item => item.cartItemId !== cartItemId));
    } catch (err) {
      console.error('Xóa item lỗi:', err);
    }
  };

  // Thanh toán giỏ hàng
  const handleCheckout = async () => {
    try {
      const cartItemIds = cartItems.map(item => item.cartItemId);
      const res = await axios.post('/api/checkout', { userId, cartItemIds });
      console.log('Thanh toán thành công, purchaseId:', res.data.purchaseId);
      setCartItems([]);
      navigate('/library'); // Chuyển hướng sau khi thanh toán
    } catch (err) {
      console.error('Thanh toán lỗi:', err);
    }
  };

  // Tính tổng tiền
  const totalPrice = cartItems.reduce((sum, item) => {
    const rawPrice = typeof item.Price === 'number'
      ? item.Price
      : parseFloat(item.Price) || 0;
    const finalPrice = item.Discount > 0
      ? rawPrice * (1 - item.Discount / 100)
      : rawPrice;
    return sum + finalPrice;
  }, 0);

  return (
    <div className="cart-page">
      <div className="detail-header">
        <h1>Giỏ hàng của bạn</h1>
        <button onClick={() => navigate(-1)}>Quay lại</button>
      </div>
      <div className="main-container">
        <div className="cart-container">
          {cartItems.length === 0 ? (
            <p>Giỏ hàng trống</p>
          ) : (
            <>
              <ul className="cart-list">
                {cartItems.map(item => {
                  const rawPrice = typeof item.Price === 'number'
                    ? item.Price
                    : parseFloat(item.Price) || 0;
                  const finalPrice = item.Discount > 0
                    ? rawPrice * (1 - item.Discount / 100)
                    : rawPrice;
                  return (
                    <li className="cart-item" key={item.cartItemId}>
                      <span className="cart-item-name">{item.Name}</span>
                      <span className="cart-item-price">${finalPrice.toFixed(2)}</span>
                      <button
                        className="remove-btn"
                        onClick={() => handleRemoveItem(item.cartItemId)}
                      >
                        Xóa
                      </button>
                    </li>
                  );
                })}
              </ul>
              <div className="checkout-container">
                <span className="total-label">Tổng cộng:</span>
                <span className="total-price">${totalPrice.toFixed(2)}</span>
                <button
                  className="checkout-btn"
                  onClick={handleCheckout}
                >
                  Thanh toán
                </button>
              </div>
            </>
          )}
        </div>
      </div>
      <footer className="footer">
        © 2025 My Game Store
      </footer>
    </div>
  );
};

export default Cart;
