import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Style/StorePage.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function StorePage({ cartItems, setCartItems }) {
  const [featured, setFeatured]       = useState([]);
  const [special, setSpecial]         = useState([]);
  const [freeGames, setFreeGames]     = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [games, setGames]             = useState([]);
  const [genres, setGenres]           = useState([]);
  const [selectedGenre, setSelectedGenre] = useState(0);
  const [query, setQuery]             = useState('');
  const [pageInfo, setPageInfo]       = useState({ special: 1, free: 1, best: 1, genre: 1 });
  const [totalPages, setTotalPages]   = useState({ special: 1, free: 1, best: 1, genre: 1 });

  const navigate = useNavigate();
  const PAGE_SIZE = 5;

  // Fetch genres
  useEffect(() => {
    axios.get('/api/genres')
      .then(res => {
        const arr = Array.isArray(res.data.data) ? res.data.data : [];
        setGenres([{ id: 0, name: 'All' }, ...arr.map(g => ({ id: g.GenreID, name: g.Name }))]);
      })
      .catch(err => console.error('Error fetching genres:', err));
  }, []);

  // Fetch games once (client-side pagination)
  useEffect(() => {
    async function fetchData() {
      try {
        const featParams  = { page: 1, pageSize: PAGE_SIZE };
        const largeParams = { page: 1, pageSize: 1000 };
        const [rFeat, rSpec, rFree, rBest, rAll] = await Promise.all([
          axios.get('/api/games', { params: featParams }),
          axios.get('/api/games', { params: { ...largeParams, type: 'special' } }),
          axios.get('/api/games', { params: { ...largeParams, type: 'free'    } }),
          axios.get('/api/games', { params: { ...largeParams, type: 'best'    } }),
          axios.get('/api/games', { params: largeParams }),
        ]);

        const feats = rFeat.data.items   || [];
        const specs = rSpec.data.items   || [];
        const frees = rFree.data.items   || [];
        const bests = rBest.data.items   || [];
        const all   = rAll.data.items    || [];

        setFeatured(feats);
        setSpecial(specs);
        setFreeGames(frees);
        setBestSellers(bests);
        setGames(all);

        setTotalPages({
          special: Math.max(1, Math.ceil(specs.length / PAGE_SIZE)),
          free:    Math.max(1, Math.ceil(frees.length / PAGE_SIZE)),
          best:    Math.max(1, Math.ceil(bests.length / PAGE_SIZE)),
          genre:   Math.max(1, Math.ceil(all.length   / PAGE_SIZE)),
        });
      } catch (err) {
        console.error('Error fetching games:', err);
      }
    }
    fetchData();
  }, []);

  // Safe parse and format price
  const safePrice = val => {
    const num = Number(String(val || '').replace(/,/g, ''));
    return Number.isFinite(num) ? num : null;
  };

  // Change page
  const handlePageChange = (cat, delta) => {
    setPageInfo(prev => {
      const nxt = prev[cat] + delta;
      return { ...prev, [cat]: Math.max(1, Math.min(nxt, totalPages[cat])) };
    });
  };

  // Select genre resets page
  const handleGenreSelect = id => {
    setSelectedGenre(id);
    setPageInfo(prev => ({ ...prev, genre: 1 }));
  };

  // Filter games
  const appliesFilter = game => {
    const title   = (game.title || game.Title || '').toLowerCase();
    const gid     = game.genreId || game.GenreID || 0;
    return title.includes(query.toLowerCase()) && (selectedGenre === 0 || gid === selectedGenre);
  };

  const fFeat = featured.filter(appliesFilter);
  const fSpec = special.filter(appliesFilter);
  const fFree = freeGames.filter(appliesFilter);
  const fBest = bestSellers.filter(appliesFilter);
  const fAll  = games.filter(appliesFilter);

  const slicePage = (arr, p) => {
    const start = (p - 1) * PAGE_SIZE;
    return arr.slice(start, start + PAGE_SIZE);
  };

  const pageSpec = slicePage(fSpec, pageInfo.special);
  const pageFree = slicePage(fFree, pageInfo.free);
  const pageBest = slicePage(fBest, pageInfo.best);
  const pageAll  = slicePage(fAll, pageInfo.genre);

  return (
    <div className="store-page">
      {/* HEADER */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>
            <img src="/logo.png" alt="Steam Logo" />
          </span>
          <div className="user-actions">
            <button className="btn-flat" onClick={() => navigate('/library')}>Library</button>
            <button className="btn-flat" onClick={() => navigate('/cart')}>Cart</button>
            <button className="btn-flat" onClick={() => navigate('/profile')}>Profile</button>
          </div>
        </div>
      </header>

      {/* BANNER */}
      <div className="banner">
        <img src="./summer_sale.jpg" alt="Sale Banner" className="banner-img" />
      </div>

      {/* SEARCH & GENRE */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search games..."
          value={query}
          onChange={e => setQuery(e.target.value)}
        />
      </div>
      <div className="genres-container">
        {genres.map(g => (
          <button
            key={g.id}
            className={`genre-button${selectedGenre === g.id ? ' active' : ''}`}
            onClick={() => handleGenreSelect(g.id)}
          >
            {g.name}
          </button>
        ))}
      </div>

      {/* CONTENT */}
      <div className="content-container">
        {/* Search / Genre Results */}
        {(query || selectedGenre !== 0) ? (
          <section className="section-panel search-results">
            <h2>Filtered Results</h2>
            <div className="game-list">
              {pageAll.length
                ? pageAll.map(g => (
                    <Link key={g.id} to={`/game/${g.id}`} className="game-card">
                      <img src={g.imageUrl} alt={g.title || g.Name} />
                      <h3>{g.title || g.Name}</h3>
                      {safePrice(g.price) > 0
                        ? <span className="discount-price">${safePrice(g.price).toFixed(2)}</span>
                        : <span className="original-price">Free</span>}
                    </Link>
                  ))
                : <p className="empty">No games found.</p>
              }
            </div>
            <div className="pagination">
              <button onClick={() => handlePageChange('genre', -1)} disabled={pageInfo.genre === 1}>
                &lt; Prev
              </button>
              <span>{pageInfo.genre}/{totalPages.genre}</span>
              <button onClick={() => handlePageChange('genre', +1)} disabled={pageInfo.genre === totalPages.genre}>
                Next &gt;
              </button>
            </div>
          </section>
        ) : (
          <>
            {/* FEATURED */}
            <section className="section-panel special-offers">
              <h2>Featured Game</h2>
              {fFeat[0] && (
                <div className="featured">
                  <Link to={`/game/${fFeat[0].id}`} className="game-card">
                    <img src={fFeat[0].imageUrl} alt={fFeat[0].title} />
                  </Link>
                  <div className="game-info">
                    <h3>{fFeat[0].title}</h3>
                    <p>{fFeat[0].description}</p>
                    <p>{(fFeat[0].salesCount || 0).toLocaleString()} lượt mua</p>
                  </div>
                </div>
              )}
            </section>

            <section className="section-panel special-offers">
        <h2>Special Deals</h2>
        <div className="game-list">
          {pageSpec.map(g => {
            const orig = safePrice(g.price);
            const disc = Number.isFinite(g.discount) ? g.discount : 0;
            const finalPrice = orig !== null ? (orig * (1 - disc / 100)) : null;

            return (
              <Link key={g.id} to={`/game/${g.id}`} className="game-card">
                {disc > 0 && <div className="discount">-{disc}%</div>}
                <img src={g.imageUrl} alt={g.title} />
                <h3>{g.title}</h3>

                {orig !== null ? (
                  <>
                    <span className="original-price">${orig.toFixed(2)}</span>
                    <span className="discount-price">${finalPrice.toFixed(2)}</span>
                  </>
                ) : (
                  <span className="free-label">Free</span>
                )}
              </Link>
            );
          })}
        </div>
              <div className="pagination">
                <button disabled={pageInfo.special <= 1} onClick={() => handlePageChange('special', -1)}>
                  &lt; Prev
                </button>
                <span>{pageInfo.special}/{totalPages.special}</span>
                <button disabled={pageInfo.special >= totalPages.special} onClick={() => handlePageChange('special', +1)}>
                  Next &gt;
                </button>
              </div>
            </section>

            {/* FREE GAMES */}
            <section className="section-panel free-games">
              <h2>Free Games</h2>
              <div className="game-list">

                {pageFree.map(g => (
                  <Link key={g.id} to={`/game/${g.id}`} className="game-card">
                    <img src={g.imageUrl} alt={g.title} />
                    <h3>{g.title}</h3>
                  </Link>
                ))}
              </div>
              <div className="pagination">
                <button disabled={pageInfo.free <= 1} onClick={() => handlePageChange('free', -1)}>
                  &lt; Prev
                </button>
                <span>{pageInfo.free}/{totalPages.free}</span>
                <button disabled={pageInfo.free >= totalPages.free} onClick={() => handlePageChange('free', +1)}>
                  Next &gt;
                </button>
              </div>
            </section>

            {/* BEST SELLERS */}
            <section className="section-panel best-seller">
              <h2>Best Sellers</h2>
              <div className="game-list">
                {pageBest.map(g => (
                  <Link key={g.id} to={`/game/${g.id}`} className="game-card">
                    {disc > 0 && <div className="discount">-{disc}%</div>}
                    <img src={g.imageUrl} alt={g.title} />
                    <h3>{g.title}</h3>
                    {safePrice(g.price) > 0
                        ? <span className="discount-price">${safePrice(g.price).toFixed(2)}</span>
                        : <span className="original-price">Free</span>}
                  </Link>
                ))}
              </div>
              <div className="pagination">
                <button disabled={pageInfo.best <= 1} onClick={() => handlePageChange('best', -1)}>
                  &lt; Prev
                </button>
                <span>{pageInfo.best}/{totalPages.best}</span>
                <button disabled={pageInfo.best >= totalPages.best} onClick={() => handlePageChange('best', +1)}>
                  Next &gt;
                </button>
              </div>
            </section>
          </>
        )}
      </div>

      {/* FOOTER */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>All trademarks are property of their respective owners.</p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul><li>About Valve</li><li>Jobs</li><li>Steamworks</li><li>Distribution</li><li>Support</li></ul>
            <ul><li>Privacy Policy</li><li>Legal</li><li>Subscriber Agreement</li><li>Refunds</li><li>Cookies</li></ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook" />
            <div className="social-icon close" />
          </div>
        </div>
      </footer>
    </div>
  );
}
