// AdminPanel.js

import React, { useState, useEffect } from "react";
import axios from "axios";

export default function AdminPanel() {
  // Tabs: "orders" hoặc "users"
  const [tab, setTab] = useState("orders");

  // State Orders
  const [orders, setOrders] = useState([]);
  const [orderForm, setOrderForm] = useState({});
  const [editingOrderId, setEditingOrderId] = useState(null);

  // State Users
  const [users, setUsers] = useState([]);
  const [userForm, setUserForm] = useState({});
  const [editingUserId, setEditingUserId] = useState(null);

  // Fetch orders/users khi mount
  useEffect(() => {
    fetchOrders();
    fetchUsers();
  }, []);

  // ================= ORDERS API =================

  const fetchOrders = async () => {
    try {
      const res = await axios.get("/api/orders");
      setOrders(res.data);
    } catch (err) {
      alert("Lỗi load orders!");
    }
  };

  const handleAddOrUpdateOrder = async (e) => {
    e.preventDefault();
    try {
      if (editingOrderId) {
        await axios.put(`/api/orders/${editingOrderId}`, orderForm);
      } else {
        await axios.post("/api/orders", orderForm);
      }
      setOrderForm({});
      setEditingOrderId(null);
      fetchOrders();
    } catch {
      alert("Lỗi thao tác order!");
    }
  };

  const handleEditOrder = (order) => {
    setOrderForm(order);
    setEditingOrderId(order.id || order._id);
  };

  const handleDeleteOrder = async (orderId) => {
    if (!window.confirm("Xóa order này?")) return;
    try {
      await axios.delete(`/api/orders/${orderId}`);
      fetchOrders();
    } catch {
      alert("Lỗi xóa order!");
    }
  };

  // ================= USERS API =================

  const fetchUsers = async () => {
    try {
      const res = await axios.get("/api/users");
      setUsers(res.data);
    } catch (err) {
      alert("Lỗi load users!");
    }
  };

  const handleAddOrUpdateUser = async (e) => {
    e.preventDefault();
    try {
      if (editingUserId) {
        await axios.put(`/api/users/${editingUserId}`, userForm);
      } else {
        await axios.post("/api/users", userForm);
      }
      setUserForm({});
      setEditingUserId(null);
      fetchUsers();
    } catch {
      alert("Lỗi thao tác user!");
    }
  };

  const handleEditUser = (user) => {
    setUserForm(user);
    setEditingUserId(user.id || user._id);
  };

  const handleDeleteUser = async (userId) => {
    if (!window.confirm("Xóa user này?")) return;
    try {
      await axios.delete(`/api/users/${userId}`);
      fetchUsers();
    } catch {
      alert("Lỗi xóa user!");
    }
  };

  // =================== RENDER UI ===================

  return (
    <div style={{ maxWidth: 1100, margin: "30px auto", background: "#181f2a", padding: 24, borderRadius: 16, color: "#fff" }}>
      <h1 style={{ marginBottom: 24 }}>Admin Panel</h1>
      <div style={{ marginBottom: 24 }}>
        <button onClick={() => setTab("orders")} style={{ marginRight: 10, padding: 8, background: tab === "orders" ? "#1a9fff" : "#333", color: "#fff", border: 0, borderRadius: 6 }}>Quản lý Orders</button>
        <button onClick={() => setTab("users")} style={{ padding: 8, background: tab === "users" ? "#1a9fff" : "#333", color: "#fff", border: 0, borderRadius: 6 }}>Quản lý Users</button>
      </div>
      {tab === "orders" ? (
        <section>
          <h2>Orders</h2>
          <form onSubmit={handleAddOrUpdateOrder} style={{ marginBottom: 18, background: "#222a38", padding: 12, borderRadius: 8 }}>
            {/* Tùy thuộc dữ liệu order, bổ sung field dưới đây */}
            <input
              placeholder="Order name"
              value={orderForm.name || ""}
              onChange={e => setOrderForm({ ...orderForm, name: e.target.value })}
              style={{ marginRight: 8, padding: 6 }}
            />
            <input
              placeholder="Order amount"
              value={orderForm.amount || ""}
              onChange={e => setOrderForm({ ...orderForm, amount: e.target.value })}
              style={{ marginRight: 8, padding: 6 }}
            />
            <button type="submit" style={{ padding: 6, background: "#1a9fff", color: "#fff", border: 0, borderRadius: 6 }}>
              {editingOrderId ? "Cập nhật" : "Thêm mới"}
            </button>
            {editingOrderId && (
              <button
                type="button"
                onClick={() => { setOrderForm({}); setEditingOrderId(null); }}
                style={{ marginLeft: 8, padding: 6, background: "#555", color: "#fff", border: 0, borderRadius: 6 }}
              >
                Hủy
              </button>
            )}
          </form>
          <table style={{ width: "100%", background: "#222a38", borderRadius: 8, marginBottom: 32 }}>
            <thead>
              <tr style={{ background: "#1a9fff" }}>
                <th style={{ padding: 8 }}>ID</th>
                <th style={{ padding: 8 }}>Tên</th>
                <th style={{ padding: 8 }}>Số lượng</th>
                <th style={{ padding: 8 }}>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((o) => (
                <tr key={o.id || o._id}>
                  <td style={{ padding: 8 }}>{o.id || o._id}</td>
                  <td style={{ padding: 8 }}>{o.name}</td>
                  <td style={{ padding: 8 }}>{o.amount}</td>
                  <td style={{ padding: 8 }}>
                    <button onClick={() => handleEditOrder(o)} style={{ marginRight: 8, padding: 6 }}>Sửa</button>
                    <button onClick={() => handleDeleteOrder(o.id || o._id)} style={{ padding: 6 }}>Xóa</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>
      ) : (
        <section>
          <h2>Users</h2>
          <form onSubmit={handleAddOrUpdateUser} style={{ marginBottom: 18, background: "#222a38", padding: 12, borderRadius: 8 }}>
            {/* Tùy thuộc dữ liệu user, bổ sung field dưới đây */}
            <input
              placeholder="Username"
              value={userForm.username || ""}
              onChange={e => setUserForm({ ...userForm, username: e.target.value })}
              style={{ marginRight: 8, padding: 6 }}
            />
            <input
              placeholder="Email"
              value={userForm.email || ""}
              onChange={e => setUserForm({ ...userForm, email: e.target.value })}
              style={{ marginRight: 8, padding: 6 }}
            />
            <button type="submit" style={{ padding: 6, background: "#1a9fff", color: "#fff", border: 0, borderRadius: 6 }}>
              {editingUserId ? "Cập nhật" : "Thêm mới"}
            </button>
            {editingUserId && (
              <button
                type="button"
                onClick={() => { setUserForm({}); setEditingUserId(null); }}
                style={{ marginLeft: 8, padding: 6, background: "#555", color: "#fff", border: 0, borderRadius: 6 }}
              >
                Hủy
              </button>
            )}
          </form>
          <table style={{ width: "100%", background: "#222a38", borderRadius: 8 }}>
            <thead>
              <tr style={{ background: "#1a9fff" }}>
                <th style={{ padding: 8 }}>ID</th>
                <th style={{ padding: 8 }}>Tên</th>
                <th style={{ padding: 8 }}>Email</th>
                <th style={{ padding: 8 }}>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {users.map((u) => (
                <tr key={u.id || u._id}>
                  <td style={{ padding: 8 }}>{u.id || u._id}</td>
                  <td style={{ padding: 8 }}>{u.username}</td>
                  <td style={{ padding: 8 }}>{u.email}</td>
                  <td style={{ padding: 8 }}>
                    <button onClick={() => handleEditUser(u)} style={{ marginRight: 8, padding: 6 }}>Sửa</button>
                    <button onClick={() => handleDeleteUser(u.id || u._id)} style={{ padding: 6 }}>Xóa</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>
      )}
    </div>
  );
}
