import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Style/Login.css';

// Cấu hình baseURL nếu cần
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function Login({ onLogin }) {
  const [isRegister, setIsRegister] = useState(false);
  const [form, setForm] = useState({
    name: '',
    email: '',
    password: '',
    accountNumberBank: ''
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const submitLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('/api/login', {
        email: form.email,
        password: form.password
      });
      onLogin(res.data.user);
      navigate('/store');
    } catch (err) {
      console.error('Login error:', err);
      if (err.response?.status === 401) {
        setError(err.response.data.message);
      } else {
        setError('Đã có lỗi xảy ra. Vui lòng thử lại.');
      }
    }
  };

  const submitRegister = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('/api/register', {
        name: form.name,
        email: form.email,
        password: form.password,
        accountNumberBank: form.accountNumberBank
      });
      if (res.status === 201) {
        alert('Đăng ký thành công! Vui lòng đăng nhập.');
        setIsRegister(false);
        setForm({ name: '', email: '', password: '', accountNumberBank: '' });
      }
    } catch (err) {
      console.error('Register error:', err);
      if (err.response?.status === 409) {
        setError(err.response.data.message);
      } else {
        setError('Đã có lỗi xảy ra. Vui lòng thử lại.');
      }
    }
  };

  return (
    <div className="auth-page">
      <form
        className="auth-form"
        onSubmit={isRegister ? submitRegister : submitLogin}
      >
        <h2>{isRegister ? 'Đăng ký' : 'Đăng nhập'}</h2>

        {isRegister && (
          <label>
            Họ và tên
            <input
              type="text"
              name="name"
              value={form.name}
              onChange={handleChange}
              required
            />
          </label>
        )}

        <label>
          Email
          <input
            type="email"
            name="email"
            value={form.email}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Mật khẩu
          <input
            type="password"
            name="password"
            value={form.password}
            onChange={handleChange}
            required
          />
        </label>

        {isRegister && (
          <label>
            Số tài khoản ngân hàng
            <input
              type="text"
              name="accountNumberBank"
              value={form.accountNumberBank}
              onChange={handleChange}
              required
            />
          </label>
        )}

        {error && <p className="error-message">{error}</p>}

        <button type="submit">
          {isRegister ? 'Đăng ký' : 'Đăng nhập'}
        </button>

        <p className="toggle-link">
          {isRegister ? 'Đã có tài khoản? ' : 'Chưa có tài khoản? '}
          <button
            type="button"
            className="btn-toggle"
            onClick={() => { setIsRegister(!isRegister); setError(''); }}
          >
            {isRegister ? 'Đăng nhập' : 'Đăng ký ngay'}
          </button>
        </p>
      </form>
    </div>
  );
}
