import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "./Style/GameDetail.css";

// axios baseURL (giống StorePage)
const API_BASE = process.env.REACT_APP_API_URL || "http://localhost:5000";
axios.defaults.baseURL = API_BASE;

const GameDetail = () => {
  const { key } = useParams();                // key có thể là số (id) hoặc slug ("elden-ring")
  const navigate = useNavigate();

  const [game, setGame]   = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchGame() {
      try {
        setLoading(true);
        // gửi nguyên key (slug hoặc id) lên backend
        const { data } = await axios.get(`/api/games/${encodeURIComponent(key)}`);
        setGame(data);
      } catch (err) {
        console.error("Error fetching game:", err);
        setError("Không tìm thấy trò chơi.");
      } finally {
        setLoading(false);
      }
    }
    fetchGame();
  }, [key]);

  if (loading) return <p className="loading">Đang tải…</p>;
  if (error)   return <p className="error">{error}</p>;
  if (!game)   return null;

  return (
    <div className="game-detail">
      {/* Header */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate("/store")}>
          <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
          </span>
          <div className="user-actions">
          <button className="download-btn" onClick={handleDiscoverClick}>
                Library
            </button>
            <span>Wishlist</span>
            <span className="cart-btn" onClick={() => navigate("/cart")} style={{ cursor: "pointer" }}>
              Cart
            </span>
            <span className="cart-btn" onClick={() => navigate("/profile")} style={{ cursor: "pointer" }}>
              Profile
            </span>
          </div>
        </div>
      </header>

      {/* Banner */}
      <div className="detail-banner">
        <img src={bannerUrl} alt={game.name} />
      </div>

      {/* Main Detail */}
      <main className="detail-main">
        <section className="detail-left">
          <h2>{game.name}</h2>
          <p className="description">{game.description}</p>
          <p><strong>Developer:</strong> {game.developer}</p>
          <p><strong>Publisher:</strong> {game.publisher}</p>
          <p><strong>Genre:</strong> {game.genre}</p>
          <div className="detail-buy">
            <div className="price">${game.price}</div>
            <button className="buy-button">Add to Cart</button>
          </div>
          <hr />
        </section>
        <aside className="detail-right">
          {game.screenshotUrl && (
            <img
              className="detail-screenshot"
              src={game.screenshotUrl}
              alt={`${game.name} screenshot`}
            />
          )}
        </aside>
      </main>

      {/* Reviews Section */}
      <div className="detail-extras">
        <h3>Reviews</h3>
        <hr />

        {/* Hiển thị danh sách review */}
        <div className="reviews-list">
          {reviews.length === 0
            ? <p>No reviews yet. Be the first!</p>
            : reviews.map((r, i) => (
              <div key={i} className="review-item">
                <strong>{r.rating} Star{r.rating>1?'s':''}</strong> &nbsp;
                <span className="review-date">({r.date})</span>
                <p>{r.text}</p>
                <hr />
              </div>
            ))
          }
        </div>

        {/* Form submit review */}
        <form className="submit-review" onSubmit={submitReview}>
          <h4>Submit Your Review</h4>
          <div className="form-group">
            <label>Rating</label>
            <select
              className="rating-select"
              value={rating}
              onChange={e => setRating(+e.target.value)}
            >
              {[5,4,3,2,1].map(n => (
                <option key={n} value={n}>
                  {n} Star{n>1 && 's'}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group" style={{ marginTop: '16px' }}>
            <label>Your Review</label>
            <textarea
              className="review-textarea"
              rows="4"
              placeholder="Write something…"
              value={reviewText}
              onChange={e => setReviewText(e.target.value)}
            />
          </div>
          <button
            type="submit"
            className="submit-btn"
            disabled={!reviewText.trim()}
          >
            Submit Review
          </button>
        </form>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>All trademarks are property of their respective owners in the US and other countries.</p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Steam Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Steam Subscriber Agreement</li>
              <li>Refunds</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
}
