import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import "./Style/Library.css";

// Thiết lập baseURL chung
axios.defaults.baseURL = process.env.REACT_APP_API_URL || "http://localhost:5000";

const Library = () => {
  const navigate = useNavigate();
  const { userId: paramUserId } = useParams();

  // Lấy userId từ session hoặc param URL
  const storedUser = sessionStorage.getItem("user");
  const parsedUser = storedUser ? JSON.parse(storedUser) : null;
  const currentUserId = parsedUser?.id;
  const userId = paramUserId || currentUserId;

  // State cho games, genres, search query và filter
  const [games, setGames] = useState([]);
  const [genres, setGenres] = useState([]);
  const [query, setQuery] = useState("");
  const [selectedGenre, setSelectedGenre] = useState(0);
  const [loading, setLoading] = useState(true);

  // Fetch cả library và genres khi component mount
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [resGames, resGenres] = await Promise.all([
          axios.get(`/api/users/${userId}/library`),
          axios.get("/api/genres"),
        ]);
        // tùy định dạng response bạn có thể điều chỉnh .data.items
        setGames(resGames.data.items || resGames.data || []);
        setGenres(resGenres.data || []);
      } catch (err) {
        console.error("Fetch error:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [userId]);

  // Hàm kiểm tra điều kiện lọc
  const appliesFilter = (game) => {
    const title = (game.title || game.Name || "").toLowerCase();
    const gid = game.genreId || game.GenreID || 0;
    return (
      title.includes(query.toLowerCase()) &&
      (selectedGenre === 0 || gid === selectedGenre)
    );
  };
  const filteredGames = games.filter(appliesFilter);

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="library-container">
      {/* Header */}
      <div className="library-header">
        <span className="steam-logo" onClick={() => navigate("/store")}>
          <img src="/logo.png" alt="Steam Logo" />
        </span>
        <div className="user-actions">
          <button className="btn-flat" onClick={() => navigate("/store")}>
            Store
          </button>
          <button className="btn-flat" onClick={() => navigate("/cart")}>
            Cart
          </button>
          <button className="btn-flat" onClick={() => navigate("/profile")}>
            Profile
          </button>
        </div>
      </div>

      {/* Search bar */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search games..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>

      {/* Genre filters */}
      <div className="genres-container">
        <button
          className={`genre-button${selectedGenre === 0 ? " active" : ""}`}
          onClick={() => setSelectedGenre(0)}
        >
          All
        </button>
        {genres.map((g) => (
          <button
            key={g.id}
            className={`genre-button${
              selectedGenre === g.id ? " active" : ""
            }`}
            onClick={() => setSelectedGenre(g.id)}
          >
            {g.name}
          </button>
        ))}
      </div>

      {/* Games list */}
      <div className="library-games">
        {filteredGames.length === 0 ? (
          <p>No games found.</p>
        ) : (
          filteredGames.map((game) => (
            <div key={game.id} className="library-game-card">
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title || game.Name}</h3>
              <button
                className="play-button"
                onClick={() => {
                  /* logic play game */
                }}
              >
                Play
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Library;
