import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Style/StorePage.css";

// Nếu dùng create-react-app, có thể cấu hình "proxy" để tránh hardcode hostname
const API_BASE = process.env.REACT_APP_API_URL || "";

const StorePage = ({ cartItems = [], setCartItems }) => {
  const navigate = useNavigate();
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch games from backend khi component mount & khi searchTerm thay đổi (có debounce nếu cần)
  useEffect(() => {
    const fetchGames = async () => {
      setLoading(true);
      setError(null);
      try {
        const response = await axios.get(`${API_BASE}/api/games`, {
          params: { search: searchTerm }
        });
        setGames(response.data);
      } catch (err) {
        console.error("Error fetching games:", err);
        setError("Không thể tải danh sách trò chơi. Vui lòng kiểm tra kết nối đến server.");
        setGames([]);
      } finally {
        setLoading(false);
      }
    };

    // Thêm debounce để tránh gọi API quá thường
    const delayDebounce = setTimeout(fetchGames, 300);
    return () => clearTimeout(delayDebounce);
  }, [searchTerm]);

  const handleGameClick = (key) => {
    navigate(`/game/${key}`);
  };

  return (
    <div className="store-container">
      <header className="store-header">
        <div className="logo" onClick={() => navigate("/")}>Valve Steam</div>
        <div className="nav-actions">
          <button onClick={() => navigate("/library")}>Library</button>
          <button onClick={() => navigate("/wishlist")}>Wishlist</button>
          <button onClick={() => navigate("/cart")}>Cart ({cartItems.length})</button>
        </div>
      </header>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Tìm kiếm trò chơi..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <div className="content">
        {loading ? (
          <p className="loading">Đang tải trò chơi...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : games.length > 0 ? (
          <div className="game-list">
            {games.map((game) => (
              <div
                key={game.key}
                className="game-card"
                onClick={() => handleGameClick(game.key)}
              >
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <p>{game.price}</p>
              </div>
            ))}
          </div>
        ) : (
          <p className="no-results">Không tìm thấy trò chơi nào.</p>
        )}
      </div>

      <footer className="store-footer">
        <p>&copy; {new Date().getFullYear()} Valve Corporation. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default StorePage;
