// src/StorePage.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Style/StorePage.css";

// Thiết lập axios baseURL
const API_BASE = process.env.REACT_APP_API_URL || "http://localhost:5000";
axios.defaults.baseURL = API_BASE;

const StorePage = ({ cartItems = [] }) => {
  const navigate = useNavigate();
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGames = async () => {
      setLoading(true);
      try {
        const response = await axios.get("/games");
        setGames(response.data);
      } catch (err) {
        setError(
          err.response
            ? `${err.response.status} ${err.response.statusText}`
            : "Server error"
        );
      } finally {
        setLoading(false);
      }
    };
    fetchGames();
  }, [searchTerm]);

  const featured = games[0] || null;
  const specialOffers = games.filter((g) => Number(g.discount) > 0);
  const freeGames = games.filter((g) => Number(g.price) === 0);
  const bestSellers = games.slice(0, 5);

  return (
    <div>
      {/* Header */}
      <header className="store-header">
        <div className="nav">
          <span>Valve Steam</span>
          <div className="user-actions">
            <button
              className="download-btn"
              onClick={() => navigate("/library")}
            >
              Library
            </button>
            <span onClick={() => navigate("/wishlist")}>Wishlist</span>
            <span onClick={() => navigate("/cart")}
            >
              Cart ({cartItems.length})
            </span>
          </div>
        </div>
      </header>

      {/* Search */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Tìm kiếm trò chơi..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {loading && <p className="loading">Đang tải...</p>}
      {error && <p className="error">Lỗi: {error}</p>}

      {/* Featured */}
      {featured && (
        <section className="featured-container">
          <h2>FEATURED</h2>
          <div className="featured">
            <div
              className="featured-item"
              onClick={() => navigate(`/game/${featured.id}`)}
            >
              <img src={featured.imageUrl} alt={featured.title} />
              <div className="game-info">
                <h3>{featured.title}</h3>
                <p>{featured.description}</p>
                {featured.discount && (
                  <span className="discount">{featured.discount}% OFF</span>
                )}
                <span className="price">
                  {featured.price > 0 ? `$${featured.price}` : "Free"}
                </span>
                <button className="buy-now">Buy Now</button>
              </div>
            </div>
          </div>
        </section>
      )}

      {/* Special Offers */}
      {specialOffers.length > 0 && (
        <section className="special-offers">
          <h2>Special Offers</h2>
          <div className="game-list">
            {specialOffers.map((game) => (
              <div
                key={game.id}
                className="game-card"
                onClick={() => navigate(`/game/${game.id}`)}
              >
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <span className="discount">-{game.discount}%</span>
                <span className="price">
                  {typeof game.price === "number" ? `$${game.price}` : game.price}
                </span>
                <button className="buy-now">Buy Now</button>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Free-to-Play Games */}
      {freeGames.length > 0 && (
        <section className="free-games">
          <h2>Free-to-Play Games</h2>
          <div className="game-list">
            {freeGames.map((game) => (
              <div
                key={game.id}
                className="game-card"
                onClick={() => navigate(`/game/${game.id}`)}
              >
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <span className="price">Free</span>
                <button className="play-now">Play Now</button>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Best Sellers */}
      {bestSellers.length > 0 && (
        <section className="best-seller">
          <h2>Best Sellers</h2>
          <div className="game-list">
            {bestSellers.map((game) => (
              <div
                key={game.id}
                className="game-card"
                onClick={() => navigate(`/game/${game.id}`)}
              >
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                {game.discount && (
                  <span className="discount">-{game.discount}%</span>
                )}
                <span className="price">
                  {typeof game.price === "number" ? `$${game.price}` : game.price}
                </span>
                <button className="buy-now">Buy Now</button>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Logo" className="footer-logo" />
            <p>© 2025 Valve Corporation</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About</li>
              <li>Support</li>
              <li>Legal</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook" />
            <div className="social-icon close" />
          </div>
        </div>
      </footer>
    </div>
  );
};

export default StorePage;
