import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Style/StorePage.css";

// Thiết lập axios baseURL
const API_BASE = process.env.REACT_APP_API_URL || "http://localhost:5000";
axios.defaults.baseURL = API_BASE;

const StorePage = ({ cartItems = [] }) => {
  const navigate = useNavigate();
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGames = async () => {
      setLoading(true);
      setError(null);
      try {
        const res = await axios.get("/api/games", { params: { search: searchTerm } });
        setGames(res.data);
      } catch (err) {
        console.error("Fetch games error:", err);
        setError(err.response
          ? `${err.response.status} ${err.response.statusText}`
          : "Không thể kết nối server");
      } finally {
        setLoading(false);
      }
    };
    const timer = setTimeout(fetchGames, 300);
    return () => clearTimeout(timer);
  }, [searchTerm]);

  // Xác định các danh sách
  const featured = games.length > 0 ? games[0] : null;
  const specialOffers = games.filter(g => g.discount && g.discount > 0);
  const freeGames = games.filter(g => {
    const priceNum = typeof g.price === 'number'
      ? g.price
      : parseFloat(String(g.price).replace(/[^0-9.]/g, ''));
    return priceNum === 0;
  });
  const bestSellers = games.slice(0, 5);

  return (
    <div className="store-page">
      <header className="store-header">
        <div className="nav">
          <span>Valve Steam</span>
          <div className="user-actions">
            <button className="download-btn" onClick={() => navigate("/library")}>Library</button>
            <span onClick={() => navigate("/wishlist")} style={{ cursor: 'pointer' }}>Wishlist</span>
            <span onClick={() => navigate("/cart")} style={{ cursor: 'pointer' }}>Cart ({cartItems.length})</span>
          </div>
        </div>
      </header>

      {/* Banner */}
      <div className="banner">
        <img src="/summer_sale.jpg" alt="Summer Sale Banner" />
      </div>

      {/* Search */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Tìm kiếm trò chơi..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Loading / Error */}
      {loading && <p className="loading">Đang tải trò chơi...</p>}
      {error && <p className="error">Lỗi: {error}</p>}

      {/* Featured Game */}
      {featured && (
        <section className="featured-container">
          <h2>Featured Game</h2>
          <div className="featured">
            <div className="featured-item" onClick={() => navigate(`/game/${featured.key}`)}>
              <img src={featured.imageUrl} alt={featured.title} />
              <div className="game-info">
                <h3 className="featured-title">{featured.title}</h3>
                <div className="featured-pricing">
                  <span className="price">
                    {typeof featured.price === 'number' ? `$${featured.price}` : featured.price}
                  </span>
                  {featured.discount > 0 && (
                    <span className="discount">-{featured.discount}%</span>
                  )}
                </div>
                <p>{featured.description}</p>
                <button className="play-now">Play Now</button>
              </div>
            </div>
          </div>
        </section>
      )}

      {/* Special Offers */}
      {specialOffers.length > 0 && (
        <section className="special-offers">
          <h2>Special Offers</h2>
          <div className="game-list">
            {specialOffers.map(game => (
              <div key={game.key} className="game-card" onClick={() => navigate(`/game/${game.key}`)}>
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <div className="card-pricing">
                  <span className="discount">-{game.discount}%</span>
                  <span className="price">
                    {typeof game.price === 'number' ? `$${game.price}` : game.price}
                  </span>
                </div>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Free-to-Play Games */}
      {freeGames.length > 0 && (
        <section className="free-games">
          <h2>Free-to-Play Games</h2>
          <div className="game-list">
            {freeGames.map(game => (
              <div key={game.key} className="game-card" onClick={() => navigate(`/game/${game.key}`)}>
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <span className="price">Free</span>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Best Sellers */}
      {bestSellers.length > 0 && (
        <section className="best-seller">
          <h2>Best Sellers</h2>
          <div className="game-list">
            {bestSellers.map(game => (
              <div key={game.key} className="game-card" onClick={() => navigate(`/game/${game.key}`)}>
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <div className="card-pricing">
                  {game.discount > 0 && <span className="discount">-{game.discount}%</span>}
                  <span className="price">
                    {typeof game.price === 'number' ? `$${game.price}` : game.price}
                  </span>
                </div>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" className="footer-logo" alt="Logo" />
            <p>© {new Date().getFullYear()} Valve Corporation. All rights reserved.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>Về chúng tôi</li>
              <li>Hỗ trợ</li>
              <li>Liên hệ</li>
            </ul>
            <ul>
              <li>Quyền riêng tư</li>
              <li>Điều khoản</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
