import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from "react-router-dom";
import Login from "./Login";
import StorePage from "./StorePage";
import GameDetail from "./GameDetail";
import Cart from "./Cart";
import Library from "./Library";
import Profile from "./Profile";
import AddGame from "./AddGame";
import Checkout from './Checkout';

function App() {
  const [cartItems, setCartItems] = useState([]);
  const [user, setUser] = useState(null);  // lưu thông tin user khi login

  return (
      <Routes>
        {/* Login */}
        <Route
          path="/login"
          element={<Login onLogin={u => setUser(u)} />}
        />

        {/* Nếu chưa login, redirect về /login */}
        <Route
          path="/"
          element={
            user 
              ? <StorePage cartItems={cartItems} setCartItems={setCartItems} user={user} /> 
              : <Navigate to="/login" replace />
          }
        />
        <Route
          path="/store"
          element={
            user 
              ? <StorePage cartItems={cartItems} setCartItems={setCartItems} user={user} /> 
              : <Navigate to="/login" replace />
          }
        />
        <Route
          path="/game/:gameId"
          element={user ? <GameDetail user={user} /> : <Navigate to="/login" replace />}
        />
        <Route
          path="/cart"
          element={user 
            ? <Cart cartItems={cartItems} setCartItems={setCartItems} user={user} /> 
            : <Navigate to="/login" replace />
          }
        />
        <Route
          path="/library"
          element={user 
            ? <Library user={user} /> 
            : <Navigate to="/login" replace />
          }
        />
        <Route
          path="/profile"
          element={user 
            ? <Profile user={user} /> 
            : <Navigate to="/login" replace />
          }
        />
        <Route
          path="/addgame"
          element={user 
            ? <AddGame user={user} /> 
            : <Navigate to="/login" replace />
          }
        />
        <Route
          path="/checkout"
          element={user 
            ? <Checkout setCartItems={setCartItems} user={user} /> 
            : <Navigate to="/login" replace />
          }
        />

        {/* fallback khi vào đường dẫn không tồn tại */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
  );
}

export default App;
