// src/GameDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/GameDetail.css';

// Thiết lập Base URL cho Axios
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const placeholderBanner = '/images/placeholder-banner.jpg';

export default function GameDetail() {
  const navigate = useNavigate();
  const { key } = useParams();
  const [game, setGame] = useState(null);
  const [notFound, set404] = useState(false);
  const [rating, setRating] = useState(5);
  const [reviewText, setReviewText] = useState('');
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    if (!key) return;
    setGame(null);
    set404(false);
    axios
      .get(`/api/games/${encodeURIComponent(key)}`)
      .then(res => setGame(res.data))
      .catch(() => set404(true));
  }, [key]);

  const submitReview = e => {
    e.preventDefault();
    const newRev = { rating, text: reviewText, date: new Date().toLocaleDateString() };
    setReviews([newRev, ...reviews]);
    setReviewText('');
    setRating(5);
  };

  if (notFound) return <div className="detail-not-found">Game not found</div>;
  if (!game) return <div className="loading">Loading...</div>;

  const bannerUrl = game.imageUrl || placeholderBanner;

  return (
    <div className="game-detail">
      {/* Header */}
      <header className="detail-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>Steam</span>
          <div className="user-actions">
            <button className="download-btn" onClick={() => navigate('/store')}>Library</button>
            <span onClick={() => navigate('/profile')} style={{ cursor: 'pointer' }}>Profile</span>
          </div>
        </div>
      </header>

      {/* Banner */}
      <div className="detail-banner">
        <img src={bannerUrl} alt={game.title} />
      </div>

      {/* Main Detail Section */}
      <div className="detail-main">
        <div className="detail-left">
          <h2>{game.title}</h2>
          <div className="description">{game.description}</div>
          <div className="detail-buy">
            {Number(game.discount) > 0 && <span className="discount">-{game.discount}%</span>}
            <span className="price">
              {Number(game.price) === 0 ? 'Free' : `$${game.price}`}
            </span>
            <button className="buy-button">
              {Number(game.price) === 0 ? 'Play Now' : 'Buy Now'}
            </button>
          </div>
          <hr />
        </div>

        <div className="detail-right">
          {game.screenshotUrl && (
            <img
              src={game.screenshotUrl}
              alt={`${game.title} Screenshot`}
              className="detail-screenshot"
            />
          )}
          {game.systemRequirements && (
            <div className="system-req">
              <h3>System Requirements</h3>
              <p>{game.systemRequirements}</p>
            </div>
          )}
        </div>
      </div>

      {/* Reviews & Extras */}
      <div className="detail-extras">
        <h3>Reviews</h3>
        <hr />
        {reviews.length === 0 ? (
          <p>No reviews yet. Be the first!</p>
        ) : (
          reviews.map((r, i) => (
            <div key={i} className="review-item">
              <strong>{r.rating} Star{r.rating > 1 ? 's' : ''}</strong>
              <p>{r.text}</p>
              <small>{r.date}</small>
            </div>
          ))
        )}
        <form className="submit-review" onSubmit={submitReview}>
          <div className="form-group">
            <label>Rating:</label>
            <select
              className="rating-select"
              value={rating}
              onChange={e => setRating(Number(e.target.value))}
            >
              {[1, 2, 3, 4, 5].map(n => (
                <option key={n} value={n}>{n}</option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Your Review:</label>
            <textarea
              className="review-textarea"
              placeholder="Write your review..."
              value={reviewText}
              onChange={e => setReviewText(e.target.value)}
            />
          </div>
          <button type="submit" className="submit-btn">Submit</button>
        </form>
      </div>

      {/* Footer */}
      <footer className="detail-footer">
        <div>© 2025 Steam</div>
      </footer>
    </div>
  );
}
