import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useuseParams, Link } from 'react-router-dom';
import './Style/GameDetail.css';

const GameDetail = ({ userId }) => {
  const { gameId } = useParams();
  const initialId = parseInt(gameId, 10);

  const [game, setGame] = useState(undefined);
  const [numericId, setNumericId] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [reviewText, setReviewText] = useState('');

  // Map shortcuts/slugs to full slug
  const slugExceptions = {
    cs2: 'counter-strike-2',
    tf2: 'team-fortress-2',
    dota: 'dota-2',
    destiny: 'destiny-2',
    halo: 'halo-infinite',
    beyond: 'beyond-two-souls',
    iratus: 'iratus-lord-of-the-dead',
    'last-of-us': 'the-last-of-us-part-1',
  };

  const toSlug = (str) =>
    str.toLowerCase().replace(/\s+/g, '-').replace(/[^\w-]+/g, '');

  useEffect(() => {
    const fetchGame = async () => {
      try {
        let fetched;
        if (!isNaN(initialId)) {
          // numeric ID
          const res = await axios.get(`http://localhost:5000/api/games/${initialId}`);
          fetched = res.data;
        } else {
          // slug handling with exceptions + variants
          const target = slugExceptions[gameId] || gameId;
          const resList = await axios.get('http://localhost:5000/api/games');
          const allGames = resList.data;
          fetched = allGames.find((g) => {
            const base = toSlug(g.name);
            // direct match full slug
            if (base === target) return true;
            // match variant for human readability
            const parts = base.split('-');
            if (parts[0] === target) return true;
            if (parts.slice(0, 2).join('-') === target) return true;
            // drop 'the-'
            if (base.startsWith('the-') && base.slice(4) === target) return true;
            // roman 'i' to '1'
            if (base.endsWith('-i') && (base.slice(0, -1) + '1') === target) return true;
            return false;
          });
        }
        setGame(fetched || null);
        if (fetched) setNumericId(fetched.id);
      } catch (e) {
        console.error('Error fetching game:', e);
        setGame(null);
      }
    };
    fetchGame();
  }, [gameId, initialId]);

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    if (numericId == null) return;
    try {
      const res = await axios.post(
        `http://localhost:5000/api/games/${numericId}/reviews`,
        { userId, rating: Number(rating), comment: reviewText }
      );
      setReviews((prev) => [...prev, res.data]);
      setRating(5);
      setReviewText('');
    } catch (e) {
      console.error('Error submitting review:', e);
    }
  };

  if (game === undefined) {
    return <div className="loading">Loading...</div>;
  }
  if (game === null) {
    return <div className="detail-not-found">Game not found</div>;
  }

  const navigate = useNavigate();
  const handleDiscoverClick = () => {
    navigate("/library"); // Điều hướng đến Library
  };

  return (
    <div className="game-detail">
       <header className="store-header">
        <div className="nav">
        <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
          <div className="user-actions">
          <button className="download-btn" onClick={handleDiscoverClick}>
                Library
            </button>
            <span>Wishlist</span>
            <span className="cart-btn" onClick={() => navigate("/cart")} style={{ cursor: "pointer" }}>
              Cart
            </span>
            <span className="cart-btn" onClick={() => navigate("/profile")} style={{ cursor: "pointer" }}>
              Profile
            </span>
          </div>
        </div>
      </header>

      {game.imageUrl && (
        <div className="detail-banner">
          <img src={game.imageUrl} alt={game.name} />
        </div>
      )}

      <div className="detail-main">
        <div className="detail-left">
          <h2>{game.name}</h2>
          <p className="description">{game.description}</p>
          <div className="detail-buy">
            <div className="price">${game.price}</div>
            <button className="buy-button">Buy Now</button>
          </div>
          <hr />
        </div>
        <div className="detail-right">
          {game.imageUrl && (
            <img
              className="detail-screenshot"
              src={game.imageUrl}
              alt={`${game.name} screenshot`}
            />
          )}
          {game.systemReq && (
            <div className="system-req">
              <h3>System Requirements</h3>
              <p>{game.systemReq}</p>
            </div>
          )}
        </div>
      </div>

      <div className="detail-extras">
        <h3>Reviews</h3>
        {reviews.length > 0 ? (
          reviews.map((r) => (
            <div key={r.id} className="review-item">
              <p>Rating: {r.rating} stars</p>
              <p>{r.comment}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet.</p>
        )}
        <hr />
      </div>

      <div className="submit-review">
        <h4>Submit your review</h4>
        <form className="review-form" onSubmit={handleSubmitReview}>
          <div className="form-group">
            <label htmlFor="rating">Rating:</label>
            <select
              id="rating"
              className="rating-select"
              value={rating}
              onChange={(e) => setRating(e.target.value)}
            >
              {[1, 2, 3, 4, 5].map((star) => (
                <option key={star} value={star}>
                  {star} Star{star > 1 ? 's' : ''}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="comment">Review:</label>
            <textarea
              id="comment"
