import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/Cart.css';

const Cart = ({ cartItems = [], setCartItems }) => {
  const navigate = useNavigate();
  const userId = 1; // Thay bằng userId thật từ auth

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await axios.get(`/api/cart/${userId}`);
        setCartItems(res.data.items || []);
      } catch (err) {
        console.error('Lấy giỏ hàng lỗi:', err);
      }
    };
    fetchCart();
  }, [setCartItems, userId]);

  const handleRemoveItem = (cartItemId) => {
    // Nếu cần, gọi API DELETE tại đây
    setCartItems(prev => prev.filter(item => item.cartItemId !== cartItemId));
  };

  const totalPrice = cartItems.reduce((sum, item) => {
    const rawPrice = typeof item.Price === 'number'
      ? item.Price
      : parseFloat(item.Price) || 0;
    const finalPrice = item.Discount > 0
      ? rawPrice * (1 - item.Discount / 100)
      : rawPrice;
    return sum + finalPrice;
  }, 0);

  return (
    <div className="cart-page">
      <div className="detail-header">
        <h1>Your Cart</h1>
        <button onClick={() => navigate(-1)}>Back</button>
      </div>
      <div className="main-container">
        <div className="cart-container">
          {cartItems.length === 0 ? (
            <p>Giỏ hàng trống</p>
          ) : (
            <>
              <ul className="cart-list">
                {cartItems.map(item => {
                  const rawPrice = typeof item.Price === 'number'
                    ? item.Price
                    : parseFloat(item.Price) || 0;
                  const finalPrice = item.Discount > 0
                    ? rawPrice * (1 - item.Discount / 100)
                    : rawPrice;
                  return (
                    <li className="cart-item" key={item.cartItemId}>
                      <span className="cart-item-name">{item.Name}</span>
                      <span className="cart-item-price">
                        ${finalPrice.toFixed(2)}
                      </span>
                      <button
                        className="remove-btn"
                        onClick={() => handleRemoveItem(item.cartItemId)}
                      >
                        Xóa
                      </button>
                    </li>
                  );
                })}
              </ul>
              <div className="cart-total">
                Tổng tiền: ${totalPrice.toFixed(2)}
              </div>
            </>
          )}
        </div>
      </div>
      <footer className="footer">
        © 2025 My Game Store
      </footer>
    </div>
  );
};

export default Cart;
