import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Style/StorePage.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function StorePage({ cartItems, setCartItems }) {
  const [featured, setFeatured] = useState([]);
  const [special, setSpecial] = useState([]);
  const [freeGames, setFreeGames] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [games, setGames] = useState([]);
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState(0);
  const [query, setQuery] = useState('');
  const navigate = useNavigate();
  const PAGE_SIZE = 4;
  const [pageInfo, setPageInfo] = useState({ special: 1, free: 1, best: 1 });
  const [totalPages, setTotalPages] = useState({ special: 1, free: 1, best: 1 });

  // Fetch genres on mount with fallback to any array in response
  useEffect(() => {
    axios.get('/api/genres')
      .then(res => {
        const raw = res.data;
        console.log('Raw genres response:', raw);
        let arr = [];
        if (Array.isArray(raw)) {
          arr = raw;
        } else if (Array.isArray(raw.items)) {
          arr = raw.items;
        } else {
          const firstArray = Object.values(raw).find(v => Array.isArray(v));
          arr = firstArray || [];
        }
        console.log('Detected genres array:', arr);
        const list = arr.map(g => ({ id: g.GenreID || g.id, name: g.Name || g.name }));
        console.log('Mapped genres list:', list);
        setGenres([{ id: 0, name: 'All' }, ...list]);
      })
      .catch(err => console.error('Error fetching genres:', err));
  }, []);

  useEffect(() => {
    async function fetchData() {
      try {
        const params = { page: 1, pageSize: PAGE_SIZE };
        const [resFeat, resSpec, resFree, resBest, resAll] = await Promise.all([
          axios.get('/api/games', { params }),
          axios.get('/api/games', { params: { ...params, type: 'special' } }),
          axios.get('/api/games', { params: { ...params, type: 'free' } }),
          axios.get('/api/games', { params: { ...params, type: 'best' } }),
          axios.get('/api/games', { params: { page: 1, pageSize: 1000 } }),
        ]);
        setFeatured(resFeat.data.items);
        setSpecial(resSpec.data.items);
        setFreeGames(resFree.data.items);
        setBestSellers(resBest.data.items);
        setTotalPages({
          special: Math.ceil(resSpec.data.total / PAGE_SIZE),
          free: Math.ceil(resFree.data.total / PAGE_SIZE),
          best: Math.ceil(resBest.data.total / PAGE_SIZE),
        });
        setGames(resAll.data.items);
      } catch (err) {
        console.error('Error fetching games:', err);
      }
    }
    fetchData();
  }, []);

  const handlePageChange = (category, delta) => {
    setPageInfo(prev => {
      const next = prev[category] + delta;
      return { ...prev, [category]: Math.max(1, Math.min(next, totalPages[category])) };
    });
  };

  const handleDiscoverClick = () => navigate('/library');

  function safePrice(val) {
    if (val == null) return null;
    const num = Number(String(val).replace(/,/g, ''));
    return Number.isFinite(num) ? num : null;
  }

  // apply search + genre filter
  const appliesFilter = game => {
    const matchesQuery = game.title.toLowerCase().includes(query.toLowerCase());
    const matchesGenre = selectedGenre === 0 || game.genreId === selectedGenre;
    return matchesQuery && matchesGenre;
  };

  // filtered lists per section
  const filteredAll = games.filter(appliesFilter);
  const filteredSpecial = special.filter(appliesFilter);
  const filteredFree = freeGames.filter(appliesFilter);
  const filteredBest = bestSellers.filter(appliesFilter);
  const filteredFeatured = featured.filter(appliesFilter);

  return (
    <div className="store-page">
      {/* Header */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>
            <img src="/logo.png" alt="Logo" className="footer-logo" />
          </span>
          <div className="user-actions">
            <button className="download-btn" onClick={handleDiscoverClick}>
              Library
            </button>
            <span className="cart-btn" onClick={() => navigate('/cart')}>
              Cart
            </span>
            <span className="cart-btn" onClick={() => navigate('/profile')}>
              Profile
            </span>
          </div>
        </div>
      </header>

      {/* Banner */}
      <div className="banner">
        <img
          src="/images/featured-banner.jpg"
          alt="Featured Banner"
          className="banner-img"
        />
      </div>

      {/* Search & Genre Filter */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search games..."
          value={query}
          onChange={e => setQuery(e.target.value)}
        />
      </div>
      <div className="genres-container">
        {genres.map(g => (
          <button
            key={g.id}
            className={selectedGenre === g.id ? 'genre-button active' : 'genre-button'}
            onClick={() => setSelectedGenre(g.id)}
          >
            {g.name}
          </button>
        ))}
      </div>

      <div className="content-container">
        {query || selectedGenre !== 0 ? (
          <section className="search-results">
            <h2>Filtered Results</h2>
            <div className="game-list">
              {filteredAll.length ? (
                filteredAll.map(game => (
                  <Link key={game.id} to={`/game/${game.id}`} className="game-card">
                    <img src={game.imageUrl} alt={game.title} />
                    <h3>{game.title}</h3>
                    {safePrice(game.price) > 0 ? (
                      <span>${safePrice(game.price).toFixed(2)}</span>
                    ) : (
                      <span>Free</span>
                    )}
                  </Link>
                ))
              ) : (
                <p>No games found.</p>
              )}
            </div>
          </section>
        ) : (
          <>
            {/* Featured Game */}
            <section className="special-offers">
              <h2>Featured Game</h2>
              {filteredFeatured[0] && (
                <div className="featured">
                  <Link to={`/game/${filteredFeatured[0].id}`} className="game-card">
                    <img src={filteredFeatured[0].imageUrl} alt={filteredFeatured[0].title} />
                  </Link>
                  <div className="game-info">
                    <h3>{filteredFeatured[0].title}</h3>
                    <p>{filteredFeatured[0].description}</p>
                    <p>{(filteredFeatured[0].salesCount || 0).toLocaleString()} lượt mua</p>
                  </div>
                </div>
              )}
            </section>

            {/* Sections: Special, Free, Best */}
            {[
              { title: 'Special Deals', data: filteredSpecial, category: 'special' },
              { title: 'Free Games', data: filteredFree, category: 'free' },
              { title: 'Best Sellers', data: filteredBest, category: 'best' }
            ].map(section => (
              <section key={section.category} className={`${section.category}-section`}>
                <h2>{section.title}</h2>
                <div className="game-list">
                  {section.data.map(game => (
                    <Link key={game.id} to={`/game/${game.id}`} className="game-card">
                      {game.discount > 0 && <div className="discount">-{game.discount}%</div>}
                      <img src={game.imageUrl} alt={game.title} />
                      <h3>{game.title}</h3>
                      {safePrice(game.price) > 0 && (
                        <span className="original-price">${safePrice(game.price).toFixed(2)}</span>
                      )}
                      <span className="discounted-price">
                        ${( (safePrice(game.price)||0) * (1 - (game.discount||0)/100) ).toFixed(2)}
                      </span>
                      <p>{(game.salesCount||0).toLocaleString()} lượt mua</p>
                    </Link>
                  ))}
                </div>
                <div className="pagination">
                  <button onClick={() => handlePageChange(section.category, -1)} disabled={pageInfo[section.category] === 1}>&lt; Prev</button>
                  <span>{pageInfo[section.category]}/{totalPages[section.category]}</span>
                  <button onClick={() => handlePageChange(section.category, 1)} disabled={pageInfo[section.category] === totalPages[section.category]}>Next &gt;</button>
                </div>
              </section>
            ))}
          </>
        )}
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>All trademarks are property of their respective owners in the US and other countries.</p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul><li>About Valve</li><li>Jobs</li><li>Steamworks</li><li>Steam Distribution</li><li>Support</li></ul>
            <ul><li>Privacy Policy</li><li>Legal</li><li>Steam Subscriber Agreement</li><li>Refunds</li><li>Cookies</li></ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
}
