import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/GameDetail.css';

export default function GameDetail({ user, cartItems, setCartItems }) {
  const { gameId } = useParams();
  const navigate = useNavigate();

  const [game, setGame] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [newRating, setNewRating] = useState(0);
  const [newComment, setNewComment] = useState('');
  const [inCart, setInCart] = useState(false);

  // Determine base for image URLs
  const baseURL = axios.defaults.baseURL;

  const buildImageUrl = path => {
    if (!path) return '';
    return path.startsWith('http') ? path : `${baseURL}${path.startsWith('/') ? '' : '/'}${path}`;
  };

  // Fetch game details
  useEffect(() => {
    async function fetchGame() {
      try {
        const res = await axios.get(`/api/games/${gameId}`);
        setGame(res.data);
      } catch (err) {
        console.error('Error fetching game:', err);
      }
    }
    fetchGame();
  }, [gameId]);

  // Fetch reviews
  useEffect(() => {
    async function fetchReviews() {
      try {
        const res = await axios.get(`/api/games/${gameId}/reviews`);
        setReviews(res.data.reviews || []);
      } catch (err) {
        console.error('Error fetching reviews:', err);
      }
    }
    fetchReviews();
  }, [gameId]);

  // Initialize inCart based on cartItems prop
  useEffect(() => {
    const exists = cartItems.some(item => +item.gameId === +gameId);
    setInCart(exists);
  }, [cartItems, gameId]);

  // Add to cart handler
  const handleAddToCart = async () => {
    console.log('Attempting to add to cart:', gameId);
    if (!user?.id) {
      alert('Bạn cần đăng nhập để thêm vào giỏ hàng');
      return;
    }
    if (inCart) {
      alert('Đã có trong giỏ hàng');
      return;
    }

    try {
      const res = await axios.post('/api/cart/add', {
        userId: user.id,
        gameId: +gameId,
      });
      console.log('Add to cart response:', res);
      if (res.status === 201) {
        setInCart(true);
        setCartItems(prev => [...prev, { gameId: +gameId }]);
        navigate('/cart');
      }
    } catch (err) {
      console.error('Add to Cart Error:', err);
      alert(err.response?.data.message || 'Lỗi thêm vào giỏ hàng');
    }
  };

  // Submit new review
  const handleReviewSubmit = async () => {
    if (!newRating || !newComment.trim()) return;
    try {
      await axios.post(`/api/games/${gameId}/reviews`, {
        user_id: user.id,
        star: newRating,
        comment: newComment.trim(),
      });
      const res = await axios.get(`/api/games/${gameId}/reviews`);
      setReviews(res.data.reviews || []);
      setNewRating(0);
      setNewComment('');
    } catch (err) {
      console.error('Submit review error:', err);
      alert(err.response?.data.error || 'Lỗi gửi đánh giá');
    }
  };

  if (!game) {
    return (
      <div className="game-detail">
        <div className="detail-main">
          <p>Loading game details...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="game-detail">
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>
            <img src="/logo.png" alt="Steam Logo" />
          </span>
          <div className="user-actions">
            <button type="button" className="btn-flat" onClick={() => navigate('/library')}>Library</button>
            <button type="button" className="btn-flat" onClick={() => navigate('/cart')}>Cart ({cartItems.length})</button>
            <button type="button" className="btn-flat" onClick={() => navigate('/profile')}>Profile</button>
          </div>
        </div>
      </header>

      <section className="detail-banner">
        <img src={buildImageUrl(game.imageUrl)} alt={game.title} />
      </section>

      <div className="detail-main">
        <div className="detail-left">
          <h2>{game.title}</h2>
          <p className="description">{game.description}</p>
          <div className="detail-buy">
            <div className="price">{game.price} VND</div>
            <button
              type="button"
              className="buy-button"
              onClick={handleAddToCart}
              disabled={inCart}
            >
              {inCart ? 'In Cart' : 'Add to Cart'}
            </button>
          </div>
          <hr />
          <div className="reviews-list">
            {reviews.length > 0 ? (
              reviews.map(r => (
                <div key={r.id} className="review-item">
                  <strong>User: {r.user_id}</strong>
                  <p>{r.comment}</p>
                  <small>Rating: {r.star} ★</small>
                </div>
              ))
            ) : (
              <p>No reviews yet.</p>
            )}
          </div>
        </div>

        <div className="detail-right">
          <img
            className="detail-screenshot"
            src={buildImageUrl(game.screenshotUrl)}
            alt={game.title}
          />
          <div className="system-req">
            <h3>System Requirements</h3>
            <ul>
              {game.systemRequirements?.map((req, idx) => (
                <li key={idx}>{req}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>

      <section className="detail-extras">
        <h3>About this game</h3>
        <hr />
        <p>{game.extras}</p>
      </section>

      <section className="submit-review">
        <h4>Write a Review</h4>
        <div className="form-group">
          <label htmlFor="rating">Rating:</label>
          <select
            id="rating"
            className="rating-select"
            value={newRating}
            onChange={e => setNewRating(+e.target.value)}
          >
            <option value={0}>Select</option>
            {[1, 2, 3, 4, 5].map(n => (
              <option key={n} value={n}>{n}</option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="comment">Comment:</label>
          <textarea
            id="comment"
            className="review-textarea"
            placeholder="Write your review..."
            value={newComment}
            onChange={e => setNewComment(e.target.value)}
          />
        </div>
        <button type="button" className="submit-btn" onClick={handleReviewSubmit}>
          Submit Review
        </button>
      </section>

      <footer className="detail-footer">
        <div className="footer-content">
          <ul>
            <li>Support</li>
            <li>About</li>
            <li>Contact</li>
          </ul>
        </div>
      </footer>
    </div>
  );
}
