import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Style/GameDetail.css';

export default function GameDetail({ user, cartItems, setCartItems }) {
  const { gameId } = useParams();
  const navigate = useNavigate();

  const [game, setGame] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [suggestedGames, setSuggestedGames] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const reviewsPerPage = 5;
  const [newRating, setNewRating] = useState(0);
  const [newComment, setNewComment] = useState('');
  const [inCart, setInCart] = useState(false);

  const baseURL = axios.defaults.baseURL;
  const buildImageUrl = path => {
    if (!path) return '';
    return path.startsWith('http') ? path : `${baseURL}${path.startsWith('/') ? '' : '/'}${path}`;
  };

  // Fetch game details
  useEffect(() => {
    async function fetchGame() {
      try {
        const res = await axios.get(`/api/games/${gameId}`);
        setGame(res.data);
        setInCart(cartItems.some(item => +item.gameId === +gameId));
      } catch (err) {
        console.error('Error fetching game details:', err);
      }
    }
    fetchGame();
  }, [gameId, cartItems]);

  // Fetch reviews
  useEffect(() => {
    async function fetchReviews() {
      try {
        const res = await axios.get(`/api/games/${gameId}/reviews`);
        setReviews(res.data.reviews || []);
      } catch (err) {
        console.error('Error fetching reviews:', err);
      }
    }
    fetchReviews();
  }, [gameId]);

  // Fetch suggested games by genre
  useEffect(() => {
    // Support both camelCase and PascalCase from API
    const genreId = game?.genreId ?? game?.GenreID;
    if (!genreId) return;

    async function fetchSuggestions() {
      try {
        // Fetch games list
        const res = await axios.get('/api/games?page=1&pageSize=100');
        const allGames = Array.isArray(res.data) ? res.data : res.data.items || [];
        // Filter by genreId and exclude current game
        const filtered = allGames
          .filter(g => +g.genreId === +genreId && +g.id !== +game.id)
          .slice(0, 5);
        setSuggestedGames(filtered);
      } catch (err) {
        console.error('Error fetching suggestions:', err);
      }
    }
    fetchSuggestions();
  }, [game]);

  const handleAddToCart = async () => {
    if (!user?.id) return alert('Bạn cần đăng nhập để thêm vào giỏ hàng');
    if (inCart) return alert('Đã có trong giỏ hàng');
    try {
      const res = await axios.post('/api/cart/add', { userId: user.id, gameId: +gameId });
      if (res.status === 201) {
        setInCart(true);
        setCartItems(prev => [...prev, { gameId: +gameId }]);
        navigate('/cart');
      }
    } catch (err) {
      console.error('Add to Cart Error:', err);
      alert(err.response?.data.message || 'Lỗi thêm vào giỏ hàng');
    }
  };

  const handleReviewSubmit = async () => {
    if (!newRating || !newComment.trim()) return;
    try {
      await axios.post(`/api/games/${gameId}/reviews`, {
        user_id: user.id,
        star: newRating,
        comment: newComment.trim(),
      });
      const res = await axios.get(`/api/games/${gameId}/reviews`);
      setReviews(res.data.reviews || []);
      setNewRating(0);
      setNewComment('');
      setCurrentPage(1);
    } catch (err) {
      console.error('Submit review error:', err);
      alert(err.response?.data.error || 'Lỗi gửi đánh giá');
    }
  };

  // Pagination logic
  const indexOfLast = currentPage * reviewsPerPage;
  const indexOfFirst = indexOfLast - reviewsPerPage;
  const currentReviews = reviews.slice(indexOfFirst, indexOfLast);
  const totalPages = Math.ceil(reviews.length / reviewsPerPage);
  const handlePageChange = page => page >= 1 && page <= totalPages && setCurrentPage(page);

  if (!game) return <div className="game-detail"><p>Loading...</p></div>;

  return (
    <div className="game-detail">
      {/* Header */}
      <header className="store-header">
        <nav className="nav">
          <span className="logo" onClick={() => navigate('/store')}><img src="/logo.png" alt="Logo" /></span>
          <div className="actions">
            <button onClick={() => navigate('/library')}>Library</button>
            <button onClick={() => navigate('/cart')}>Cart ({cartItems.length})</button>
            <button onClick={() => navigate('/profile')}>Profile</button>
          </div>
        </nav>
      </header>

      {/* Banner */}
      <section className="detail-banner">
        <img src={buildImageUrl(game.imageUrl)} alt={game.title} />
      </section>

      {/* Main Content */}
      <div className="detail-main">
        <div className="detail-left">
          <h2>{game.title}</h2>
          <p className="description">{game.description}</p>
          <div className="buy-section">
            <span className="price">${game.price}</span>
            <button className={inCart ? 'in-cart' : ''} onClick={handleAddToCart}>
              {inCart ? 'In Cart' : 'Add to Cart'}
            </button>
          </div>
          <section className="submit-review">
            <h4>Write a Review</h4>
            <label>Rating:
              <select value={newRating} onChange={e => setNewRating(+e.target.value)}>
                <option value={0}>Select</option>
                {[1,2,3,4,5].map(n => <option key={n} value={n}>{n}</option>)}
              </select>
            </label>
            <label>Comment:
              <textarea value={newComment} onChange={e => setNewComment(e.target.value)} placeholder="Write your review..." />
            </label>
            <button onClick={handleReviewSubmit}>Submit Review</button>
          </section>
        </div>

        <div className="detail-right">
          <img className="screenshot" src={buildImageUrl(game.screenshotUrl)} alt={`${game.title} screenshot`} />
          <section className="reviews">
            <h4>Đánh giá</h4>
            {currentReviews.length ? currentReviews.map(r => (
              <div key={r.id} className="review-item">
                <strong>User: {r.user_id}</strong>
                <p>Rating: {r.star} ★</p>
                <p>{r.comment}</p>
              </div>
            )) : <p>No reviews yet.</p>}
            {totalPages > 1 && (
              <div className="pagination">
                <button onClick={() => handlePageChange(currentPage-1)} disabled={currentPage===1}>Prev</button>
                {[...Array(totalPages)].map((_,i) => (
                  <button key={i} onClick={() => handlePageChange(i+1)} className={currentPage===i+1?'active':''}>{i+1}</button>
                ))}
                <button onClick={() => handlePageChange(currentPage+1)} disabled={currentPage===totalPages}>Next</button>
              </div>
            )}
          </section>
        </div>
      </div>

      {/* Suggested Games */}
      <section className="suggestions">
        <h4>Các game cùng thể loại</h4>
        {suggestedGames.length ? (
          <ul>{suggestedGames.map(g => <li key={g.id}><Link to={`/games/${g.id}`}>{g.title}</Link></li>)}</ul>
        ) : <p>Không có game gợi ý.</p>}
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="content">
          <div className="left"><img src="/logo.png" alt="Logo" /><p>© 2025 My Game Store</p></div>
          <div className="links"><a>About</a><a>Support</a><a>Privacy</a><a>Terms</a></div>
          <div className="social"><span className="facebook"/><span className="twitter"/></div>
        </div>
      </footer>
    </div>
  );
}
