import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/Cart.css';

// Configure base URL for API
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function Cart({ user, cartItems, setCartItems }) {
  const navigate = useNavigate();

  // Fetch cart items on mount or when user changes
  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await axios.get(`/api/cart/${user.id}`);
        setCartItems(res.data.items || []);
      } catch (err) {
        console.error('Error fetching cart:', err);
      }
    };

    if (user && user.id) fetchCart();
  }, [user, setCartItems]);

  // Remove an item from cart
  const handleRemove = async (cartItemId) => {
    try {
      await axios.delete(`/api/cart/${user.id}/${cartItemId}`);
      setCartItems(prev => prev.filter(item => item.cartItemId !== cartItemId));
    } catch (err) {
      console.error('Error removing cart item:', err);
    }
  };

  // Calculate total price
  const totalPrice = cartItems.reduce((sum, item) => {
    const price = Number(item.Price) || 0;
    const discount = Number(item.Discount) || 0;
    return sum + price * (1 - discount / 100);
  }, 0);

  return (
    <div className="cart-page">
      {/* Header */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>
            <img src="/logo.png" alt="Steam Logo" />
          </span>
          <input
            className="search-bar__input"
            type="text"
            placeholder="Search games..."
            value={query}
            onChange={e => setQuery(e.target.value)}
          />
          <div className="user-actions">
            <button className="btn-flat" onClick={() => navigate('/library')}>Library</button>
            <button className="btn-flat" onClick={() => navigate('/cart')}>Cart ({cartItems.length})</button>
            <button className="btn-flat" onClick={() => navigate('/profile')}>Profile</button>
          </div>
        </div>
      </header>
      {/* Main Content */}
      <div className="main-container">
        <div className="cart-container">
          {cartItems.length === 0 ? (
            <div className="cart-empty">Your cart is empty</div>
          ) : (
            <>
              <ul className="cart-list">
                {cartItems.map(item => {
                  const price = Number(item.Price) || 0;
                  const discount = Number(item.Discount) || 0;
                  const finalPrice = (price * (1 - discount / 100)).toFixed(2);
                  return (
                    <li key={item.cartItemId} className="cart-item">
                      <span className="cart-item-name">{item.Name}</span>
                      <span className="cart-item-price">${finalPrice}</span>
                      <button
                        className="remove-btn"
                        onClick={() => handleRemove(item.cartItemId)}
                      >
                        Remove
                      </button>
                    </li>
                  );
                })}
              </ul>

              <div className="cart-total">Total: ${totalPrice.toFixed(2)}</div>

              <div className="cart-actions">
                <button className="btn-back" onClick={() => navigate('/store')}>
                  Continue Shopping
                </button>
                <button className="btn-back" onClick={() => navigate('/checkout')}>
                  Checkout
                </button>
              </div>
            </>
          )}
        </div>
      </div>

      {/* FOOTER */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
            <p>Â© 2023 Valve Corporation. All rights reserved.</p>
            <p>All trademarks are property of their respective owners.</p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Subscriber Agreement</li>
              <li>Refunds</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook" />
            <div className="social-icon close" />
          </div>
        </div>
      </footer>
    </div>
  );
}
