import React, { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import axios from "axios";

import Login from "./Login";
import StorePage from "./StorePage";
import GameDetail from "./GameDetail";
import Cart from "./Cart";
import Library from "./Library";
import Profile from "./Profile";
import AddGame from "./AddGame";
import Checkout from "./Checkout";
import PrivateRoute from "./PrivateRoute";
import AdminRoute from './AdminRoute';
import AdminPanel from './AdminPanel';

// Thiết lập URL backend chung
axios.defaults.baseURL = process.env.REACT_APP_API_URL || "http://localhost:5000";

function App() {
  const [user, setUser] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [isInitializing, setIsInitializing] = useState(true);

  const handleLogin = (userData, token) => {
    localStorage.setItem("token", token);
    localStorage.setItem("user", JSON.stringify(userData));
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    setUser(userData);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    delete axios.defaults.headers.common["Authorization"];
    setUser(null);
    setCartItems([]);
  };

  // Load lại thông tin đăng nhập khi F5
  useEffect(() => {
    const savedToken = localStorage.getItem("token");
    const savedUser = localStorage.getItem("user");

    if (savedToken && savedUser) {
      try {
        const payload = JSON.parse(atob(savedToken.split(".")[1]));
        if (payload.exp * 1000 > Date.now()) {
          axios.defaults.headers.common["Authorization"] = `Bearer ${savedToken}`;
          setUser(JSON.parse(savedUser));
        } else {
          localStorage.removeItem("token");
          localStorage.removeItem("user");
        }
      } catch {
        localStorage.removeItem("token");
        localStorage.removeItem("user");
      }
    }
    setIsInitializing(false);
  }, []);

  if (isInitializing) {
    return <div>Đang khởi tạo, vui lòng chờ…</div>;
  }

  return (
      <Routes>
        {/* Login */}
        <Route
  path="/login"
  element={
    user
      ? <Navigate to="/store" />
      : <Login setUser={setUser} />
  }
/>


        {/* Trang chủ & Store */}
        <Route
          path="/"
          element={
            user ? (
              <StorePage
                cartItems={cartItems}
                setCartItems={setCartItems}
                user={user}
                onLogout={handleLogout}
              />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />
        <Route
          path="/store"
          element={
            user ? (
              <StorePage
                cartItems={cartItems}
                setCartItems={setCartItems}
                user={user}
                onLogout={handleLogout}
              />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        {/* Game detail */}
        <Route
          path="/game/:gameId"
          element={
            user ? (
              <GameDetail
                user={user}
                cartItems={cartItems}
                setCartItems={setCartItems}
              />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        {/* Cart */}
        <Route
          path="/cart"
          element={
            user ? (
              <Cart
                cartItems={cartItems}
                setCartItems={setCartItems}
                user={user}
              />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        {/* Library */}
        <Route
          path="/library"
          element={
            user ? <Library user={user} /> : <Navigate to="/login" replace />
          }
        />

        {/* Profile (đã sửa) */}
        <Route
          path="/profile"
          element={
            user ? <Profile /> : <Navigate to="/login" replace />
          }
        />

        {/* Add game (admin) */}
        <Route
          path="/addgame"
          element={
            user ? <AddGame user={user} /> : <Navigate to="/login" replace />
          }
        />

        {/* Checkout */}
        <Route
          path="/checkout"
          element={
            user ? (
              <Checkout setCartItems={setCartItems} user={user} />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />    
        {/* Chỉ admin mới xem được /admin */}
        <Route element={<AdminRoute />}>
          <Route path="/admin" element={<AdminPanel />} />
        </Route>
        {/* Fallback */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
  );
}

export default App;
