import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Style/StorePage.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function StorePage({ cartItems, setCartItems }) {
  const [featured, setFeatured] = useState([]);
  const [special, setSpecial] = useState([]);
  const [freeGames, setFreeGames] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);

  const PAGE_SIZE = 4;
  const [pageInfo, setPageInfo] = useState({ special: 1, free: 1, best: 1 });
  const [totalPages, setTotalPages] = useState({ special: 1, free: 1, best: 1 });

  useEffect(() => {
    async function fetchGames() {
      try {
        const params = { page: 1, pageSize: PAGE_SIZE };
        const [resFeat, resSpec, resFree, resBest] = await Promise.all([
          axios.get('/api/games', { params }),
          axios.get('/api/games', { params: { ...params, type: 'special' } }),
          axios.get('/api/games', { params: { ...params, type: 'free' } }),
          axios.get('/api/games', { params: { ...params, type: 'best' } }),
        ]);
        setFeatured(resFeat.data.items);
        setSpecial(resSpec.data.items);
        setFreeGames(resFree.data.items);
        setBestSellers(resBest.data.items);
        setTotalPages({
          special: Math.ceil(resSpec.data.total / PAGE_SIZE),
          free: Math.ceil(resFree.data.total / PAGE_SIZE),
          best: Math.ceil(resBest.data.total / PAGE_SIZE),
        });
      } catch (err) {
        console.error('Error fetching games:', err);
      }
    }
    fetchGames();
  }, []);

  const handlePageChange = (category, delta) => {
    setPageInfo(prev => {
      const next = prev[category] + delta;
      return { ...prev, [category]: Math.max(1, Math.min(next, totalPages[category])) };
    });
  };

  return (
    <div className="store-page">
      {/* Featured Game (only one) */}
      <section className="featured-container">
        <h2>Featured Game</h2>
        {featured[0] && (
          <div className="featured">
            <Link to={`/game/${featured[0].id}`} className="game-card">
              <img src={featured[0].imageUrl} alt={featured[0].title} />
            </Link>
            <div className="game-info">
              <h3>{featured[0].title}</h3>
              <p>{featured[0].description}</p>
              <p>{(featured[0].salesCount || 0).toLocaleString()} lượt mua</p>
            </div>
          </div>
        )}
      </section>

      {/* Special Deals */}
      <section className="special-offers">
        <h2>Special Deals</h2>
        <div className="game-list">
          {special.map(game => (
            <Link key={game.id} to={`/game/${game.id}`} className="game-card">
              <div className="discount">-{game.discount}%</div>
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
              {game.price > 0 && <span className="original-price">${game.price.toFixed(2)}</span>}
              <span className="discounted-price">${(game.price * (1 - game.discount/100)).toFixed(2)}</span>
              <p>{(game.salesCount || 0).toLocaleString()} lượt mua</p>
            </Link>
          ))}
        </div>
        <div className="pagination">
          <button onClick={() => handlePageChange('special', -1)} disabled={pageInfo.special === 1}>&lt; Prev</button>
          <span>{pageInfo.special}/{totalPages.special}</span>
          <button onClick={() => handlePageChange('special', 1)} disabled={pageInfo.special === totalPages.special}>Next &gt;</button>
        </div>
      </section>

      {/* Free Games */}
      <section className="free-games">
        <h2>Free Games</h2>
        <div className="game-list">
          {freeGames.map(game => (
            <Link key={game.id} to={`/game/${game.id}`} className="game-card">
              {game.discount > 0 && <div className="discount">-{game.discount}%</div>}
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
              <p>Free</p>
              <p>{(game.salesCount || 0).toLocaleString()} lượt mua</p>
            </Link>
          ))}
        </div>
        <div className="pagination">
          <button onClick={() => handlePageChange('free', -1)} disabled={pageInfo.free === 1}>&lt; Prev</button>
          <span>{pageInfo.free}/{totalPages.free}</span>
          <button onClick={() => handlePageChange('free', 1)} disabled={pageInfo.free === totalPages.free}>Next &gt;</button>
        </div>
      </section>

      {/* Best Sellers */}
      <section className="best-seller">
        <h2>Best Sellers</h2>
        <div className="game-list">
          {bestSellers.map(game => (
            <Link key={game.id} to={`/game/${game.id}`} className="game-card">
              <div className="discount">-{game.discount}%</div>
              <img src={game.imageUrl} alt={game.title} />
              <h3>{game.title}</h3>
              {game.price > 0 && <span className="original-price">${game.price.toFixed(2)}</span>}
              <span className="discounted-price">${(game.price * (1 - game.discount/100)).toFixed(2)}</span>
              <p>{(game.salesCount || 0).toLocaleString()} lượt mua</p>
            </Link>
          ))}
        </div>
        <div className="pagination">
          <button onClick={() => handlePageChange('best', -1)} disabled={pageInfo.best === 1}>&lt; Prev</button>
          <span>{pageInfo.best}/{totalPages.best}</span>
          <button onClick={() => handlePageChange('best', 1)} disabled={pageInfo.best === totalPages.best}>Next &gt;</button>
        </div>
      </section>
    </div>
  );
}
