import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Style/AdminPanel.css';

/**
 * AdminPanel – sync with current Express API (19‑May‑2025)
 * ------------------------------------------------------
 * Sidebar: Người dùng | Đơn hàng | Game
 * Game CRUD now hits `/api/games` (server‑side path) and adapts to
 *   { items, total } response shape.
 * Quick‑and‑dirty <prompt> UI retained; for production you should replace
 *   with a form + image upload (server requires image & genreId).
 */
const pageSize = 5;
const API_GAMES = '/api/games';

const AdminPanel = ({ onLogout }) => {
  const token = sessionStorage.getItem('token');
  const authHeader = { Authorization: `Bearer ${token}` };
  const [activeTab, setActiveTab] = useState('users'); // users | orders | games

  /* ---------------- USERS --------------- */
  const [users, setUsers] = useState([]);
  const [searchUser, setSearchUser] = useState('');
  const [userPage, setUserPage] = useState(1);
  const [totalUsers, setTotalUsers] = useState(0);

  /* ---------------- ORDERS -------------- */
  const [orders, setOrders] = useState([]);
  const [orderPage, setOrderPage] = useState(1);
  const [totalOrders, setTotalOrders] = useState(0);

  /* ---------------- GAMES --------------- */
  const [games, setGames] = useState([]);
  const [searchGame, setSearchGame] = useState('');
  const [gamePage, setGamePage] = useState(1);
  const [totalGames, setTotalGames] = useState(0);

  /* ---------------- EFFECTS ------------- */
  useEffect(() => { if (activeTab==='users')  fetchUsers();  /* eslint-disable-next-line */ }, [searchUser, userPage, activeTab]);
  useEffect(() => { if (activeTab==='orders') fetchOrders(); /* eslint-disable-next-line */ }, [orderPage, activeTab]);
  useEffect(() => { if (activeTab==='games')  fetchGames();  /* eslint-disable-next-line */ }, [searchGame, gamePage, activeTab]);

  /* ---------------- API CALLS ----------- */
  const fetchUsers = async () => {
    try {
      const { data } = await axios.get('/api/admin/users', {
        params: { search: searchUser, page: userPage, pageSize }, headers: authHeader });
      setUsers(data.users); setTotalUsers(data.total);
    } catch (err) { console.error(err); }
  };

  const fetchOrders = async () => {
    try {
      const { data } = await axios.get('/api/admin/orders', {
        params: { page: orderPage, pageSize }, headers: authHeader });
      setOrders(data.orders); setTotalOrders(data.total);
    } catch (err) { console.error(err); }
  };

  const fetchGames = async () => {
    try {
      const { data } = await axios.get(API_GAMES, {
        params: { search: searchGame, page: gamePage, pageSize }, headers: authHeader });
      setGames(data.items); setTotalGames(data.total);
    } catch (err) { console.error(err); }
  };

  /* ---------------- HANDLERS ------------ */
  // --- USER CRUD ---
  const handleUpdateUser = async u => {
    const name = prompt('Tên mới', u.name);
    const role = prompt('Vai trò (user/admin)', u.role);
    if (!name || !role) return;
    try {
      await axios.put(`/api/admin/users/${u.id}`, { name, role }, { headers: authHeader });
      fetchUsers();
    } catch (err) { alert(err.response?.data?.message || 'Lỗi'); }
  };
  const handleDeleteUser = async id => {
    if (!window.confirm('Xoá người dùng?')) return;
    try {
      await axios.delete(`/api/admin/users/${id}`, { headers: authHeader });
      fetchUsers();
    } catch (err) { alert(err.response?.data?.message || 'Lỗi'); }
  };

  // --- ORDER ---
  const handleViewOrder = async id => {
    try {
      const { data } = await axios.get(`/api/admin/orders/${id}`, { headers: authHeader });
      alert(JSON.stringify(data.orderDetails, null, 2));
    } catch (err) { alert(err.response?.data?.message || 'Lỗi'); }
  };

  // --- GAME CRUD (simplified) ---
  const handleAddGame = async () => {
    const title    = prompt('Tên game');
    const price    = prompt('Giá (VNĐ)');
    const genreId  = prompt('GenreID (bắt buộc trong DB)', '1');
    if (!title || price==null || !genreId) return;
    try {
      const payload = { Name: title, Price: Number(price), genreId: Number(genreId), Discount: 0 };
      await axios.post(API_GAMES, payload, { headers: authHeader });
      fetchGames();
    } catch (err) { alert(err.response?.data?.message || 'Lỗi'); }
  };

  const handleUpdateGame = async g => {
    const title    = prompt('Tên mới', g.title);
    const price    = prompt('Giá mới', g.price);
    const discount = prompt('Giảm giá (%)', g.discount || 0);
    if (!title || price==null) return;
    try {
      const payload = { Name: title, Price: Number(price), Discount: Number(discount), genreId: g.genreId || 1 };
      await axios.put(`${API_GAMES}/${g.id}`, payload, { headers: authHeader });
      fetchGames();
    } catch (err) { alert(err.response?.data?.message || 'Lỗi'); }
  };

  const handleDeleteGame = async id => {
    if (!window.confirm('Xoá game?')) return;
    try {
      await axios.delete(`${API_GAMES}/${id}`, { headers: authHeader });
      fetchGames();
    } catch (err) { alert(err.response?.data?.message || 'Lỗi'); }
  };

  /* ---------------- PAGINATION ----------- */
  const totalUserPages  = Math.max(1, Math.ceil(totalUsers  / pageSize));
  const totalOrderPages = Math.max(1, Math.ceil(totalOrders / pageSize));
  const totalGamePages  = Math.max(1, Math.ceil(totalGames  / pageSize));

  /* ---------------- UI ------------------- */
  return (
    <div className="admin-panel-container">
      {/* SIDEBAR */}
      <aside className="admin-sidebar">
        <h2 className="admin-sidebar-title">Quản lý</h2>
        <div className={`admin-menu-item ${activeTab==='users'  ? 'active' : ''}`} onClick={()=>setActiveTab('users')}>Người dùng</div>
        <div className={`admin-menu-item ${activeTab==='orders' ? 'active' : ''}`} onClick={()=>setActiveTab('orders')}>Đơn hàng</div>
        <div className={`admin-menu-item ${activeTab==='games'  ? 'active' : ''}`} onClick={()=>setActiveTab('games')}>Game</div>
        <button className="admin-logout-btn" onClick={onLogout}>Đăng xuất</button>
      </aside>

      {/* MAIN */}
      <main className="admin-main">
        {/* USERS */}
        {activeTab==='users' && (
          <section className="admin-section">
            <h2>Quản lý Người dùng</h2>
            <input placeholder="Tìm kiếm" value={searchUser} onChange={e=>{ setSearchUser(e.target.value); setUserPage(1); }} />
            <table className="admin-table">
              <thead><tr><th>ID</th><th>Name</th><th>Email</th><th>Role</th><th>Hành động</th></tr></thead>
              <tbody>{users.map(u => (
                <tr key={u.id}><td>{u.id}</td><td>{u.name}</td><td>{u.email}</td><td>{u.role}</td><td>
                  <button onClick={()=>handleUpdateUser(u)}>Sửa</button>
                  <button onClick={()=>handleDeleteUser(u.id)}>Xoá</button>
                </td></tr>
              ))}</tbody>
            </table>
            <div className="admin-pagination">
              <button disabled={userPage===1} onClick={()=>setUserPage(p=>p-1)}>«</button>
              <span>{userPage}/{totalUserPages}</span>
              <button disabled={userPage===totalUserPages} onClick={()=>setUserPage(p=>p+1)}>»</button>
            </div>
          </section>
        )}

        {/* ORDERS */}
        {activeTab==='orders' && (
          <section className="admin-section">
            <h2>Quản lý Đơn hàng</h2>
            <table className="admin-table">
              <thead><tr><th>ID</th><th>User</th><th>Total</th><th>Date</th><th>Hành động</th></tr></thead>
              <tbody>{orders.map(o => (
                <tr key={o.id}><td>{o.id}</td><td>{o.customerName}</td><td>{o.total_amount?.toLocaleString()}</td><td>{new Date(o.purchase_date).toLocaleDateString()}</td><td>
                  <button onClick={()=>handleViewOrder(o.id)}>Xem</button>
                </td></tr>
              ))}</tbody>
            </table>
            <div className="admin-pagination">
              <button disabled={orderPage===1} onClick={()=>setOrderPage(p=>p-1)}>«</button>
              <span>{orderPage}/{totalOrderPages}</span>
              <button disabled={orderPage===totalOrderPages} onClick={()=>setOrderPage(p=>p+1)}>»</button>
            </div>
          </section>
        )}

        {/* GAMES */}
        {activeTab==='games' && (
          <section className="admin-section">
            <h2>Quản lý Game</h2>
            <div className="admin-toolbar">
              <input placeholder="Tìm kiếm" value={searchGame} onChange={e=>{ setSearchGame(e.target.value); setGamePage(1); }} />
              <button onClick={handleAddGame}>+ Thêm game</button>
            </div>
            <table className="admin-table">
              <thead><tr><th>ID</th><th>Title</th><th>Price</th><th>Discount(%)</th><th>Hành động</th></tr></thead>
              <tbody>{games.map(g => (
                <tr key={g.id}><td>{g.id}</td><td>{g.title}</td><td>{g.price?.toLocaleString()}</td><td>{g.discount}</td><td>
                  <button onClick={()=>handleUpdateGame(g)}>Sửa</button>
                  <button onClick={()=>handleDeleteGame(g.id)}>Xoá</button>
                </td></tr>
              ))}</tbody>
            </table>
            <div className="admin-pagination">
              <button disabled={gamePage===1} onClick={()=>setGamePage(p=>p-1)}>«</button>
              <span>{gamePage}/{totalGamePages}</span>
              <button disabled={gamePage===totalGamePages} onClick={()=>setGamePage(p=>p+1)}>»</button>
            </div>
          </section>
        )}
      </main>
    </div>
  );
};

export default AdminPanel;
