import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Style/StorePage.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function StorePage({ cartItems, setCartItems }) {
  const [featured, setFeatured]       = useState([]);
  const [special, setSpecial]         = useState([]);
  const [freeGames, setFreeGames]     = useState([]);
  const [fullTopBest, setFullTopBest] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [games, setGames]             = useState([]);
  const [genres, setGenres]           = useState([{ id: 0, name: 'All' }]);
  const [selectedGenreId, setSelectedGenreId] = useState(0);
  const [query, setQuery]             = useState('');
  const navigate = useNavigate();

  const PAGE_SIZE = 4;
  const [pageInfo, setPageInfo]     = useState({ special: 1, free: 1, best: 1 });
  const [totalPages, setTotalPages] = useState({ special: 1, free: 1, best: 1 });

  // Utility: Convert price string (VN format) to Number
  function safePrice(val) {
    if (val == null || val === '') return null;
    let str = String(val).trim();
    if (/^\d{1,3}(\.\d{3})*,\d+$/.test(str)) {
      str = str.replace(/\./g, '').replace(',', '.');
    } else {
      const parts = str.split('.');
      if (parts.length > 2) {
        const dec = parts.pop();
        str = parts.join('') + '.' + dec;
      }
      str = str.replace(/,/g, '');
    }
    const num = Number(str);
    return Number.isFinite(num) ? num : null;
  }

  // 1) Fetch initial data for all sections
  useEffect(() => {
    async function fetchData() {
      try {
        const paramsBase = { page: 1, pageSize: PAGE_SIZE };

        const [resFeat, resSpec, resFree] = await Promise.all([
          axios.get('/api/games',      { params: paramsBase }),
          axios.get('/api/games',      { params: { ...paramsBase, type: 'special' } }),
          axios.get('/api/games',      { params: { ...paramsBase, type: 'free'    } }),
        ]);

        // Best sellers logic
        const resBestAll = await axios.get('/api/games', { params: { type: 'best', page: 1, pageSize: 1000 } });
        const sortedBest = (resBestAll.data.items || [])
          .sort((a, b) => (b.salesCount || 0) - (a.salesCount || 0))
          .slice(0, 16);

        // Full list for search & genre
        const resAll = await axios.get('/api/games', { params: { page: 1, pageSize: 1000 } });

        setFeatured(resFeat.data.items);
        setSpecial(resSpec.data.items);
        setFreeGames(resFree.data.items);
        setFullTopBest(sortedBest);
        setBestSellers(sortedBest.slice(0, PAGE_SIZE));
        setGames(resAll.data.items);

        setTotalPages({
          special: Math.ceil(resSpec.data.total / PAGE_SIZE),
          free:    Math.ceil(resFree.data.total / PAGE_SIZE),
          best:    Math.ceil(sortedBest.length     / PAGE_SIZE),
        });
      } catch (err) {
        console.error('Error fetching games data:', err);
      }
    }
    fetchData();
  }, []);

  // 2) Fetch genres
  useEffect(() => {
    async function fetchGenres() {
      try {
        // Call with pagination to return only data array
        const res = await axios.get('/api/genres', { params: { page: 1, pageSize: 1000 } });
        const list = (res.data.data || []).map(g => ({ id: g.GenreID, name: g.Name }));
        setGenres([{ id: 0, name: 'All' }, ...list]);
      } catch (err) {
        console.warn('Could not load genres');
      }
    }
    fetchGenres();
  }, []);

  // 3) Fetch filtered games for search or genre
  useEffect(() => {
    if (selectedGenreId === 0 && query.trim() === '') return;
    async function fetchFiltered() {
      try {
        const params = { page: 1, pageSize: 1000 };
        if (selectedGenreId !== 0) params.genreId = selectedGenreId;
        if (query.trim())       params.search  = query.trim();

        const res = await axios.get('/api/games', { params });
        setGames(res.data.items);
      } catch (err) {
        console.error('Error fetching filtered games:', err);
      }
    }
    fetchFiltered();
  }, [selectedGenreId, query]);

  // 4) Pagination for sections
  async function handlePageChange(category, delta) {
    const next = Math.max(1, Math.min(pageInfo[category] + delta, totalPages[category]));
    setPageInfo(prev => ({ ...prev, [category]: next }));

    if (category === 'best') {
      const start = (next - 1) * PAGE_SIZE;
      setBestSellers(fullTopBest.slice(start, start + PAGE_SIZE));
      return;
    }
    try {
      const res = await axios.get('/api/games', { params: { type: category, page: next, pageSize: PAGE_SIZE } });
      if (category === 'special') setSpecial(res.data.items);
      if (category === 'free')    setFreeGames(res.data.items);
    } catch (err) {
      console.error(`Error fetching ${category} page ${next}`, err);
    }
  }

  const isFiltering = query.trim() !== '' || selectedGenreId !== 0;
  const activeGenreName = genres.find(g => g.id === selectedGenreId)?.name || '';

  return (
    <div className="store-page">
      {/* Genre Filter */}
      <div className="genre-filter">
        {genres.map(genre => (
          <button
            key={genre.id}
            className={genre.id === selectedGenreId ? 'active' : ''}
            onClick={() => { setSelectedGenreId(genre.id); setQuery(''); }}
          >{genre.name}</button>
        ))}
      </div>

      {/* Search Bar */}
      <div className="search-bar">
        <input
          type="text"
          value={query}
          onChange={e => setQuery(e.target.value)}
          placeholder="Search games..."
        />
      </div>

      {/* Content */}
      {isFiltering ? (
        <section className="games-list">
          {games.map(game => (
            <div key={game.GameID} className="game-card">
              <Link to={`/game/${game.GameID}`}>
                <img src={game.coverUrl} alt={game.Title} />
                <h3>{game.Title}</h3>
                <p>{safePrice(game.Price)} VND</p>
              </Link>
              <button onClick={() => {/* add to cart */}}>Add to Cart</button>
            </div>
          ))}
          {games.length === 0 && (
            <p>No games found{query ? ` for "${query}"` : ` in ${activeGenreName}`}</p>
          )}
        </section>
      ) : (
        <>
          {/* Featured Game */}
          <section className="featured">
            <h2>Featured Game</h2>
            {featured[0] && (
              <div className="game-card-large">
                <Link to={`/game/${featured[0].GameID}`}>
                  <img src={featured[0].coverUrl} alt={featured[0].Title} />
                  <h3>{featured[0].Title}</h3>
                  <p>{featured[0].description}</p>
                </Link>
              </div>
            )}
          </section>

          {/* Special Deals */}
          <section className="special-offers">
            <h2>Special Deals</h2>
            <div className="game-list">
              {special.map(game => (
                <Link key={game.GameID} to={`/game/${game.GameID}`} className="game-card">
                  {game.discount > 0 && <div className="åçdiscount">-{game.discount}%</div>}
                  <img src={game.coverUrl} alt={game.Title} />
                  <h3>{game.Title}</h3>
                  <div className="price">
                    {safePrice(game.Price) > 0 && (
                      <span className="original-price">{safePrice(game.Price).toLocaleString()} VND</span>
                    )}
                    <span className="discounted-price">
                      {((safePrice(game.Price) || 0) * (1 - (game.discount || 0) / 100)).toLocaleString()} VND
                    </span>
                  </div>
                  <p>{(game.salesCount || 0).toLocaleString()} lượt mua</p>
                </Link>
              ))}
            </div>
            <div className="pagination">
              <button onClick={() => handlePageChange('special', -1)} disabled={pageInfo.special === 1}>&lt; Prev</button>
              <span>{pageInfo.special}/{totalPages.special}</span>
              <button onClick={() => handlePageChange('special', 1)} disabled={pageInfo.special === totalPages.special}>Next &gt;</button>
            </div>
          </section>

          {/* Free Games */}
          <section className="free-games">
            <h2>Free Games</h2>
            <div className="game-list">
              {freeGames.map(game => (
                <Link key={game.GameID} to={`/game/${game.GameID}`} className="game-card">
                  <img src={game.coverUrl} alt={game.Title} />
                  <h3>{game.Title}</h3>
                  <p>Free</p>
                  <p>{(game.salesCount || 0).toLocaleString()} lượt mua</p>
                </Link>
              ))}
            </div>
            <div className="pagination">
              <button onClick={() => handlePageChange('free', -1)} disabled={pageInfo.free === 1}>&lt; Prev</button>
              <span>{pageInfo.free}/{totalPages.free}</span>
              <button onClick={() => handlePageChange('free', 1)} disabled={pageInfo.free === totalPages.free}>Next &gt;</button>
            </div>
          </section>

          {/* Best Sellers */}
          <section className="best-seller">
            <h2>Best Sellers</h2>
            <div className="game-list">
              {bestSellers.map(game => (
                <Link key={game.GameID} to={`/game/${game.GameID}`} className="game-card">
                  <img src={game.coverUrl} alt={game.Title} />
                  <h3>{game.Title}</h3>
                  <div className="price">
                    {safePrice(game.Price) > 0 && (
                      <span className="original-price">{safePrice(game.Price).toLocaleString()} VND</span>
                    )}
                    <span className="discounted-price">
                      {((safePrice(game.Price) || 0) * (1 - (game.discount || 0) / 100)).toLocaleString()} VND
                    </span>
                  </div>
                  <p>{(game.salesCount || 0).toLocaleString()} lượt mua</p>
                </Link>
              ))}
            </div>
            <div className="pagination">
              <button onClick={() => handlePageChange('best', -1)} disabled={pageInfo.best === 1}>&lt; Prev</button>
              <span>{pageInfo.best}/{totalPages.best}</span>
              <button onClick={() => handlePageChange('best', 1)} disabled={pageInfo.best === totalPages.best}>Next &gt;</button>
            </div>
          </section>
        </>
      )}

      {/* Footer */}
      <footer className="footer">
        {/* footer content unchanged */}
      </footer>
    </div>
  );
}
