// src/components/Profile.js

import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Style/Profile.css";

// thiết lập base URL cho tất cả các request
axios.defaults.baseURL =
  process.env.REACT_APP_API_URL || "http://localhost:5000";

export default function Profile({ user, onLogout }) {
  const navigate = useNavigate();

  const [purchaseHistory, setPurchaseHistory] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [loadingHistory, setLoadingHistory] = useState(true);

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }

    // Thiết lập header Authorization
    const token = sessionStorage.getItem("token");
    if (token) {
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    }

    // 1) Fetch lịch sử mua hàng
    axios
      .get("/api/purchases/history")
      .then((res) => {
        setPurchaseHistory(res.data.purchases || []);
      })
      .catch((err) => {
        console.error("Error fetching purchase history:", err);
      })
      .finally(() => {
        setLoadingHistory(false);
      });

    // 2) Fetch reviews của user
    axios
      .get(`/api/users/${user.id}/reviews`)
      .then((res) => {
        // API trả về { reviews: [...] } hoặc tương tự
        const data = Array.isArray(res.data.reviews)
          ? res.data.reviews
          : Array.isArray(res.data.data)
          ? res.data.data
          : [];
        setReviews(data);
      })
      .catch((err) => {
        console.error("Error fetching user reviews:", err);
      });
  }, [user, navigate]);

  if (!user) {
    return <div className="loading">Loading profile…</div>;
  }

  return (
    <div className="profile-page">
      {/* HEADER */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate("/store")}>
            <img src="/logo.png" alt="Steam Logo" />
          </span>
          <div className="user-actions">
            <button className="btn-flat" onClick={() => navigate("/library")}>
              Library
            </button>
            <button className="btn-flat" onClick={() => navigate("/cart")}>
              Cart
            </button>
            <button
              className="btn-flat"
              onClick={() => navigate("/profile")}
            >
              Profile
            </button>
          </div>
        </div>
      </header>

      {/* THÔNG TIN CÁ NHÂN */}
      <section className="user-info">
        <h3>Thông tin cá nhân</h3>
        <p>
          <strong>Tên:</strong> {user.name || user.username}
        </p>
        <p>
          <strong>Email:</strong> {user.email}
        </p>
        {user.createdAt && (
          <p>
            <strong>Ngày tạo:</strong>{" "}
            {new Date(user.createdAt).toLocaleDateString()}
          </p>
        )}
      </section>

      {/* NỘI DUNG CHÍNH */}
      <main className="profile-container">
        {/* LỊCH SỬ MUA HÀNG */}
        <section className="purchase-history">
          <h3>Lịch sử mua hàng</h3>

          {loadingHistory ? (
            <p>Đang tải lịch sử mua hàng...</p>
          ) : purchaseHistory.length === 0 ? (
            <p className="placeholder-text">Bạn chưa có đơn hàng nào.</p>
          ) : (
            <ul className="orders-list">
              {purchaseHistory.map((order) => (
                <li key={order.id} className="order-item">
                  <div className="order-meta">
                    <span className="order-id">Đơn #{order.id}</span>
                    <span className="order-date">
                      {new Date(order.purchase_date).toLocaleString()}
                    </span>
                    <span className="order-total">
                      Tổng: {order.total_amount?.toLocaleString()} đ
                    </span>
                    <button
                      className="btn-flat btn-sm"
                      onClick={() => navigate(`/orders/${order.id}`)}
                    >
                      Xem chi tiết
                    </button>
                  </div>
                  <ul className="order-items">
                    {order.items.map((item) => (
                      <li key={item.game_id}>
                        {item.game_name} —{" "}
                        {item.unit_price?.toLocaleString()} đ
                      </li>
                    ))}
                  </ul>
                </li>
              ))}
            </ul>
          )}
        </section>

        {/* YOUR REVIEWS */}
        <section className="user-reviews">
          <h3>Your Reviews</h3>
          {reviews.length > 0 ? (
            <ul className="reviews-list">
              {reviews.map((r) => (
                <li key={r.id} className="review-item">
                  <strong>{r.title || "Untitled Review"}</strong>
                  <p>{r.comment || r.content}</p>
                </li>
              ))}
            </ul>
          ) : (
            <p className="placeholder-text">
              You haven’t written any reviews yet.
            </p>
          )}
        </section>

        <button className="logout-btn" onClick={onLogout}>
          Đăng xuất
        </button>
      </main>

      {/* FOOTER */}
      <footer className="footer">
        {/* ...nội dung footer nếu có... */}
      </footer>
    </div>
  );
}
