import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Style/Profile.css";

// Cấu hình base URL cho API
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function Profile({ user, onLogout }) {
  const navigate = useNavigate();
  const [uploadedGames, setUploadedGames] = useState([]);
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }

    // Thiết lập header xác thực
    axios.defaults.headers.common["Authorization"] = `Bearer ${sessionStorage.getItem("token")}`;
    const userId = user.id || user.userId || user._id;

    // Fetch uploaded games
    axios.get(`/api/games?uploader=${userId}`)
      .then(res => {
        const payload = res.data;
        let list = Array.isArray(payload)
          ? payload
          : Array.isArray(payload.data)
            ? payload.data
            : Array.isArray(payload.games)
              ? payload.games
              : [];
        setUploadedGames(list);
      })
      .catch(err => console.error("Error fetching games:", err));

    // Fetch user reviews
    axios.get(`/api/users/${userId}/reviews`)
      .then(res => {
        const payload = res.data;
        let list = Array.isArray(payload)
          ? payload
          : Array.isArray(payload.data)
            ? payload.data
            : Array.isArray(payload.reviews)
              ? payload.reviews
              : [];
        setReviews(list);
      })
      .catch(err => console.error("Error fetching reviews:", err));
  }, [user, navigate]);

  if (!user) {
    return <div className="loading">Loading profile…</div>;
  }

  return (
    <div className="profile-page">
      {/* HEADER */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>
            <img src="/logo.png" alt="Steam Logo" />
          </span>
          <div className="user-actions">
            <button className="btn-flat" onClick={() => navigate('/library')}>Library</button>
            <button className="btn-flat" onClick={() => navigate('/cart')}>Cart</button>
            <button className="btn-flat" onClick={() => navigate('/profile')}>Profile</button>
          </div>
        </div>
      </header>

      {/* MAIN */}
      <main className="profile-container">
        {/* Recent Activity */}
        <section className="recent-activity">
          <h3>Recent Activity</h3>
          {Array.isArray(user.recentActivity) && user.recentActivity.length > 0 ? (
            <div className="activity-list">
              {user.recentActivity.map((item, i) => (
                <div key={i} className="activity-item">
                  <img src={item.image} alt={item.game} />
                  <p>{item.game}</p>
                  <p>{item.hours} hrs</p>
                  <p>Last played: {item.lastPlayed}</p>
                </div>
              ))}
            </div>
          ) : (
            <p className="placeholder-text">No recent activity.</p>
          )}
        </section>

        {/* Uploaded Games */}
        <section className="uploaded-games">
          <h3>Uploaded Games</h3>
          {uploadedGames.length > 0 ? (
            <div className="uploaded-list">
              {uploadedGames.map(game => (
                <div key={game.id} className="game-card">
                  <img src={game.imageUrl} alt={game.title} />
                  <h4>{game.title}</h4>
                  <p>{game.price}</p>
                  <button onClick={() => navigate(`/games/${game.id}`)}>View</button>
                </div>
              ))}
            </div>
          ) : (
            <p className="placeholder-text">You haven’t uploaded any games yet.</p>
          )}
        </section>

        {/* User Reviews */}
        <section className="user-reviews">
          <h3>Your Reviews</h3>
          {reviews.length > 0 ? (
            <ul className="reviews-list">
              {reviews.map(r => (
                <li key={r.id || r.reviewId} className="review-item">
                  <strong>{r.title || 'Untitled Review'}</strong>
                  <p>{r.content || r.comment}</p>
                </li>
              ))}
            </ul>
          ) : (
            <p className="placeholder-text">You haven’t written any reviews yet.</p>
          )}
        </section>

        <button className="logout-btn" onClick={onLogout}>
          Đăng xuất
        </button>
      </main>

      {/* FOOTER */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>All trademarks are property of their respective owners.</p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Subscriber Agreement</li>
              <li>Refunds</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook" />
            <div className="social-icon close" />
          </div>
        </div>
      </footer>
    </div>
  );
}
