import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import "./Style/Library.css";

const Library = () => {
  const navigate = useNavigate();
  const { userId: paramUserId } = useParams();

  // Lấy userId mặc định từ sessionStorage nếu không có param
  const storedUser = sessionStorage.getItem("user");
  const parsedUser = storedUser ? JSON.parse(storedUser) : null;
  const currentUserId = parsedUser?.id;
  const userId = paramUserId || currentUserId;

  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);

  // Điều hướng đến thư viện của user
  const handleDiscoverClick = () => {
    if (currentUserId) navigate(`/library/${currentUserId}`);
  };

  useEffect(() => {
    const fetchGames = async () => {
      try {
        if (!userId) throw new Error("User ID is missing");
        const response = await axios.get(`/api/users/${userId}/library`);
        setGames(response.data.items || []);
      } catch (error) {
        console.error("Error fetching user library:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchGames();
  }, [userId]);

  if (loading) {
    return <div className="loading">Loading games for user {userId || "..."}...</div>;
  }

  return (
    <div>
      <h1>Steam Library for User {userId}</h1>
      <h2>All Games</h2>

      <div className="library2-game-list">
        {games.map((game) => (
          <div key={game.id} className="library2-game-card">
            <img src={game.imageUrl} alt={game.title} />
            <h3>{game.title}</h3>
            <button className="play-button">Play</button>
          </div>
        ))}
      </div>

      <footer className="footer">
        <div className="footer-content">
          <div className="footer-left">
            <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
            <p>All trademarks are property of their respective owners in the US and other countries.</p>
            <p>VAT included in all prices where applicable.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Steam Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Steam Subscriber Agreement</li>
              <li>Refunds</li>
              <li>Cookies</li>
            </ul>
          </div>
          <div className="footer-social">
            <div className="social-icon facebook"></div>
            <div className="social-icon close"></div>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Library;
