// src/GameDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate }           from 'react-router-dom';
import axios                   from 'axios';
import './Style/GameDetail.css';

axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
const placeholderBanner = '/images/placeholder-banner.jpg';

export default function GameDetail() {
  const navigate = useNavigate();
  const { key } = useParams();      // slug hoặc ID
  const [game, setGame]      = useState(null);
  const [notFound, set404]   = useState(false);
  const [rating, setRating]  = useState(5);
  const [reviewText, setReviewText] = useState('');
  // tạm giả sử chưa có API lấy review list
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    if (!key) return;
    setGame(null);
    set404(false);

    axios.get(`/api/games/${encodeURIComponent(key)}`)
      .then(res => setGame(res.data))
      .catch(() => set404(true));
  }, [key]);

  const submitReview = e => {
    e.preventDefault();
    const newRev = { rating, text: reviewText, date: new Date().toLocaleDateString() };
    setReviews([ newRev, ...reviews ]);
    setReviewText('');
    setRating(5);
  };

  const handleDiscoverClick = () => navigate('/store');

  if (notFound) {
    return <div className="detail-not-found">Game not found</div>;
  }

  if (!game) {
    return <div className="loading">Loading...</div>;
  }

  const bannerUrl = game.imageUrl || placeholderBanner;

  return (
    <div className="game-detail">
      {/* Header */}
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate("/store")}>Steam</span>
          <div className="user-actions">
            <button className="download-btn" onClick={handleDiscoverClick}>
              Library
            </button>
            <span>Wishlist</span>
            <span className="cart-btn" onClick={() => navigate("/cart")} style={{ cursor: "pointer" }}>
              Cart
            </span>
            <span className="cart-btn" onClick={() => navigate("/profile")} style={{ cursor: "pointer" }}>
              Profile
            </span>
          </div>
        </div>
      </header>

      {/* Banner */}
      <div className="detail-banner">
        <img src={bannerUrl} alt={game.title} />
      </div>

      {/* Info */}
      <div className="detail-info">
        <h2>{game.title}</h2>
        <p>{game.description}</p>

        <div className="detail-meta">
          <span><strong>Developer:</strong> {game.developer}</span>
          <span><strong>Publisher:</strong> {game.publisher}</span>
          <span><strong>Genre:</strong> {game.genre}</span>
        </div>

        <div className="detail-price">
          {Number(game.discount) > 0 && (
            <span className="discount">-{game.discount}%</span>
          )}
          <span className="price">
            {Number(game.price) === 0 ? 'Free' : `$${game.price}`}
          </span>
        </div>

        <button className="buy-btn">
          {Number(game.price) === 0 ? 'Play Now' : 'Buy Now'}
        </button>
      </div>

      {/* Reviews */}
      <div className="detail-extras">
        <h3>Reviews</h3>
        <hr />
        <div className="reviews-list">
          {reviews.length === 0
            ? <p>No reviews yet. Be the first!</p>
            : reviews.map((r, i) => (
              <div key={i} className="review-item">
                <strong>{r.rating} Star{r.rating > 1 ? 's' : ''}</strong> &nbsp;
                <span className="review-date">({r.date})</span>
                <p>{r.text}</p>
                <hr />
              </div>
            ))}
        </div>

        <form className="submit-review" onSubmit={submitReview}>
          <h4>Submit Your Review</h4>
          <div className="form-group">
            <label>Rating</label>
            <select className="rating-select" value={rating} onChange={e => setRating(+e.target.value)}>
              {[5,4,3,2,1].map(n => (
                <option key={n} value={n}>{n}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Review</label>
            <textarea value={reviewText} onChange={e => setReviewText(e.target.value)} />
          </div>

          <button type="submit" className="submit-btn" disabled={!reviewText}>Submit</button>
        </form>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div>
            <img src="/logo.png" alt="Valve Steam Logo" className="footer-logo" />
            <p>© 2023 Valve Corporation. All rights reserved.</p>
          </div>
          <div className="footer-links">
            <ul>
              <li>About Valve</li>
              <li>Jobs</li>
              <li>Steamworks</li>
              <li>Steam Distribution</li>
              <li>Support</li>
            </ul>
            <ul>
              <li>Privacy Policy</li>
              <li>Legal</li>
              <li>Cookies</li>
            </ul>
          </div>
        </div>
      </footer>
    </div>
  );
}
