import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/CartPage.css';

// Configure base URL for API
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function Cart({ user, cartItems, setCartItems }) {
  const navigate = useNavigate();

  // Fetch cart items on mount or when user changes
  useEffect(() => {
    if (!user?.id) return;
    (async () => {
      try {
        const res = await axios.get(`/api/cart/${user.id}`);
        setCartItems(res.data.items || []);
      } catch (err) {
        console.error('Error fetching cart:', err);
      }
    })();
  }, [user, setCartItems]);

  // Remove an item from cart (optimistically update UI)
  const handleRemove = async (itemId) => {
    // Remove locally first
    setCartItems(prev => prev.filter(item => item.cartItemId !== itemId));
    try {
      await axios.delete(`/api/cart/${itemId}`, { data: { userId: user.id } });
    } catch (err) {
      console.error('Error removing cart item:', err);
      // Optionally refetch or revert state
    }
  };

  // Calculate total price
  const totalPrice = cartItems.reduce((sum, item) => {
    const price = Number(item.Price) || 0;
    const discount = Number(item.Discount) || 0;
    return sum + price * (1 - discount / 100);
  }, 0);

  return (
    <div className="cart-page">
      {/* Header */}
      <header className="detail-header">
        <h1>Your Cart</h1>
        <button className="btn-back" onClick={() => navigate(-1)}>
          Back
        </button>
      </header>

      {/* Main Content */}
      <div className="main-container">
        <div className="cart-container">
          {cartItems.length === 0 ? (
            <div className="cart-empty">Your cart is empty</div>
          ) : (
            <>
              <ul className="cart-list">
                {cartItems.map(item => {
                  const price = Number(item.Price) || 0;
                  const discount = Number(item.Discount) || 0;
                  const finalPrice = (price * (1 - discount / 100)).toFixed(2);
                  return (
                    <li key={item.cartItemId} className="cart-item">
                      <span className="cart-item-name">{item.Name}</span>
                      <span className="cart-item-price">${finalPrice}</span>
                      <button
                        className="remove-btn"
                        onClick={() => handleRemove(item.cartItemId)}
                      >
                        Remove
                      </button>
                    </li>
                  );
                })}
              </ul>

              <div className="cart-total">Total: ${totalPrice.toFixed(2)}</div>

              <div className="cart-actions">
                <button className="btn-back" onClick={() => navigate('/store')}>
                  Continue Shopping
                </button>
                <button className="btn-checkout" onClick={() => navigate('/checkout')}>
                  Checkout
                </button>
              </div>
            </>
          )}
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <p>© 2023 Valve Corporation. All rights reserved.</p>
      </footer>
    </div>
  );
}
