import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function GameDetail({ user, cartItems, setCartItems }) {
  const { gameId } = useParams();
  const navigate = useNavigate();
  const [game, setGame] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [newRating, setNewRating] = useState(0);
  const [newComment, setNewComment] = useState('');
  const [inCart, setInCart] = useState(false);

  // Fetch game details
  useEffect(() => {
    async function fetchGame() {
      try {
        const { data } = await axios.get(`/api/games/${gameId}`);
        setGame(data);
      } catch (err) {
        console.error(err);
      }
    }
    fetchGame();
  }, [gameId]);

  // Fetch reviews
  useEffect(() => {
    async function fetchReviews() {
      try {
        const res = await axios.get(`/api/games/${gameId}/reviews`);
        // API returns { page, limit, total, totalPages, reviews: [...] }
        setReviews(res.data.reviews);
      } catch (err) {
        console.error('Error fetching reviews:', err);
      }
    }
    fetchReviews();
  }, [gameId]);

  const handleAddToCart = async () => {
    try {
      const userId = user.id;
      const res = await axios.post('/api/cart/add', { userId, gameId: parseInt(gameId, 10) });
      if (res.status === 201) {
        setInCart(true);
        setCartItems(prev => [...prev, { gameId: parseInt(gameId, 10) }]);
        navigate('/cart');
      } else {
        alert(res.data.message || 'Could not add to cart.');
      }
    } catch (err) {
      console.error('Add to Cart API Error:', err);
      alert(err.response?.data.message || 'Error adding to cart.');
    }
  };

  const handleReviewSubmit = async () => {
    try {
      await axios.post(`/api/games/${gameId}/reviews`, {
        userId: user.id,
        star: newRating,
        comment: newComment,
      });
      // refresh reviews list
      const res = await axios.get(`/api/games/${gameId}/reviews`);
      setReviews(res.data.reviews);
      setNewRating(0);
      setNewComment('');
    } catch (err) {
      console.error('Error submitting review:', err);
      alert('Error submitting review.');
    }
  };

  if (!game) return <div>Loading game details...</div>;

  return (
    <div>
      {/* Game Info */}
      <h1>{game.title}</h1>
      <p>{game.description}</p>
      <button onClick={handleAddToCart} disabled={inCart}>
        {inCart ? 'In Cart' : 'Add to Cart'}
      </button>

      {/* Reviews Section */}
      <h2>Reviews</h2>
      {reviews.length > 0 ? (
        reviews.map(r => (
          <div key={r.id} style={{ border: '1px solid #ccc', padding: '8px', margin: '8px 0' }}>
            <strong>User: {r.user_id}</strong>
            <p>{r.comment}</p>
            <p>Rating: {r.star} ★</p>
          </div>
        ))
      ) : (
        <p>No reviews yet.</p>
      )}

      {/* Write a Review */}
      <h3>Write a Review</h3>
      <div>
        <label>
          Rating:{' '}
          <select value={newRating} onChange={e => setNewRating(parseInt(e.target.value, 10))}>
            <option value={0}>Select</option>
            {[1,2,3,4,5].map(n => (
              <option key={n} value={n}>{n}</option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Comment:{' '}
          <textarea value={newComment} onChange={e => setNewComment(e.target.value)} />
        </label>
      </div>
      <button onClick={handleReviewSubmit} disabled={newRating === 0 || !newComment.trim()}>Submit Review</button>
    </div>
  );
}
