import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Style/Cart.css';

// Cấu hình base URL cho API
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export default function Cart({ user, cartItems, setCartItems }) {
  const navigate = useNavigate();

  // Fetch cart items khi component mount hoặc user thay đổi
  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await axios.get(`/api/cart/${user.id}`);
        setCartItems(res.data.items || []);
      } catch (err) {
        console.error('Error fetching cart:', err);
      }
    };

    if (user && user.id) {
      fetchCart();
    }
  }, [user, setCartItems]);

  // Xóa item khỏi giỏ
  const handleRemove = async (cartItemId) => {
    try {
      await axios.delete(`/api/cart/${user.id}/${cartItemId}`);
      setCartItems((prev) => prev.filter(item => item.cartItemId !== cartItemId));
    } catch (err) {
      console.error('Error removing cart item:', err);
    }
  };

  return (
    <div className="cart-container">
      <header className="store-header">
        <div className="nav">
          <span className="steam-logo" onClick={() => navigate('/store')}>
            <img src="/logo.png" alt="Steam Logo" />
          </span>
          <div className="user-actions">
            <button className="btn-flat" onClick={() => navigate('/library')}>Library</button>
            <button className="btn-flat" onClick={() => navigate('/cart')}>Cart ({cartItems.length})</button>
            <button className="btn-flat" onClick={() => navigate('/profile')}>Profile</button>
          </div>
        </div>
      </header>
      <h1>Giỏ hàng của bạn</h1>
      {(!cartItems || cartItems.length === 0) ? (
        <div className="cart-empty">Giỏ hàng trống</div>
      ) : (
        <div className="cart-items">
          {cartItems.map(item => (
            <div key={item.cartItemId} className="cart-item">
              <h2>{item.Name}</h2>
              <p>Price: {Number(item.Price) * (1 - (item.Discount || 0) / 100)} VND</p>
              <button className="remove-btn" onClick={() => handleRemove(item.cartItemId)}>
                Xóa
              </button>
            </div>
          ))}
        </div>
      )}
      <div className="cart-actions">
        <button className="back-btn" onClick={() => navigate(-1)}>Quay lại</button>
        {cartItems && cartItems.length > 0 && (
          <button className="checkout-btn" onClick={() => navigate('/checkout')}>
            Thanh toán
          </button>
        )}
      </div>

    </div>
  );
}
