import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getStoreGames } from './services/apiService';
import './Style/StorePage.css';

export default function StorePage() {
  const navigate = useNavigate();
  const [games, setGames] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    getStoreGames()
      .then(data => setGames(data))
      .catch(err => console.error('Error fetching games:', err));
  }, []);

  const handleSearch = async (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    if (term) {
      try {
        const results = await getStoreGames(term);
        setSuggestions(results);
      } catch (err) {
        console.error('Error searching games:', err);
      }
    } else {
      setSuggestions([]);
    }
  };

  const selectSuggestion = (game) => {
    setSearchTerm('');
    setSuggestions([]);
    navigate(`/game/${game.id}`);
  };

  // Sections
  const specialOffers = games.filter(g => g.discount > 0);
  const freeGames = games.filter(g => g.price === 0);
  const bestSellers = games.slice(0, 8);

  return (
    <div className="store-page">
      <header className="store-header">
        <div className="nav content-container">
          <span onClick={() => navigate('/')}>Steam</span>
          <nav>
            <a onClick={() => navigate('/')}>Home</a>
            <a onClick={() => navigate('/extras')}>Extras</a>
          </nav>
        </div>
      </header>

      <div className="banner">
        <img src="/.jpg" alt="Banner" />
      </div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Tìm kiếm trò chơi..."
          value={searchTerm}
          onChange={handleSearch}
        />
        {suggestions.length > 0 && (
          <ul className="suggestions-list">
            {suggestions.map(game => (
              <li key={game.id} onClick={() => selectSuggestion(game)}>
                {game.title}
              </li>
            ))}
          </ul>
        )}
      </div>

      <main className="content-container">
        <section className="featured-container">
          <h2>FEATURED</h2>
          <div className="featured">
            {games.slice(0, 1).map(game => (
              <div key={game.id} className="featured-item" onClick={() => navigate(`/game/${game.id}`)}>
                <img src={game.imageUrl} alt={game.title} />
                <div className="game-info">
                  <h3>{game.title}</h3>
                  <p>{game.description}</p>
                </div>
              </div>
            ))}
          </div>
        </section>

        <section className="special-offers">
          <h2>Special Offers</h2>
          <div className="game-list">
            {specialOffers.map(game => (
              <div key={game.id} className="game-card" onClick={() => navigate(`/game/${game.id}`)}>
                <img src={game.imageUrl} alt={game.title} />
                <span className="discount">-{game.discount}%</span>
                <h3>{game.title}</h3>
                <span className="price">
                  ${(game.price * (1 - game.discount / 100)).toFixed(2)}
                </span>
              </div>
            ))}
          </div>
        </section>

        <section className="free-games">
          <h2>Free Games</h2>
          <div className="game-list">
            {freeGames.map(game => (
              <div key={game.id} className="game-card" onClick={() => navigate(`/game/${game.id}`)}>
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <span className="price">Free</span>
              </div>
            ))}
          </div>
        </section>

        <section className="best-seller">
          <h2>Best Sellers</h2>
          <div className="game-list">
            {bestSellers.map(game => (
              <div key={game.id} className="game-card" onClick={() => navigate(`/game/${game.id}`)}>
                <img src={game.imageUrl} alt={game.title} />
                <h3>{game.title}</h3>
                <span className="price">
                  {game.price > 0 ? `$${game.price}` : 'Free'}
                </span>
              </div>
            ))}
          </div>
        </section>
      </main>

      <footer className="footer">
        <div className="content-container footer-content">
          <ul className="footer-links">
            <li onClick={() => navigate('/about')}>About</li>
            <li onClick={() => navigate('/support')}>Support</li>
          </ul>
          <ul className="footer-links">
            <li onClick={() => navigate('/privacy')}>Privacy Policy</li>
            <li onClick={() => navigate('/legal')}>Legal</li>
            <li onClick={() => navigate('/cookies')}>Cookies</li>
          </ul>
        </div>
      </footer>
    </div>
  );
}
